css:
/*@import url('https://fonts.googleapis.com/css?family=Open+Sans:600,700|Pacifico'); */
@-moz-document domain("twitch.tv") {
    

/* custom colors */             /*var(--main-bg)*/
:root {
--main-bg: #1d1e2a !important;               
--dark-bg: #1a1b26 !important;              
--darker-bg: #14151F !important;      
  
--main-text: #aaa !important;                    
--secondary-text: #ccc !important;        
--third-text: #aaa !important;             
  
--accent: #ddd !important;       /* accent color */  
}     
  
body, .cn-content #channel, .activity-button, .form--theme-dark .form__input, .main, .fullwidth_main, #directory-list, .warp__tab::after, .dash-tabs__item--active, .video-stats .table__cell--header, .button--radio .button__label, .button--checkbox .button__label, .vod-message.vod-message--focused .vod-message__timestamp:hover, .vod-message:hover .vod-message__timestamp:hover, .player-placeholder {
    background: var(--main-bg) !important;
  }
  
.activity-card__status, .cn-bar, .warp__item--anchor, .ember-chat .chat-interface .textarea-contain textarea, #site_header, #carousel .carousel-nav, #carousel_and_background, #carousel .dark_wrapper, #carousel .items, #ember1049, .warp__item--toggled > a, .following-col .following-list .load-more span, .balloon--dropmenu, .filter-bar__balloon-footer, .warp__drawer, .conversations-list .conversations-list-header, #settings form, .cl-container .section-header, .cl-container .cl-section .cl-subheader, fieldset, #settings #followers_content .hdr, #large_nav .content .nav_section a:hover:not(.button), .ember-chat .chat-interface, .dash-widget__body, .activity-create__actions, select.form__input, #dash_main .action .content .data, #flyout .content, .app-main, .top-nav-drawer, .top-nav-drawer__status .top-nav-drawer__item, .top-nav-drawer__status, .theme--dark .sc-item:hover, .theme--dark .sc-item:hover .sc-item__hover-buttons, .brick, .button--radio [type="radio"]:checked + .button__label, .button--radio [type="checkbox"]:checked + .button__label, .button--checkbox [type="radio"]:checked + .button__label, .button--checkbox [type="checkbox"]:checked + .button__label, .vod-message.vod-message--focused, .vod-message:hover, .vod-chat__header, .collection-wrapper, .search-panel__title-bar, .search-result-view__titlesep.noHover, .viewall a .text, .viewall a .countries-input {
    background: var(--dark-bg) !important;
  }  
  
.activity-card__comments, .conversations-content .conversations-list-bottom-bar, .button, .cn-tabs__item > a, .warp, .chat-container.dark .chat-interface, .chat-container.dark, .chat-container, .ember-chat-container, .cn-notification-controls .toggle-notification-menu, .form__input[type="text"], #right_col, .balloon .balloon__link:hover, .balloon .balloon__link.balloon__link--active, .warp__item a:hover, .conversations-list .scroll-container, .conversations-list, div.player-menu__menu--intro, .card-carousel__button, .player-content-button, input.text, textarea, .top, .tse-content, .tse-scroll-content, .multi_select li.selector form:hover, .multi_select li.selector > label:hover, .multi_select li.selector .ms-int:hover, .bits-promo__content, .ember-chat .chat-header, .brick--theme-white, .brick--block, .brick--faint, .cn-vod-info__divider, .dash-widget__header, .brick--theme-grey, .brick--block, .dash-tabs__item, #dash_main #setup_link, ul.subtabs li.selected, .pill, .top-nav__nav-items-container, .top-nav__logo, .balloon-wrapper .balloon, .top-nav-drawer__item a:hover, .notification-center__header, .notification-center__footer, .notification--unread, .notification--read, .notification-center__subheader--sticky, .notification-center__subheader, .has-sc .sc-search, .friend-requests__empty, .top-nav-drawer__item--link:hover, .autohost-onboarding, .button, .button--hollow, .button--radio, .button--checkbox, .js-vod-filter > a:nth-child(1), .offer-item__background--gray, .offer-item--title, .vod-chat__input, .vod-message.vod-message, .vod-message, .vod-message__timestamp, .cn-tabs__item, .pinned-cheer__top-bar, .sticky-message, .header-announcement, #carousel .carousel-nav ul li a, .top-nav__user-card, .top-nav__drawer-anchor {
    background: var(--darker-bg) !important;
  }  
  
                         /* txt color */
  
body, h3, h4, h5, p, .button--hollow, .button--radio, .button--checkbox, .chat-container.dark, .button--text, .browse-this h4::after, h2, .following-col .header, .bttv-switch-selection, textarea, .cn-bar__displayname, .dash-brick, .dash-widget, .video-stats .table__cell--header, .notification__content-msg, .notification-center__subheader-text, .offer-item__background--gray, .offer-item--title, .search-result-view__titlesep, .search-result-view__item .card__info span, .viewall a .text span:first-child, .viewall a .countries-input span:first-child, .flex > *, .header-announcement__link {
    color: var(--main-text) !important;
  }  
  
  
.cl-red, strong, .cn-metabar .cn-metabar__title a, .following-col .col-header.inactive .title, .tabs > li, label, .cn-metabar__viewcount, .c-text-live, .top-nav__nav-link.top-nav__nav-link, .card a, .friend-requests__empty, input, textarea, .tabs > .tab > a, .tabs > li > a, .tabs__item > a, #flyout .account_actions a, #flyout .linklist a, .pinned-cheer__top-bar, #carousel a {
    color: var(--secondary-text) !important;
  }  
  
a:hover, .balloon .balloon__link, .top-nav-drawer__item a:hover, .tw-tabs__item > a, .notification-center__header-text, #large_nav .content .nav_section .header {
    color: var(--third-text) !important;
  }   
  
  
.theme--dark .sc-collapsed-header svg, .top-nav__nav-link.active, #large_nav .content .nav_section a:not(.button) .filter_icon svg path, .pinned-cheers .svg-bits-trophy path, .pinned-cheers .svg-bits-hourglass path, .message a {
    fill: var(--accent) !important;
    color: var(--accent) !important;
  }  
  
                              /* font */
  
body, a, .chat-container.dark, .ember-chat .chat-messages .chat-line {
    font-family: "Open Sans" !important;
    font-weight: 600 !important;
    font-size: 13px !important;
  }
  
  h2, .cn-chat-replay-header p, .cn-bar__displayname {
   font-family: "Pacifico" !important; 
    font-size: 25px !important;
  }   
  
                      /* remove borders */
  
  
  
.activity-card, .activity-button, .conversations-content .conversations-list-bottom-bar, .cn-tabs__item--withseparator::after, .cn-tabs__item > a.active::after, .ember-chat .chat-interface .textarea-contain textarea, .items-grid .item .boxart, .conversations-content .conversations-list-search-bar input, .conversations-list .conversations-list-header, .conversations-list, #main_col:not(.expandLeft) .cn-metabar__more, .cn-bar__avatar-wrap, .cl-container .section-header, .cl-container fieldset, input.text, textarea, .multi_select li.selector form, .multi_select li.selector > label, .multi_select li.selector .ms-int, .multi_select li.hdr, .brick--theme-white, .brick--block, .brick--faint, #main_col:not(.expandRight) .cn-metabar__more, .dash-widget, .border-b, .dash-widget__header, .dash-tabs__item, .brick, ul.subtabs, #dash_main .action, .video-stats .table__cell--header, hr, #flyout .point::before, .activity-create, .dash-widget__divider, .balloon-wrapper .balloon, .notification-center__header, .notification-center__subheader, .notification__container, .notification-center__footer, .has-sc .sc-search, .has-sc.has-sc--collapsed #main_col.expandLeft .cn-bar-fixed, .chat-container, .offer-item--title, .prime-crown__new-offer-count, .sticky-message--pinned-cheers, .sticky-message, .search-panel--fly, .search-panel__title-bar, .card__boxpin, #carousel .carousel-nav ul li.active::before {
    border: none !important;
  }  
  
.activity-meta-divider, .activity-card__comments, .button--hollow, .button--radio, .button--checkbox, .form--theme-dark .form__input, .form__input[type="text"], .tabs, .warp__drawer, .bttv-switch-selection, textarea, .cn-chat-replay-header, select, .video-stats__table-title, .tw-tabs, .balloon-wrapper .balloon, .theme--dark .sc-header, .ember-chat .chat-header, .cn-bar, .theme--dark .sc-collapsed-header, .sc-collapsed-header, .notification-center__core-end, .vod-chat__header, .vod-chat__input {
    box-shadow: none !important;    
  }
  
                             /* fixes */

a {
    text-decoration: none !important;
  }  
  
.card__meta {
    background: var(--darker-bg) !important;
    margin: 0 !important;
    opacity: 0.7 !important;
}  
                          
.tw-tabs__item > a.active, .tw-tabs__item > button.active, .tw-tabs__item > a:hover, .tw-tabs__item > button:hover, .tabs__item--active, .tabs__item.active, .tabs__item.selected, .tabs > li.selected, .tabs > li > a.active, .tabs > li > a:hover, .tabs > li:hover {
    box-shadow: 0 -1px 0px var(--third-text) inset !important;
  }  
  
.pinned-cheer__expando-arrow .pinned-cheer__arrow::before {
    border-color: var(--third-text) !important;
}  

.balloon::after, #large_nav .content #footer .links_group {
    display: none !important;
  }  

  
.button--icon-only path, .button--hollow.button--dropmenu::after, .button--radio.button--dropmenu::after, .button--checkbox.button--dropmenu::after {
    filter: grayscale(100%) !important;
  }  
  
  
           /* remove recommended channels */
  
  
.has-sc.has-sc--collapsed .sc-channels__recommended {
    display: none !important;
  }  
 
  
  
                     /* transitions */
  
  
  
.card__img img, .card__img img:hover, a, a:hover, .top-nav-drawer__item--link, .vod-chat__message-list-wrapper {
    transition: ease-in-out .3s !important;
}    
  
.card__img img:hover {
    opacity: 0.7 !important;    
}  
  
  
                   /* hide scrollbar in chat */
  
.vod-chat__message-list-wrapper {
    overflow: hidden !important;
}
  
.vod-chat__message-list-wrapper:hover {
    overflow: auto !important;
}  
  
  
}
