css:
@-moz-document domain("teknik.io") {
body {
    color: #a5a5a5 !important;
    background: #1f222a !important;
}

/* Registration & Login */

.modal-body {
    background: #1f222a;
}

.modal-header-default {
    background: #1f222a;
    border-bottom: 1px solid #286090;
}



/* Logo Top */

.navbar-brand>img {
    content: url("https://p.fuwafuwa.moe/yfazut.png");
}
/* Menu */

.navbar-default {
    background: #272b35 !important;
    border-color: #272b35;
    border-bottom: none;
}
.navbar-default .navbar-nav>.active>a,
.navbar-default .navbar-nav>.active>a:focus,
.navbar-default .navbar-nav>.active>a:hover {
    color: #418bca !important;
    background-color: #1f222a;
}
.navbar-default .navbar-nav>.open>a,
.navbar-default .navbar-nav>.open>a:focus,
.navbar-default .navbar-nav>.open>a:hover {
    color: #418bca;
    background-color: #1f222a;
}
/* Buttons */

.btn-default {
    color: #418bca;
    background-color: #272b35;
    border-color: #418bca;
}
.btn-default:hover {
    color: #418bca;
    background-color: #1f222a;
    border-color: #418bca;
}

.btn-default.focus, .btn-default:focus {
    color: #418bca;
    background-color: #272b35;
    border-color: #418bca;
}

/* Profile */

.nav-tabs>li.active>a,
.nav-tabs>li.active>a:focus,
.nav-tabs>li.active>a:hover {
    color: #a5a5a5;
    cursor: default;
    background-color: #272b35;
    border: none;
}
.nav-tabs>li>a:hover {
    background-color: #272b35;
    border: none;
}
.nav-tabs {
    border-bottom: 1px solid #418bca;
}
/* Panel */

.panel-default {
    background: #1f222a;
    border-color: #41444c;
}
.panel-default>.panel-heading {
    color: #418bca;
    background-color: #1f222a;
    border-color: #272b35;
}
.panel-title {
    font-weight: bold;
}
.list-group-item {
    background-color: #272b35;
    border: 1px solid #41444c;
}
/* Dropdown Menu */

.dropdown-menu>li>a {
    color: #a5a5a5;
    background-color: #1f222a !important;
}
.navbar-nav>li>.dropdown-menu {
    color: #a5a5a5;
    background-color: #1f222a !important;
    border-color: #41444c;
}
.dropdown-menu>li>a:focus,
.dropdown-menu>li>a:hover {
    color: #418bca;
    text-decoration: none;
    background-color: #f5f5f5;
}
.navbar-default .navbar-nav>li>a:hover {
    color: #418bca;
}
.dropdown-menu>.active>a,
.dropdown-menu>.active>a:focus,
.dropdown-menu>.active>a:hover {
    color: #418bca;
    font-weight: bold;
}
/* Upload */

.well {
    background-color: #272b35;
    border: 3px solid #41444c;
}
.bootstrap-switch .bootstrap-switch-handle-on.bootstrap-switch-primary,
.bootstrap-switch .bootstrap-switch-handle-off.bootstrap-switch-primary {
    color: #fff;
    background: #428bca;
}
/* Paste */

.form-control {
    color: #a8aaae;
    background-color: #272b35;
    background-image: none;
    border: 1px solid #41444c;
    border-radius: 4px;
}
/* Blog */

.breadcrumb {
    background-color: #272b35;
    border-radius: 5px;
}
/* Git Gud */

.following.bar.light {
    background: #272b35;
    border-color: #272b35;
    color: white;
}
.ui.secondary.menu .item {
    background: #272b35;
    color: #fefefe;
}
.ui.vertical.menu .item {
    background-color: #272b35 !important;
    color: #a8aaae;
}
ui.list .list>.item .header,
.ui.list>.item .header {
    color: #a8aaae;
}
.ui.list .list>.item .description,
.ui.list>.item .description {
    color: #a8aaae;
}
.ui.action.input:not([class*="left action"])>input {
    background-color: #272b35;
}
.repository .header-wrapper {
    background-color: #1f222a !important;
}
.ui.tabular.menu .item {
    color: #fefefe;
}
}
