css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("srad.jp") {
    body.users2 #firehose > *,
    body.index2 #firehose > *,
    body.firehose #firehose > *
    {
        margin-right: 10px;
    }
    
    ul#commentlisting
    {
        padding-left: 0em;
    }
    
    .col_2 {
        background: none repeat scroll 0 0 white;
        min-width: 840px;
        margin-left: 130px;
        margin-right: 130px;
    }

    #firehose article header h2,
    #commentlisting li.comment.full > .comment-item h4
    {
        background-color: rgb(0, 102, 102);
    }
    
    #firehose article header h2,
    #firehose article header .details
    {
        padding-left: 70px;
    }
    
    #firehose article header h2 a,
    #commentlisting li.comment.full > .comment-item h4 a,
    #commentlisting li.comment.full > .comment-item h4 .score,
    #commentlisting li.comment.full > .comment-item h4 .score a,
    #slashboxes header
    {
        color: white;
    }
    
    .details,
    article .details,
    #commentlisting .details,
    #commentlisting li.comment.full > .comment-item .details
    {
        color: rgb(77, 77, 77);
        background-color: rgb(225, 225, 225);
        padding: 6px;
    }
    
    #comments li.comment.oneline > div[id^="comment_"] a
    {
        color: gray;
        margin-top: 3px;
        padding-left: 5px;
    }
    
    #comments li.comment.oneline > div[id^="comment_"] .score
    {
        color: gray;
    }

    div[id^="comment_"] .score
    {
        pointer-events: none;
    }

/*    
    blockquote {
        border-left: 3px solid #dddddd;
        margin-bottom: 0.75em;
        margin-left: 0.5em;
        margin-top: 0.5em;
        padding-left: 0.75em;
    }
*/

    #d2header {
        height: 30px;
    }
    
    #article-bottom,
    #slashboxes,
    #ads-index-text
    {
       display: none;
    }
    
    #commentlisting li.comment.oneline > .comment-item .commentBody,
    #commentlisting li.comment.oneline > .comment-item .details,
    #commentlisting li.comment.oneline > .comment-item .commentBody a,
    #commentlisting li.comment.oneline > .comment-item .details a
    {
        background-color: rgba(0,0,0,0);
    }
}
