css:
@-moz-document domain("usuncut.com") {
#main-content-wrap {
    background: #333;
}

.content-main {
    color: white;
}

span.post-tags-header, span.post-header, #post-header .post-title, .archive-list-info span, .head-latest-text h2, .feat-main-top-text h2, .feat-main-sub-text h2, .feat-wide-text h2, .feat-main2-text h2, .archive-list-text a {
    color: white;
}

#leader-wrap {
    background: #333;
}

#fly-wrap {
    background: transparent;
}

.ab_thumbnails-rr_abp-mode .syndicatedItem .video-title {
    color: white;
}

#sidebar-wrap .head-latest-out {
    background: #1a1a1a;
    border-radius: 5px;
}

ul.head-latest-list li {
    background: #1a1a1a;
    border-radius: 8px;
}
ul.head-latest-list li {
    border-top: 1px solid grey;
}

#main-nav-wrap {
    
	background: url(http://cdn.xl.thumbs.canstockphoto.com/canstock12334116.jpg);
}

.ab_thumbnails-a_abp-mode .syndicatedItem .video-title {
    color: white !important;
}

.post-tags span.post-tags-header {
    color: grey;
}

ul.archive-list li {
    background: #1a1a1a;
    border-top: 1px solid grey;
    border-radius: 8px;
}

#foot-wrap {
    background: url(http://www.myfreetextures.com/wp-content/uploads/2011/06/another-rough-old-and-worn-parchment-paper.jpg) right;
    max-width: 100%;
}

.foot-widget {
    color: black;
    font-weight: bold !important;
}

#foot-wrap a {
    color: black;
}

ul.archive-list li:first-child {
    border-top: 1px solid grey;
}

#sidebar-wrap .head-latest-out {
    border-top: 1px solid grey;
}

.trc_elastic .trc_rbox {
    width: auto;
}

.ab_thumbnails-rr_abp-mode {
    border-color: grey;
    border-radius: 8px !important;
}

img {
    border-radius: 8px;
}

._2pi8 {
    border-radius: 8px;
}

span.archive-share-but {
    background: #a8a8a8;
    color: #444;
    border-radius: 8px;
}


.head-main-out {
    margin-left: -330px;
    background: #333;
}

.sec-marg-out2, .sec-marg-out4 {
    background: #333;
}

#main-nav-right {
    background: #333;
}

.head-latest-scroll {
    background: #1a1a1a;
    color: #999;
    border-radius: 8px;
}

#head-main-top {
    background: #333;
}

#fly-wrap, #site-fixed, .fly-bottom-soc, .fly-to-top, .fly-soc-head {
    background: transparent;
}

header {
    height: auto;
    background-color: #333;
}

.content-main blockquote p {
    color: lightgrey;
}

nav.main-menu {
    background: #333;
    border-radius: 8px;
}

nav.main-menu ul li a {
    color: white;
}

.fly-but-search, .fly-open {
    background: url(http://cdn.xl.thumbs.canstockphoto.com/canstock12334116.jpg);;
}

.fly-menu-out {
    background: #333;
}

.fly-search-out {
    background: url(http://cdn.xl.thumbs.canstockphoto.com/canstock12334116.jpg);
}
}
