css:
@-moz-document url("https://www.t411.ai/"), url("http://www.t411.ai/") {
html{height:auto!important;line-height:1vh;text-align:center;}
body{display:inline-block !important;}
.wrapper .contentWrapper:not(:nth-of-type(2)){background:red;}
.wrapper .contentWrapper>div:not(.content){background:red;}
.wrapper .contentWrapper>div>div,.accordion.newsWrapper h3,.date, .readmore, .announce hr,br,b,.footer,.spacer{display:none;}
.accordion.newsWrapper{border:none;}
.wrapper .contentWrapper>div>.block:nth-of-type(1){display:block;}
.announce.open{background:none;border:none;}
.announce.open p{font-size:0;line-height:0;}



.wrapper > .contentWrapper:nth-child(2) > .content > .block:nth-child(1) > .accordion.newsWrapper > .announce.open > p > img{text-align:center;max-height:500px !important;}
.wrapper > .contentWrapper:nth-child(2) > .content > .block:nth-child(1) > .accordion.newsWrapper > .announce.open > p > img{margin:0 !important;padding:0 !important;width:auto !important;height:auto !important;}
.wrapper > .contentWrapper:nth-child(2) > .content > .block:nth-child(1) > .accordion.newsWrapper > .announce.open > p{margin:0 !important;padding:0 !important;width:auto !important;height:auto !important;text-align:center;min-width:0;}
.wrapper > .contentWrapper:nth-child(2) > .content > .block:nth-child(1) > .accordion.newsWrapper > .announce.open{margin:0 !important;padding:0 !important;width:auto !important;height:auto !important;min-width:0;}
.wrapper > .contentWrapper:nth-child(2) > .content > .block:nth-child(1) > .accordion.newsWrapper{margin:0 !important;padding:0 !important;width:auto !important;height:auto !important;min-width:0;}
.wrapper > .contentWrapper:nth-child(2) > .content > .block{margin:0 !important;padding:0 !important;width:auto !important;height:auto !important;min-width:0;}
.wrapper > .contentWrapper:nth-child(2) > .content{margin:0 !important;padding:0 !important;width:auto !important;height:auto !important;min-width:0;}
.wrapper > .contentWrapper{margin:0 !important;padding:0 !important;width:auto !important;height:auto !important;min-width:0;}
.wrapper,p,a,hr,span{margin:0 !important;padding:0 !important;width:auto !important;height:auto !important;min-width:0;}

.wrapper{margin-bottom:30px!important;}
.info, .loginBarSide, .loginBar, .searchHistory, .loginWrapper{display:none !important;}

.headerPlace{min-width:0;padding:0;margin:0;margin-top:calc(50vh - 366px);}

#lavamenu{display:none;}
#lavamenu li{display:block;}
#lavamenu li ul{display:block !important;}


.headWrapper{height:50vh !important;height:auto !important;background:none;box-shadow:none;width:100%;text-align:center;padding:0;}

.headWrapper .head{display:inline-block !important;padding:0;text-align:center;height:auto !important;width:auto!important}

.headWrapper .head .logo{position:relative;display:block;margin:20px auto;background-position:50% 50%;}
.headWrapper .head .toolbar{top:0;position:relative;display:block;margin:0 auto;width:500px !important;}

.headWrapper .head .toolbar .searchBar{background:none;margin:0 auto;float:none;width:100%;height:auto !important;padding-bottom:40px;}

.headWrapper .head .toolbar .searchBar #bar-search{position:relative;width:100%;display:block;}
.headWrapper .head .toolbar .searchBar #bar-search input{margin:0 !important;width:calc(100% - 42px) !important;float:none;clear:both;padding:10px 20px !important;border: 1px solid rgba(0,0,0,.2);border-radius:0;margin-bottom:20px;}

.headWrapper .head .toolbar .searchBar #bar-search .searchBtn{margin:0;position:absolute;top:0;right:0;background: url(data:image/svg+xml;utf8;base64,) center center no-repeat;background-size: 50%;height:100%;padding:0 10px;}
}

@-moz-document domain("t411.ai") {
/***************************** Desactiver les pubs **********************************/
.results tbody a[target="_blank"], 
.torrentDetails > .align-center,
.content > .align-center,
.block.description > article > div > center,
.block.description > article > center,
.wrapper .contentWrapper #left,
.wrapper .contentWrapper #right,
.banner728, .content .ads,
.block .bottomads,
.details>center,
.details>table>tbody tr:last-child,
.details>center, .details>table>tbody tr:nth-last-child(2),
.details>table>tbody tr:nth-last-child(7),
.vtt,
.dbnw26543,.drgt14367,.drgt14368,
div[style="width: 468px; text-align: center;"],
article div.align-center:first-child,
article div.align-center:last-child,
article h2.align-center,
.block > div > a[href*="app.t411.ai/app.php"],
.drgt14367 + div,
.drgt14368 + div
{display:none;}


/******************************* a faire disparaitre ********************************/
.notice, #lavamenu, .block h2,span.down,span.up,.share,.navigation,.torrent-status.verify,.footer,.signature,.comment .userinfo span,.smiles{display:none;}

.headWrapper{background:none;box-shadow:none;}


/* liste des torrents */
tr>td:nth-child(2)>a[href="#"]{display:none;}
tr>td>dl{display:none;}

/*footer*/
.footer{background:blue;}
.footer *{display:none;}
}
