    border-top: 3px solid #E0E0E0;
}

.article_nav-bar {
    background: #6b6b6b;
    position: relative;
    width: 100%;
}

.br-texture--sudo-fade-to-white:after, .br-texture--sudo-fade-to-white:before {
    width: 100px;
    height: 100%;
    top: 0;
