css:
@-moz-document domain("osuskills.tk") {
body{
background-color: #000 !important;
background-image: repeating-linear-gradient(-135deg, transparent, transparent 35px, rgba(255, 255, 255, 0.04) 35px, rgba(255, 255, 255,0.04) 70px) !important;
}
*:not(.mainBtn):not(.versusBtn):not(.userRankTitle):not(.subHeader){
color: #AAA;
}
.pink, p *,a:focus,.footerContent a,.recentlyChecked li a:hover *{
color: rgb(68, 170, 221) !important;
}
.headerContent nav li.dropdown ul{
background: url("http://i.imgur.com/3yTpS3i.png") !important;
}
.spoiler, .spoiler-content{
background: transparent !important;
box-shadow: none !important;
}
.spoiler-content-visible img,.spoiler-content img{
display: none !important;
}
.faq .spoiler-content-visible{
background: url("http://puu.sh/rOzLJ.png") !important;
background-repeat: no-repeat !important;
height: 1022px !important;
}
.footerContent{
background: transparent !important;
padding-top: 10px !important;
}
.footerContent div:nth-child(1):before{
margin-top: 0px !important;
content: "Custom CSS made by Dungeonfire ( ´ ・ω・` )" !important;
color: #AAA !important;
display: block !important;
}
option, select{
color: #000 !important;
}
.mapList li:hover, .recentlyChecked li a:hover{
background: rgba(255,255,255,0.3)
}
.dropdown ul{
border: 3px solid #111 !important;
}
.dropdown ul:hover,.dropdown:hover ul{
border: 3px solid #4AD !important;
}
.globe-icon{
-webkit-filter: invert(85%) !important;
filter: invert(85%) !important;
}
.timeline-Tweet-text{
color: #000 !important;
}
.TweetAuthor-name{
color: #000 !important;
}
.TweetAuthor-name:hover{
color: #3B94D9 !important;
}
.TweetAuthor-screenName{
color: #EEE !important;
}
#loader{
display: none !important;
}
.disable{
background: repeating-linear-gradient(-45deg, rgba(108, 108, 108, 0.21), rgba(108, 108, 108, 0.21) 35px, rgba(255, 255, 255, 0.15) 35px, rgba(255, 255, 255, 0.15) 70px) !important;
} 
.wrap, .manualInputModal,.ui-widget-content{
background: url("http://i.imgur.com/3yTpS3i.png") !important;
}
a:hover,.headerContent nav li a:hover, .headerContent nav li a:focus, .headerContent nav li a:active, .headerContent nav li span.active, .headerContent nav li.dropdown:hover, .headerContent nav li.dropdown:focus, .headerContent nav li.dropdown:active{
color: rgb(68, 170, 221) !important;
}
.headerContent nav li a:hover::after, .headerContent nav li a:focus::after, .headerContent nav li a:active::after, .headerContent nav li span.active::after{
  background: rgb(68, 170, 221) !important;
  }
.header:after{
content: "" !important;
border-bottom: 3px solid rgb(68, 170, 221) !important;
width: 1050px !important;
display: block !important;
margin-left: -50px !important;
}
.subHeader *{
color: #FFF !important;
}}
