@-moz-document domain("youtube.com") {
/* ------ Feed ------------ */

.feed-header {border-bottom: 0px !important; background: linear-gradient(#f1f1f1 0%, rgba(166, 215, 249, 0.33)), radial-gradient(farthest-side at 50% 100%, rgba(0,0,0,.1), rgba(0,0,0,0)) 0 100% !important;}

.site-center-aligned .yt-card {background: transparent !important; box-shadow: none !important;}



.site-center-aligned .branded-page-v2-subnav-container {border-bottom: 0px !important;}




/* ------ Footer ---------- */

#footer-container {background: #f1f1f1 !important; border-top: 0px !important;box-shadow: 0px -25px 40px -45px #888888 !important;}








branded-page-v2-primary-col .branded-page-box,.section-list li .item-section .branded-page-box{border-bottom:1px solid transparent}.yt-card{box-shadow:none}.guide-pinned.show-guide .guide-pinning-enabled #page,.guide-pinned.show-guide .guide-pinning-enabled #appbar-content{transition:.3s ease-in-out}.show-guide #player,.show-guide #content{filter:blur(5px);-webkit-filter:blur(5px);-moz-filter:blur(5px);-o-filter:blur(5px);-ms-filter:blur(5px);opacity:.5;pointer-events:none;cursor:default;-webkit-touch-callout:none;-webkit-user-select:none;-khtml-user-select:none;-moz-user-select:none;-ms-user-select:none;user-select:none}.show-guide .player-api{background:tranparent}#c4-header-bg-container{border-color:transparent}.lohp-large-shelf-container{background:#fff}.channel-header-profile-image-container{border:.5em solid #262626;background-color:#262626;top:75%;left:3%;float:left;z-index:9999}#channel-navigation-menu{display:table;margin:0 auto}#channel-subheader ul{float:none}


horizontal.subscribed{background:#e62117;border:1px solid #e62117;color:#fafafa;padding:4px 8px 2px 0;height:22px}.yt-uix-subscription-preferences-button+.yt-subscription-button-subscriber-count-branded-horizontal,.yt-uix-button-subscribed-branded+.yt-uix-subscription-preferences-button+.yt-subscription-button-subscriber-count-branded-horizontal.subscribed{background:#e62117;border:1px solid #e62117;color:#fafafa;padding:4px 8px 2px 0;height:22px}.yt-uix-button-subscribed-branded+.yt-uix-subscription-preferences-button{background:#e62117;border:1px solid #e62117;padding:4px 6px 2px;height:30px}.yt-uix-button.yt-uix-button-subscribe-branded,.yt-uix-button.yt-uix-button-subscribed-branded{background:#e62117;border:1px solid #e62117;;height:30px;color:#fafafa}.primary-header-upper-section-block:first-child{padding-left:12%}



 /*Image Zoom*/
img{-webkit-transition: all 0.3s ease-in-out!important;-moz-transition: all 0.3s ease-in-out!important;-ms-transition: all 0.3s ease-in-out!important;-o-transition: all 0.3s ease-in-out!important;transition: all 0.3s ease-in-out!important;}  
img:hover{transform: scale(1.1)!important; -ms-transform: scale(1.1)!important; -moz-transform: scale(1.1)!important; -webkit-transform: scale(1.1)!important; -o-transform: scale(1.1)!important; }

.upload-active-widget {width: 100% !important;}


#masthead-positioner{
  height: 60px !important;
  overflow: hidden;
}

#masthead-positioner-height-offset{
  height: 50px !important;
  overflow: hidden;
}

#masthead-positioner:hover{
  height: 60px !important;
  overflow: visible;
}

.ytp-chrome-bottom {
    bottom:0;
    transition:bottom .25s cubic-bezier(0.0,0.0,0.2,1) !important;
    
}
.ytp-autohide:not(.ytp-watch-controls) .ytp-chrome-bottom, .ytp-chrome-bottom[aria-hidden=true] {
    opacity:1 !important;
    bottom:-37px;
}

.ytp-big-mode.ytp-autohide:not(.ytp-watch-controls) .ytp-chrome-bottom, .ytp-big-mode .ytp-chrome-bottom[aria-hidden=true] {
    bottom:-54px;
}

.ytp-progress-bar-container {
    width:100%;
    margin-left:0;
    transition: all .25s cubic-bezier(0.0,0.0,0.2,1) !important;
}

.ytp-autohide .ytp-progress-bar-container {
    width: calc( 100% + 24px );
    margin-left:-12px;
}

.ytp-big-mode.ytp-autohide .ytp-progress-bar-container {
    width: calc( 100% + 48px );
    margin-left:-24px;
}

.ytp-autohide .ytp-player-content:not(.html5-endscreen) {
    transition:bottom .25s cubic-bezier(0.0,0.0,0.2,1),top .25s cubic-bezier(0.0,0.0,0.2,1);
}

.ytp-autohide:not(.ytp-ad-overlay-open) .ytp-subtitles-player-content:not(.html5-endscreen):not(.ytp-iv-player-content) {
    bottom: 1em !important;
}




/* LIGHT VERSION */

.caption-line-highlight {
       background-color: #000;
       border: none;
       padding: 5px !important;
       font-weight: 500;
}

.caption-line-highlight:hover {
       background-color: rgba(0, 0, 0, 0.2);
       border: none;
}

.caption-line {
       border: none;
