@-moz-document domain("youtube.com") {
/* CHANGES */
/* Header  */

#masthead-positioner {
       /* Headershadow */
       
       box-shadow: 0 4px 5px 0 rgba(0, 0, 0, 0.14), 0 1px 10px 0 rgba(0, 0, 0, 0.12), 0 2px 4px -1px rgba(0, 0, 0, 0.4);
}

#yt-masthead-container {
       /* Headercontainer & Headercolor */
       
       position: relative;
       padding-top: 7px;
       padding-bottom: 10px;
       border-bottom: 0px;
}

#yt-masthead-signin {
       /* Align sign-in button  */
       
       margin-right: -19px !important;
       margin-top: 6px !important;
}

.signin-container .yt-uix-button-primary {
       /* New sign-in button */
       
       height: 32px;
       width: 32px;
       border-radius: 50%;
       border: none;
       background: no-repeat url(https://i.imgur.com/9wGydcv.png) 4px 4px !important;
       color: transparent;
       -webkit-user-select: none;
}

.signin-container {
       /* New sign-in button */
       
       min-height: 32px;
       min-width: 32px;
       background: rgba(0, 0, 0, 0.15) !important;
       border-radius: 50%;
       border: none;
}

#logo-container {
       /* Align Logo-Container */
       
       margin-left: 15px;
}

.logo {
       /* Old YouTube Logo */
       
       visibility: hidden;
}

.exp-responsive #yt-masthead #logo-container {
       /* New YouTube logo */
       
       background: no-repeat url(http://i.imgur.com/wOoVoVL.png);
       background-size: 73px 30px !important;
       width: 73px;
       height: 30px;
}

#yt-masthead .yt-masthead-logo-container {
       /* Align leftside menu icon */
       
       margin-top: 10px;
       -webkit-user-select: none;
}

.yt-uix-button-icon-appbar-guide {
       /* New leftside menu icon */
       
       background: no-repeat url(http://i.imgur.com/qkAVTiV.png);
       background-size: 18px 12px;
       width: 18px;
       height: 12px;
       margin-left: -7px;
       margin-top: -4px;
}

#masthead-positioner:hover .yt-uix-button-icon-appbar-guide {
       /* New leftside menu icon on hover */
       
       background: no-repeat url(http://i.imgur.com/QcF189G.png);
       background-size: 18px 12px;
       width: 18px;
       height: 12px;
       margin-left: -7px;
       margin-top: -4px;
}


.search-btn-component {
       /* Align & edited searchbutton */
       
       position: absolute;
       z-index: 1;
       border: none;
       box-shadow: none;
       margin-top: 8px;
       height: 0px;
       width: 0px;
}


#yt-masthead-user {
       /* User-Container */
       
       margin-top: 6px;
       align-items: center;
       margin-right: -15px;
       -webkit-user-select: none;
}

#yt-masthead-user .yt-masthead-user-icon {
       /* Resized profile picture */
       
       width: 34px;
       height: 34px;
       border-radius: 50% !important;
}

.yt-thumb-27 {
       /* Resized profile picture */
       
       width: 34px;
       height: 34px;
}

#yt-masthead-user .yt-uix-button img,
.yt-uix-button-has-icon.no-icon-markup .yt-uix-button-icon-wrapper+.yt-uix-button-content {
       /* Resized profile picture */
       
       min-width: 32px;
       max-width: 32px;
       min-height: 32px;
       max-height: 32px;
       margin-left: -1px;
       margin-top: -2px;
}

#upload-btn {
       /* Edited upload-button */
       
       background: none;
       box-shadow: none;
       border: none;
       padding-left: 5px;
       padding-right: 5px;
       margin-right: 5px;
       margin-top: 6px;
       font-size: 14px;
       font-weight: normal;
       text-transform: uppercase;
}

#yt-masthead-user #sb-button-notify {
       /* Notifcation-button */
       
       height: 28px;
       width: 28px !important;
       padding: 0 0 0;
       margin-left: 10px;
       margin-right: 10px;
}

#yt-masthead-user .yt-uix-button-icon-bell {
       /* New notification icon */
       
       background: no-repeat url(http://i.imgur.com/lx8VBhA.png) 0px 0px !important;
       opacity: 1;
       margin-top: 8px;
}

#yt-masthead-user .yt-uix-button-icon-bell:hover {
       /* New notification icon on hover */
       
       background: no-repeat url(http://i.imgur.com/5IUFb5g.png) 0px 0px !important;
       opacity: 1;
       margin-top: 8px;
}

#yt-masthead-user .sb-notif-on .yt-uix-button-content {
       /* New notification button on notification */
       /* ready to try */
       
       background: no-repeat url(http://i.imgur.com/kxeuquD.png) 0px 0px !important;
       height: 20px;
       width: 20px;
       border-bottom: none;
       border-left: none;
       border-radius: 0;
       margin-left: -14px;
       margin-top: 8px;
}
/* Appbar */

#masthead-appbar {
       /* Appbar height & color */
       
       border-bottom: none;
       height: 58px;
}

#masthead-positioner-height-offset {
       height: 112px;
}

.appbar-hidden #masthead-positioner-height-offset {
       height: 64px;
}

#appbar-content {
       /* Alignment */
       
       height: 48px;
       padding-left: 0px !important;
       -webkit-user-select: none;
}

.appbar-hidden #masthead-appbar {
       margin-top: -48px !important;
}

#appbar-nav .yt-uix-button.yt-uix-button-epic-nav-item {
       /* Text-style when not active */
       
       text-transform: uppercase;
       font-size: 14px;
       font-weight: 500;
       padding: 5px 24px 0px;
       height: 48px;
       border-bottom: 2px;
}

.appbar-nav-menu li {
       /* Spacing between buttons */
       
       margin-left: 0 !important;
}

#appbar-nav .epic-nav-item-heading {
       /* Text-style when active */
       
       height: 48px;
       font-size: 14px;
       font-weight: 500;
       text-transform: uppercase;
       padding: 5px 24px 3px !important;
       border-bottom: 2px solid;
       box-shadow: none;
}
/* Button Ripple*/

.appbar-nav-menu li a {
       position: relative;
       overflow: hidden;
}

.appbar-nav-menu li a:after {
       content: '';
       display: block;
       position: absolute;
       left: 50%;
       top: 50%;
       width: 150px;
       height: 150px;
       margin-left: -75px;
       margin-top: -75px;
       background: rgba(0, 0, 0, 0.15);
       border-radius: 100%;
       transform: scale(0);
}

.appbar-nav-menu li a:not(:active)::after {
       animation: ripple 0.55s ease-out;
}

.appbar-nav-menu li a:after {
       visibility: hidden;
}

.appbar-nav-menu li a:focus:after {
       visibility: visible;
}
/*----------------------------------*/
/* Keyframes 1 */

@keyframes ripple {
       0% {
              transform: scale(0);
              opacity: 1;
       }
       100% {
              opacity: 0;
              transform: scale(1.2);
       }
}
/* Navigation */

#appbar-guide-menu {
       /* Size & style */
       
       top: 5px !important;
       min-width: 400px !important;
       transition: ease-in-out 0.5s !important;
       opacity: 1 !important;
       -webkit-user-select: none;
}

.appbar-guide-menu-layout {
       /* Size 2 */
       
       left: -320px;
}

#guide-container {
       /* Content size */
       
       width: 320px !important;
       padding: 8px 0px !important;
}

#guide-container .guide-item.guide-item {
       /* Text-style */
       
       font-size: 15px;
       font-weight: 500;
       text-shadow: none;
}

.exp-scrollable-guide #appbar-guide-menu {
       /* Scrollbar always active */
       
       overflow-y: invisible;
}

.guide-channels-list .guide-item {
       /* Subscribed channel width */
       
       width: 320px;
}

.guide-item .display-name {
       /* Text alignment & size */
       
       margin-left: 32px;
       min-width: 100%;
}

.guide-item .display-name.no-count {
       /* ??? */
       
       width: 100%;
}

.guide-item.guide-item-selected {
       /* Background-color when active */
       
       background: none;
}

.guide-section-separator {
       /* Separatorline */
       
       box-shadow: 0px 1px 2px 0px rgba(0, 0, 0, 0.2);
       opacity: 0.0;
}

.guide-item .guide-count,
.guide-badge-live {
       /* Amount of videos alignment */
       
       right: 35px;
}

.guide-notification-item {
       /* Button-size */
       
       max-height: 48px;
       min-height: 48px;
}

.guide-item,
.guide-view-more {
       /* Selection size */
       
       min-height: 48px;
}

button.guide-view-more {
       /* Alignment & font-style of show-more-button */
       
       font-size: 13px;
       text-align: right;
       padding-right: 40px;
}

li.guide-section h3 a {
       /* Alignment & text-style for headline */
       
       font-size: 14px;
       font-weight: normal;
       text-transform: capitalize;
       text-decoration: none;
       pointer-events: none;
       padding-top: 10px;
       padding-left: 22px;
}

.guide-item .thumb {
       /* Alignment icons and channel pictures */
       
       margin-left: 24px;
}

.guide-user-links .guide-item .thumb {
       /* Icon-color overlay */
       
       -webkit-filter: brightness(10);
}

.yt-thumb-20 {
       /* Shape of channel pictures */
       
       border-radius: 50%;
       background-color: transparent;
}
.guide-pinned .guide-pinning-enabled #appbar-guide-menu {
  /* Box-shadow */
       
       border: none;
       box-shadow: 0px 0px 0px 0px rgba(0, 0, 0, 0.45);
}

/* Content */

body {
       overflow-x: hidden;
}

.page-loaded .yt-base-gutter,
.page-loaded #page,
.page-loaded #appbar-content {
       transition: ease-in-out 0.5s !important;
}

#browse-items-primary .item-section>li>.yt-lockup-tile,
.branded-page-v2-primary-col .branded-page-box,
.section-list li .item-section .branded-page-box {
       /* Seperatorline */
       
       border: none;
}

.browse-list-item-container .branded-page-module-title .video-thumb {
       /* Channelpicture */
       
       border-radius: 50%;
       background: transparent;
}

.yt-thumb {
       /* Imagebackground */
       
       background: transparent;
}

.compact-shelf .yt-uix-button-shelf-slider-pager {
       /* Slidebuttons */
       
       border: none !important;
       height: 36px !important;
       width: 36px !important;
       border-radius: 50%;
       right: -40px !important;
       margin-top: -53px;
       -webkit-transition: all 250ms ease-in-out;
       -ms-transition: all 250ms ease-in-out;
