css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.radianttranslations.com") {
.entry-wrap {
    display: block;
    padding: 60px;
    background-color: #000;
    border-radius: 4px;
  text-align: center;
    box-shadow: 0 0.15em 0.35em 0 rgba(0,0,0,0.135);
}
h1 {
    background-color: #000000;
color: grey;
}
  h2 {
color: grey;
}
  h3 {
color: grey;
}
    h4 {
color: grey;
}
 li {
    background-color: #000000;
color: white;
}
.x-brand {
    background-color: #000000;
color: grey;
}
    body {
    font-size: 13px;
    font-style: normal;
    font-weight: 400;
    color: hsl(0, 0%, 100%);
}
p {
    background-color: rgb(0,0,0);
    color: grey;
    font-weight: bold
}
.x-boxed-layout-active .site {
    margin: 0 auto;
    border: 1px solid #d5d5d5;
    border: 1px solid rgba(0,0,0,0.275);
    border-top: 0;
    border-bottom: 0;
    box-shadow: 0 0 5px rgba(0,0,0,0.125);
    -webkit-box-sizing: content-box;
    -moz-box-sizing: content-box;
    box-sizing: content-box;
}
  .x-boxed-layout-active .site {
    margin: 0 auto;
    border: 0px solid #d5d5d5;
    border: 0px solid rgba(0,0,0,0.275);
    border-top: 0;
    border-bottom: 0;
    background-color: transparent;
    box-shadow: 0 0 0px rgba(0,0,0,0.125);
    -webkit-box-sizing: content-box;
    -moz-box-sizing: content-box;
    box-sizing: content-box;
}
  body {
    font-size: 0px;
 }
  x-sidebar {
    width: 0;
}
.right {
    float: none;
}
  .x-comments-area {
     display: none;
}
  .x-main {
    width: 100%;
   }
  .x-sidebar .widget {
    display: none;
     }
  .x-navbar {
    background-color: #000;
}
.x-logobar {
    background-color: #000;
  }
    .x-colophon{
    background-color: #000;
    }
  .x-navbar .desktop .x-nav > li > a:hover, .x-navbar .desktop .x-nav > .x-active > a, .x-navbar .desktop .x-nav > .current-menu-item > a, .x-navbar .desktop .sub-menu a:hover, .x-navbar .desktop .sub-menu .x-active > a, .x-navbar .desktop .sub-menu .current-menu-item > a, .x-navbar .desktop .x-nav .x-megamenu > .sub-menu > li > a, .x-navbar .mobile .x-nav li > a:hover, .x-navbar .mobile .x-nav .x-active > a, .x-navbar .mobile .x-nav .current-menu-item > a {
    color: grey;
    }
  .x-topbar .p-info, .x-topbar .p-info a, .x-navbar .desktop .x-nav > li > a, .x-navbar .desktop .sub-menu a, .x-navbar .mobile .x-nav li > a, .x-breadcrumb-wrap a, .x-breadcrumbs .delimiter
  {
    color: grey;
    }
  a:link {
    color: grey;
}

/* visited link */
a:visited {
    color: #708090;
}

/* mouse over link */
a:hover {
    color: red;
}

/* selected link */
a:active {
    color: blue;
}
    img {
    visibility: hidden
    }
    body {
    background-image: url("https://newevolutiondesigns.com/images/freebies/black-wallpaper-10.jpg");
    }
    .comment-form-author label, .comment-form-email label, .comment-form-url label, .comment-form-rating label, .comment-form-comment label, .widget_calendar #wp-calendar th, .p-landmark-sub strong, .widget_tag_cloud .tagcloud a:hover, .widget_tag_cloud .tagcloud a:active, .entry-footer a:hover, .entry-footer a:active, .x-breadcrumbs .current, .x-comment-author, .x-comment-author a {
    color: white;
}
       }
