css:
@-moz-document domain("redsarmy.com") {
.container {
    background: #333;
}

body {
    color: #fff;
}

a {
    color: #fff;
}
      
p {
    color: #fff;
}

.tab_content {
    background: #333;
}

.line-title h3, .page-line-title h1, .widget-line-title h3 {
    background: #333;
}

.single-content blockquote p {
    color: rgb(45, 185, 83);
}

.mainNav li > ul li a:hover, .error_msg, readmore, a:hover, .single-content ul.tabs li a:hover, .single-content ul.tabs li a.active, h5.toggle a:hover, .nivo-caption a:hover {
    color: rgb(45, 185, 83);
}

.post-content a, .single-content a {
    color: #ffffff;
}

a {
    text-decoration: none;
    color: white !important;
}

.logo-header {
    position: absolute;
    top: 22px;
    left: -10px;
    z-index: 1000;
    border-radius: 6px;
    background: transparent;
}


html, body, div, span, applet, object, iframe, h1, h2, h3, h4, h5, h6, p, blockquote, pre, a, abbr, acronym, address, big, cite, code, del, dfn, em, img, ins, kbd, q, s, samp, small, strike, strong, sub, sup, tt, var, b, u, i, center, dl, dt, dd, ol, ul, li, fieldset, form, label, legend, table, caption, tbody, tfoot, thead, tr, th, td, article, aside, canvas, details, embed, figure, figcaption, footer, header, hgroup, menu, nav, output, ruby, section, summary, time, mark, audio, video {
    border-radius: 8px;
}

.logo-header a {
    display: block;
    padding-top: 7px;
}
div.social-header {
    background-color: #fff;
}

.social-header ul li {
    padding: 5px 10px 5px 5px !important;
}

.social-header ul {
    background: url(http://redsarmy.com/wp-content/themes/Backstreet/images/patterns/pattern18.jpg);
}

.container_12 .grid_4 {
    width: 270px;
}

#search #s {
    background: none;
    border: none;
    color: #fff;
    width: 240px;
    float: left;
}

#search {
    background: #00601a;
    padding: 5px 10px;
    overflow: hidden;
}

.header {
    position: relative;
    height: 100px;
    background: url(http://redsarmy.com/wp-content/themes/Backstreet/images/patterns/pattern18.jpg) center;
    border-radius: 0px !important;
}

.container_12, .container_16 {
    width: 1200px;
}

.logo-header {
    left: 725px;
}
}
