style_setting_options.value:
/* 房间左侧边栏：隐藏 */
#sidebar-vm,
.room-left-sidebar {
    display: none;
}

/* 房间背景：淡灰 */
body,
.room-bk {
    background-color: /*[[bgcolor]]*/;
}
.room-bg,
.room-bk .bk-img {
	display: none;
}
.bk-changers {
	display: none;
}

/* 房间背景：图案 */
body,
.room-bk {
    background-image: url(/*[[bgkximg]]*/) !important;
    background-size: cover !important;
    background-attachment: fixed;
}
.room-bg,
.room-bk .bk-img {
	display: none;
}
.bk-changers {
	display: none;
}

/* 推荐视频绘画 */
.activity-pushing-out,
.recommend-section {
    display: none !important;
}

/* 顶栏背景 */
.link-navbar-ctnr,
.link-navbar,
.body.top-nav-light-theme, .live-top-nav-ctnr {
 	background: transparent !important;
    box-shadow: none;
}
/* B 站直播 LOGO */
.nav-logo {
    display: none;
}
/* 下载链接 */
.download-link {
    display: none;
}
/* 搜索框 */
#top-nav-search-form {
	border: none;
    border-bottom: 1px solid;
}
/*顶栏文字重叠*/
#link-navbar-vm {
    position: absolute !important;
}

/* 页面底部 */
.link-footer-ctnr,
.live-footer {
    display: none;
}

/* 播主信息 */
.icon-label[data-v-17538976],
.icon-label[data-v-0944d69a],
.anchor-info-row {
    display: none;
}

/* 屏蔽主播头像装饰 */
.anchor-title-image {
    display: none;
}

/* 直播状态开关 */
.live-status-switcher .live-switcher {
    display: none;
}

/* 礼品信息面板 */
.gift-info-panel, .live-hover-panel {
    display: none !important;
}

/* 聊天主面板 */
.aside-area,
.chat-ctnr {
    display: none;
}
/* 播放器本体：配合隐藏聊天面板使用 */
.player-area {
    margin: 0 auto;
    float: none;
    width: 100%;
}
.player-ctnr {
    padding-right: 0 !important;
}
.live-room-app .app-content .app-body
{
    max-width: 1400px;
}

/* 播放器容器：配合隐藏聊天面板使用 */
.main-section.player-ctnr {
    width: 100%;
    height: 672px;
}

/* 播放器全屏容器：配合隐藏聊天面板使用 */
body.player-full-win .player-ctnr {
    width: 100% !important;
    height: 100% !important;
}
body.player-full-win .video-section .player-area {
    width: 100% !important;
}
.player-full-win .player-section {
    width: 100% !important;
}

/* 顶部房间信息栏：配合隐藏聊天面板使用 */
.info-ctnr {
    width: 1065px !important;
}

/* 送礼榜单 */
.rank-list-ctnr {
    visibility: hidden;
}

/* 礼物容器 */
.super-gift-ctnr, .gift-msg-1000 {
    display: none;
}

/* 会员勋章 */
.medal-icon {
    display: none;
}

/* 其他勋章标志 */
.chat-msg > a {
    display: none;
}

/* 房间内通知 */
.system-msg {
    display: none;
}

/* 系统广播 */
.announcement-container {
    display: none;
}

/* 帐号操作区（发送框下方）*/
.bottom-actions .left-action[data-v-47749bcc],
.profile-ctrl {
    visibility: hidden;
}

.right-container {
    display: none;
}
.room-feed {
    display: none;
}

/* 屏蔽看板娘 */
.live-haruna-ctnr {
    display: none;
}
