/* ----------> StartPage - PureDark   /  v3.0 - 03-10-2019 <------------------ */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("startpage.com"), domain("ixquick.com")
{


/* ============= BACKGROUND ============== */
a
{
    text-decoration: none !important
}

.hero-search, .header__content, .header__navigation,
#query, .search-suggestions__list,
.hero-search__learn-more, .search-form__button,
.header__container, .layout, .w-gl,
.search-form__form,
.layout-images__mainline, .layout-images__body, .layout-video,
.card__header,
.search-filter-time__options, .search-filter-place__options,
.images-filter-size__options__container, .images-filter-color__options__container, .filter-button, .images-filter-type__options, .images-filter-size__options__rightrail__button,
.video-filter-type__options, .video-filter-duration__options,
.footer__container, .footer,
.columns, .text-input__element, .main,
.settings-page__form-action,
.suggested-search__dropdown
{
  background:url('data:image/png;base64,')!important;
}

/* Phrase "Le moteur... au monde" */
.hero-search__tagline 
{
    display: none !important;
}
/* Bas de page "En savoir plus" */
.hero-search__learn-more-trigger
{
    display: none !important;
}

/* LOGO */
.hero-search__logo
{
    opacity: 0.6;
    -webkit-filter: saturate(100%);
}
.header__logo
{
    opacity: 0.25;
}
.addon__popup
{
    display: none !important;
}

/* ======================== ACCUEIL ======================== */

/* Position bloc */

.hero-search__form
{
    position: relative;
    bottom:40%;
}

.hero-search__learn-more-separator svg
{
    opacity: 0.05 !important;
}


/* Texte */
#query 
{
    color: #5C6F84 !important;
}

/* Texte Suggestions */
.search-suggestions__item
{
  color:#72767A!important;
}
.search-suggestions__item:hover
{
  background:#313346 !important;
  border-bottom: 0px !important;
}

.header__container, .instant-wiki
{
    border:1px solid #313346 !important;
}

.search-suggestions__list li:hover
{
    background: #313346 !important;
}

.search-form, .search-form__form, .search-form__q, .instant-wiki__content, .instant-wiki,
.suggested-search__dropdown, .search-suggestions__list
{
    box-shadow: none !important;
}
.search-form__input, .search-form__form,
.suggested-search__dropdown, .search-suggestions__list
{
    border: 1px solid #313346 !important;
}
.inline-nav-menu__container {
	border-bottom: 1px solid #586A7E !important;;
}

/* Loupe */
.search-form__button
{
    color: #586A7E !important;
    /*transition: all .5s!important;*/
}
.search-form__button:hover
{
    color: #55AAAA !important;
    /*transform: rotate(1turn)!important;*/
}
/* effacer */
.search-form__reset:hover
{
    color: #FF8040 !important;
    /*transform: rotate(1turn)!important;*/
}


/* Liens menu top droit */

.header-search__navigation-left a,
.burger-menu__links a,
.menu-container a
{
    color: #42558C !important;
}
.header-search__navigation-left a:hover,
.burger-menu__links a:hover,
.menu-container a:hover
{
    color: #899BC9 !important;
}
.header-search__navigation-left a:active,
.burger-menu__links a:active
{
    color: #FF8040 !important;
}


/* ======================== SEARCHES ======================== */

/* fond suggestions */
.suggestion
{
    color: #666666 !important;
}
.suggestion:hover
{
    background: #32394E !important;
    color: #888888 !important;
}

.sp-gl__container
{
    background: #202533  !important;
    border: 1px solid #3B435C !important;
}
.sp-gl__label
{
    color: #888888 !important;
}
.sp-gl__result
{
    color: #888888 !important;
}
.sp-gl__result:hover
{
    color: #51A4F7 !important;
}


/* menu catégories */
.inline-nav-menu__link
{
    color:#3B435C !important;
}
.inline-nav-menu__link:hover
{
    color:#5C6F84 !important;
}


.search-form .highlight
{
    border: 1px solid #576488 !important;
}

/* link */

.w-gl__result-title
{
    color: #3E72BF !important
}
.w-gl__result-title:hover
{
    color: #51A4F7 !important
}

/* adress */
.w-gl__result-url
{
    color: #3d764b !important
}
/* description */
.w-gl__description
{
    color: #7c7c7c !important
}
/* mode anonyme */
.w-gl__anonymous-view-url
{
    color: #363636 !important
}
.w-gl__anonymous-view-url:hover
{
    color: #51A4F7 !important
}

/* couleur saisie */
.search-form__q
{
   color: #71859B !important; 
}

/* liens visités */
.w-gl__result-title:visited
{
    color: #805555!important;
}
.w-gl__result-title:visited:hover
{
    color: #A37474!important;
}

/* loupe */
.search-form__button, .ico-wrapper
{
    background: transparent !important;
    opacity: 0.4;
}
.ico-wrapper:hover
{
    opacity: 0.9;
}

/* delete search cross */
.search-form__clear-button
{
    opacity: 0.3;
}
.search-form__clear-button:hover
{
    opacity: 0.8;
}

/* Cadre Résumé wiki */


/* title */
.search-item__title a
{
    color: #3E72BF !important;
}
.search-item__title a:hover
{
    color: #51A4F7 !important;
}

/*description*/
.reveal-content__short, .reveal-content
{
    color: #7c7c7c !important;
}

/* bouton plus */
.instant-wiki__link:hover
{
    color: orange !important;
    text-decoration: none !important;
}



/* recherche avancée */
.fieldset
{
    color: #3d764b !important;
}
.text-input__label, .select__label
{
    color: #7c7c7c !important;
}
.text-input__element
{
    color: #5C6F84 !important;
}


/* IMAGES */

.image-container
{
    opacity: 0.7;
}
.image-container:hover
{
    opacity: 1;
}

/* VIDEOS */

.vo-sp__title
{
   color: #3E72BF !important; 
}
.vo-sp__title:hover
{
   color: #51A4F7 !important; 
}
.vo-sp__description
{
    color: #7c7c7c !important;
}


.pagination__num
{
   color: #3E72BF !important; 
}
.pagination__num:hover
{
   color: #51A4F7 !important;
    background: transparent !important;
}

/* numérotation pages */
.num--active
{
   color: orange !important;
    background: transparent !important;
}

.pagination__next-prev-button, .desktop-arrow
{
    color: #3E72BF !important;
}
.pagination__next-prev-button:hover, .desktop-arrow
{
    color: #51A4F7 !important;
}



.images-filter-size__options__rightrail__button
{
    border: 1px solid #3B435C;
    color:#5C6F84 !important;
}
.images-filter-size__options__rightrail__button:hover
{
    border: 1px solid #5C6F84;
    color: #3E72BF !important;
}



/* footer */

.footer__links a, .footer__ul
{
    color:#5C6F84 !important;
}

/* ======================================== */
/* -------------------------- FIN  -------------------------------- */
}
