css:
@-moz-document domain("www.pixiv.net") {
/* LIGHTER */

._work-items,
.find,
._tab-navigation,
.item,
.cover-footer,
._portfolio-container .header,
.modules .details,
.following + .arrow ._icon-menu,
.cover-footer .arrow ._icon-menu,
._balloon-menu-popup .follow-and-edit,
._balloon-menu-popup .edit,
._balloon-menu-popup .unfollow,
._balloon-menu-popup .follow,
.contents .status-container, 
._profile-view-modal .follow_button > *,
.items .image{
    background-color: #2a2a2a !important;
}


._balloon-menu-popup .group-header {
    background-color: #3f3f3f !important;
}

._tab-navigation {
    border-color: #2a2a2a!important;
}
._portfolio-container .short-text,
.description-text,
.ui-expander-target,
.modules .details .user-name,
._icon-dot {
    color: #CCCCCC!important;
}
.user-icon-container > ._title,
.modules .details .title,
.following + .arrow ._icon-menu,
.cover-footer .arrow ._icon-menu{
    color: #FFFFFF!important;
}
/* ----------- */

.find,
input {
    color: #E4E4E4!important;
    border-color: #333333!important;
}
/* DARKER */

.media,
.auto {
    background-color: #212121!important;
}
._sort-menu .count {
    color: #CCCCCC!important;
}
._header-container,
._header-container > * {
    background-color: #1a1a1a !important;
}
/*------------*/
/* MODAL VIEW */

.modal-background,
.modal-header,
.header-container,
.dot,
._unit,
._profile-view-modal .container {
    background-color: #121212!important;
    border-color: #202020!important;
    border-radius: 20px 20px 0px 0px;
}
/* COMMENTS */

.comment-type,
.comment-text-form,
._profile-view-modal .integration-list {
    background-color: #212121!important;
    border-color: #202020!important;
}
.scroll-wrapper,
._work-shortcut-container,
.sub-container,
.main-info-container ._icon-pencil,
._follow-button > *,
.comment-input-container + *,
.related-works-container .big{
    background-color: #121212!important;
}
.author-info-container,
._work-shortcut-container,
.related-works-container {
    background-color: #121212!important;
    border-color: #202020!important;
}
.ad-info-container {
    background-color: #0e0e0e!important;
    border-color: #202020!important;
}
._events-item-container,
.content-container,
._profile-view-modal .user-info-table {
    border-color: #202020!important;
}
.main-info-container .tag {
    color: #CCCCCC!important;
    background-color: #292929!important;
}
._section-title,
._profile-view-modal .text,
._profile-view-modal th,
._profile-view-modal td {
    color: #CCCCCC!important;
}
.main-info-container ._title {
    color: #FFFFFF!important;
}
/*---------------*/

body,
.title,
.contents > .title,
.user-name,
.item,
.user-activity {
    color: #E4E4E4!important;
}
.title-and-name > .user-link,
.title-and-name >.title {
    color: #212121!important;
}
/* , .popular-tags E4E4E4 */

._balloon-menu-popup,
.user-bg,
._profile-popup .contents {
    background-color: #111111!important;
    border-color: #545454!important;
}
.user-name > a {
    color: #FFFFFF!important;
}
._balloon-menu-right-align .item {
    background-color: #111111!important;
}
._balloon-menu-popup>h2,
.submenu-group,
.booth_item,
.booth_item>a,
.sensei_new_course,
.sensei_new_course>a,
.pair,
.pair>a,
._profile-popup .text {
    border-color: #333333!important;
    color: #CCCCCC!important;
}
.account a {
    color: #FFFFFF!important;
}
/* :hover */

.message-text,
.submenu-group>a {
    color: #CCCCCC!important;
}
.ui-modal-close {
    background-color: rgba(0, 0, 0, 0.4)!important;
}
}
