css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("noted.fm") {

  body {
    color: #999;
    background-color: #444;
  }
  
  header {
    background-color: black !important;
  }
  
  .input-group .form-control:first-child {
    border-radius: 5px;
    border-color: #666 !important;
    background-color: black !important;
    color: greenyellow !important;
  }
  
  header .content-search .input-group .input-group-addon {
    background-color: #333 !important;
    border-radius: 5px;
    border-color: #666 !important;
  }
  
  header .home-btn, a.trigger,
  feed post .post-box .post-action-btns {
    filter: invert(100%);
  }

  a.user-pic, a.notification-image {
    filter: invert(100%) !important;
  }

  span.count {
    filter: invert(100%);
    background-color: #900 !important;
  }
  
  a, a:visited, a:focus {
    color: #077;
  }
  
  #posted-by .username {
    color: #077 !important;
  }
  
  a:hover {
    color: #099 !important;
  }
  
  feed post .inner-post .caption a {
    color: #888 !important;
  }
  
  .site-content{
    background-color: #222 !important;
  }
  
  feed post .inner-post .song-title{
    color: #ccc !important;
  }
  
  
  .user-body,
  .inner-post {
    background-color: #333 !important;
  }

  div.site-content {
    background-color: #222 !important;
    background: #222; /* Old browsers */
    background: -moz-linear-gradient(left, #000 1%, #222 30%, #222 88%, #000 90%); /* FF3.6-15 */
    background: -webkit-linear-gradient(left, #000 1%, #222 30%, #222 88%, #000 90%); /* Chrome10-25,Safari5.1-6 */
    /*background: linear-gradient(to right, #000 1%,#222 30%, #222 88%, #000 90%); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
    
  }
  
  .user-body .title {
    color: #fff !important;
  }
  
  #blogs-page .users .user-container .user-box .playlist-creator, 
  #blogs-page .users .user-container .user-box .playlist-name, 
  #blogs-page .users .users-container .user-box .playlist-creator, 
  #blogs-page .users .users-container .user-box .playlist-name, 
  #explore-page .users .user-container .user-box .playlist-creator, 
  #explore-page .users .user-container .user-box .playlist-name, 
  #explore-page .users .users-container .user-box .playlist-creator, 
  #explore-page .users .users-container .user-box .playlist-name, 
  #followers-page .users .user-container .user-box .playlist-creator,
  #followers-page .users .user-container .user-box .playlist-name,
  #followers-page .users .users-container .user-box .playlist-creator,
  #followers-page .users .users-container .user-box .playlist-name,
  #following-page .users .user-container .user-box .playlist-creator,
  #following-page .users .user-container .user-box .playlist-name,
  #following-page .users .users-container .user-box .playlist-creator,
  #following-page .users .users-container .user-box .playlist-name,
  #more-playlists .users .user-container .user-box .playlist-creator, 
  #more-playlists .users .user-container .user-box .playlist-name, 
  #more-playlists .users .users-container .user-box .playlist-creator, 
  #more-playlists .users .users-container .user-box .playlist-name, 
  #music-feed .users .user-container .user-box .playlist-creator, 
  #music-feed .users .user-container .user-box .playlist-name, 
  #music-feed .users .users-container .user-box .playlist-creator, 
  #music-feed .users .users-container .user-box .playlist-name, 
  #search-page .users .user-container .user-box .playlist-creator, 
  #search-page .users .user-container .user-box .playlist-name, 
  #search-page .users .users-container .user-box .playlist-creator, 
  #search-page .users .users-container .user-box .playlist-name, 
  #suggested-posts-page .users .user-container .user-box .playlist-creator,
  #suggested-posts-page .users .user-container .user-box .playlist-name,
  #suggested-posts-page .users .users-container .user-box .playlist-creator,
  #suggested-posts-page .users .users-container .user-box .playlist-name,
  #suggested-users-page .users .user-container .user-box .playlist-creator,
  #suggested-users-page .users .user-container .user-box .playlist-name,
  #suggested-users-page .users .users-container .user-box .playlist-creator,
  #suggested-users-page .users .users-container .user-box .playlist-name {
    color: #999 !important;
  }
 
  .user-body .title .see-all {
    color: #0c9 !important;
  }
  
  #posted-by .username {
    color: #0c9 !important;
  }
  
  .top-playlists .playlist-image .badge {
    color: #000 !important;
    background-color: #9e0 !important;
  }

  top-playlist-item .trending-background.unseen-posts,
  .top-playlists .playlist-image img.unseen-posts {
    border-color: #9e0 !important;
  }
 
  feed post .post-box {
    border-color: #555 !important;
    box-shadow: 0 1px 3px black !important;
    background-image: none !important;
    border-radius: 15px;
  }

   feed post.narrow-post,
   feed post .inner-post{
     border-radius: 16px !important;
     border-color: #222;
  }
  
  feed post {
	  border-top: 5px solid #222;
	  border-bottom: 10px solid #171717;
    border-radius: 15px;
  }

  feed post .inner-post .video, feed post .inner-post iframe {
    border-radius: 20px 0px 20px 0px;
  }
  
  .site-content-right {
    background-color: #222 !important;
    background: #222; /* Old browsers */
    background: -moz-linear-gradient(left, #222 1%, #000 100%); /* FF3.6-15 */
    background: -webkit-linear-gradient(left, #222 1%,#000 100%); /* Chrome10-25,Safari5.1-6 */
    /*background: linear-gradient(to right, #222 1%,#0000100%); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
    /*filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#222', endColorstr='#000',GradientType=1 ); /* IE6-9 */
    
    color: #444 !important;
  }
  
  .site-content-right a {
    color: #666 !important;
  }
  
  div#user-right.upper-right-content
  {
    background-color: #444 !important;
  }
  
  .bg-white {
    background-color: #222 !important;
  }

  .upper-right-container,
  #activity-list,
  #conversations-page, .modal-body.add-to-conversation {
    background-color: black !important;
    border-radius: 10px;
  }
  
  #conversations-container {
    background-color: black !important;
    border-radius: 10px;
  }
  
  .section-title,
  #activity-list .section-title,
  #conversations-page .section-title .title-text, .modal-body.add-to-conversation .section-title .title-text {
    background-color: #111 !important;
    color: #666 !important;
  }
  
  .content-box .title,
  #conversation-divider {
    background-color: #333 !important;
    color: #666 !important;
  }
  
  #progress-container #progress-color {
    background-color: #7ff !important;
  }
  
  
  #music-feed .top-playlists {
    background-color: #111 !important;
    border-color: #999 !important;
    border-radius: 10px;
  }
  
  .content-box {
    background-color: #111 !important;
    border-color: #999 !important;
  }
  
  .video-wrapper .noted-controls {
    background-color: #fff !important;
    filter: invert(100%);
    border-color: #999 !important;
    border-radius: 10px;
 }
 
  footer {
    color: #999 !important;
  }
  footer .song-name a {
    color: #8aa !important;
  }
  footer.show-footer {
    background-color: black !important;
    border-color: #777 !important;
  }

  footer.hide-footer {
    background-color: #111 !important;
    border-color: #333 !important;
  }

  footer .player-btns,
  footer div.action-btns{
    filter: invert(100%);
  }
  footer #progress-container #progress-color {
    background-color: #c00 !important;
  }
 
}
