css:
@-moz-document domain("habitica.com") {
span.hair_flower_0 + span.ng-scope,span.hair_flower_1 + span.ng-scope,span.hair_flower_2 + span.ng-scope,span.hair_flower_3 + span.ng-scope,span.hair_flower_4 + span.ng-scope,span.hair_flower_5 + span.ng-scope,span.hair_flower_6 + span.ng-scope{opacity:1;left:0px;top:16px;zoom:0.7}
.addthis_counter .addthis_button_expanded,.addthis_counter .atc_s{box-shadow:0 0 0 0 rgba(0,0,0,1);background-color:transparent!important;margin-top:4px}
/*[[hide]]*/
.header-wrap.ng-scope{background-image:url(/*[[bg]]*/);background-position:0px 190px}
.npc_matt{background-image:url(http://imgur.com/8ZySbtb.png);background-position:center;width:422px;height:190px;margin-bottom:0}
@media only screen and (max-width: 1046px){.npc_matt+.static-popover{max-width:360px}}
[class*=Pet-Wolf],[class*=Pet-TigerCub],[class*=Pet-PandaCub],[class*=Pet-LionCub],[class*=Pet-Fox],[class*=Pet-FlyingPig],[class*=Pet-Dragon],[class*=Pet-Cactus],[class*=Pet-BearCub]
{background-position:center;left:66px;bottom:0}
button.pet-button{left:0px}
[class*=Icon_Wolf],[class*=Icon_TigerCub],[class*=Icon_PandaCub],[class*=Icon_LionCub],[class*=Icon_Fox],[class*=Icon_FlyingPig],[class*=Icon_Dragon],[class*=Icon_Cactus],[class*=Icon_BearCub]
{background-position:0;}
li.toolbar-button{background:url(http://i.imgur.com/)!important}
[class*=Mount_Body_],[class*=Mount_Head_],.current-pet,.pet-button{background-repeat:no-repeat}
[class*=Body_Wolf],[class*=Body_TigerCub],[class*=Body_PandaCub],[class*=Body_LionCub],[class*=Body_Fox],[class*=Body_FlyingPig],[class*=Body_Dragon],[class*=Body_Cactus],[class*=Body_BearCub]
{display:none}
/*[[gen]]*/
}

style_setting_options.value:
[class*=Head_Wolf],[class*=Head_TigerCub],[class*=Head_PandaCub],[class*=Head_LionCub],[class*=Head_Fox],[class*=Head_FlyingPig],[class*=Head_Dragon],[class*=Head_Cactus],[class*=Head_BearCub]
{margin-top:77px;left:0;z-index:1;background-position:0 0;background-repeat:no-repeat;display:block}
[class*=Pet-Wolf], [class*=Pet-TigerCub], [class*=Pet-PandaCub], [class*=Pet-LionCub], [class*=Pet-Fox], [class*=Pet-FlyingPig], [class*=Pet-Dragon], [class*=Pet-Cactus], [class*=Pet-BearCub] {
background-position:center;left:66px;bottom:10px;
}
