@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("fstoppers.com") {

  div, .author-block .socicons .icon-wrapper {
    background-color: /*[[color2]]*/ !important; color: /*[[text]]*/ !important;   
  }
  #navigation, header, header div.inner, #header-region, .login-links, .header-links, .header-links ul, .socicons, .icon-wrapper, footer, #footer div.inner, #block-fs_helpers-footer, .footer-col, .copyright, .block-title, blockquote {
    background-color: /*[[color1]]*/ !important; color: /*[[text]]*/ !important;   
  }
  #pre-footer, #pre-footer div.inner {
    background-color: /*[[color3]]*/ !important; color: /*[[text]]*/ !important;
  }
  h1, h2, h3, h4, h5, h6, strong {
    color: /*[[text]]*/ !important;
  }
  a:hover, .field-name-field-categories a {
    color: /*[[text]]*/ !important;
  }
  a, .field-name-field-categories a:hover {
    color: /*[[text]]*/ !important;
  }
  .membership-button a {
    color: black!important;
  }
  .view-popular-articles .view-content li, .view-popular-articles .view-content .lead img, .node-product.teaser .catalog-image img {
    border: none;
  }
  .title a {
    text-decoration: none;
  }
  #scrollgroup-2 > div:nth-child(1) {
    display:none;
  }
  .breadcrumb a {
    color: /*[[text]]*/ !important;
    filter: invert(1);
  }
  .breadcrumb a:hover {
    color: /*[[link]]*/ !important;
    filter: invert(1);
  }
  .breadcrumb .crumb, #logo {
    filter: invert(1);
  }
  .twentytwenty-overlay {
    background-color: transparent !important;
  }
}
