css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ubuntu.com") {
   
    body, html {
        background: none !important;
        background-color: #000009 !important;
        border-radius: 0.5em !important;
        border: none !important;
        color: #999 !important;
    }
    
    div, /*p, h1, h2, 
    h3,*/ table, tr, td, th,
    ul, li, input, select, 
    footer, header, nav {
        background-color: #081016 !important;
        border: none !important;
        box-shadow: none !important;
    }
    
    div, p, h1, h2, 
    h3, table, tr, td, th,
    ul, li, input, select, 
    footer, header, nav {
        color: #888 !important;
    }

    hr {   
        background-color: #000009 !important;
    }
    
    .arrow-up {
        background: transparent !important;
    }

    a {
        border: none !important;
    }
    
    img {
        opacity: .7 !important;
        transition: opacity .2s;
        border-radius: 0.5em !important;
    }
    
    img:hover, a:hover img {
        opacity: 1 !important;
        border-radius: 0.5em !important;
    }

    ul.portletactions li a, ul.portletactions li span {
        color: #e95420 !important;
    }
    
    ul.portletactions li.active span, ul.portletactions a:hover {
        background: none !important;
    }

    #title a {
        color: #888 !important;
    }
    
    #breadcrumbs li {
        filter: invert(100%);
    }
    
    #breadcrumbs li a {
        color: #15abdf !important;
    }
    
    .banner .nav-primary ul li ul li a, .banner .nav-primary ul li ul li a:link, .banner .nav-primary ul li ul li a:visited {
        color: #888 !important;
    }
    
    .u-nav-global a:link, .u-nav-global a:visited {
        color: #888 !important;
    }
    
    .u-nav-global a:hover {
        color: #e95420 !important;
    }
    
    #nav-global a:link, #nav-global a:visited {
        color: #888 !important;
    }
    
    #nav-global a:hover {
        color: #e95420 !important;
    }
    
    .nav-secondary .second-level-nav li a, .nav-secondary .third-level-nav li a {
        color: #888 !important;
    }
    
    #second-nav li:first-child {
        background: none !important;
    }
    
    .box-padded {
        background-image: none !important;
        background-color: #081016 !important;
    }
    
    form.header-search {
        filter: invert(100%);
    }
    
    li.mktoFormCol {
        filter: invert(100%);
    }
    
    #search-container {
        -moz-border-radius: 0px 0px 4px 4px !important;
        -webkit-border-bottom-left-radius: 4px !important;
        -webkit-border-bottom-right-radius: 4px !important;
        -moz-box-shadow: #bbb 0px 0px 2px !important;
        -webkit-box-shadow: #bbb 0px 0px 2px !important;
        background-color: #081016 !important;
        background-image: none !important;
    }
    
    #search-box {
        -moz-border-radius: 0px 0px 4px 4px !important;
        -webkit-border-bottom-left-radius: 4px !important;
        -webkit-border-bottom-right-radius: 4px !important;
        -moz-box-shadow: #bbb 0px 0px 2px !important;
        -webkit-box-shadow: #bbb 0px 0px 2px !important;
        background-color: #f2f2f2 !important;
        filter: invert(100%);
    }
    
    input#id_email {
        filter: invert(100%);
    }

    pre {
        color: #f3f3f3 !important;
        background-color: #2e3436 !important;
        border: 1px dashed #778 !important;
    }
    
    div.yui3-widget-bd {
        background: #edf5ff !important;
    }
    
    div.yui3-widget-ft {
        background: #edf5ff !important;
    }
    
    a.yui3-button {
        filter: invert(100%);
    }
    
    .row.row-white, .row.strip-light, .row.row-grey, .row.row--grey {
        background-color: #081016 !important;
    }
    
    .row-quote blockquote p, .row-quote blockquote cite {
        color: #888 !important;
    }
    
    fieldset {
        background-color: #081016 !important;
    }
    
    body.homepage .row-hero, body.homepage .row--ubuntu-news {
        background-image: none !important;
        background-color: #081016 !important;
    }
    
    body.homepage .row--ubuntu-news .row--ubuntu-news__title {
        background-image: url("https://assets.ubuntu.com/v1/f24a8aa0-external-link-orange.svg") !important;
    }
    
    body.homepage .row--ubuntu-news .row--ubuntu-news__title a {
        color: #e95420 !important;
    }
    
    body.homepage .row--cloud-products {
        background-color: #081016 !important;
    }
    
    body.homepage .featured--left::before {
        background-color: #081016 !important;
    }
    
    body.homepage .row--intro {
        background: none !important;
    }
    
    body.homepage .row--iot {
        background: none !important;
    }
    
    body.homepage .row--ubuntu-news a {
        color: #888 !important;
    }
    
    body.homepage .row--ubuntu-news a:hover {
        color: #e95420 !important;
    }
    
    .last-col, .strip-inner-wrapper {
        background: none !important;
    }
    
    #sc-mockup, #sc-mockup .header {
        background: none !important;
    }
    
    .one-col, .two-col, .three-col, .four-col, .five-col, .six-col, .seven-col, .eight-col, .nine-col, .ten-col, .eleven-col, .twelve-col {
        background: none !important;
    }
    
    footer.global li ul li a:link, footer.global li ul li a:visited, footer.global .legal a:link, footer.global .legal a:visited {
        color: #e95420 !important;
    }
    
    footer.global .footer-a h2 a:link, footer.global .footer-a h2 a:visited, footer.global .footer-b h2 a:link, footer.global .footer-b h2 a:visited {
        color: #888 !important;
    }
    
    footer.global .legal p {
        background: none !important;
    }
    
    div.footer-div a {
        color: #e95420 !important;
    }
}
