@namespace url(http://www.w3.org/1999/xhtml);
/* =======================================================
            Fyens.dk
       
            by TDJ

            version: Marts 2013

            See comments below to disable what you do not want..
   ======================================================= */

@-moz-document domain("fyens.dk") {
/* ADS */
   .ad,
   .p2w-ad,
   div.textads,
   .class-fsarticlefbnextarticle,
   .class-annoncefbadservingbanner,
   .class-fsafblatestnews2,
   .class-annoncefbadservingbanner,
   .class-annoncefbgoogleadsense,
   .class-xfsafbarticlefinal,
   div[data-blockid="17406"],  /* hide "andre læste også */
   .last-block-class-annoncefbadservingbanner, 
   .last-block-class-fbcodeblock, /* Facebook crap */
   iframe[id^="google"],
   div.banner,
   div[id^="adserving"]{ display:none !important;}
   a[target="_blank"] img { display:none !important;}

   div#wallpaper {
   background-image:none!important}

/* top banner */
#top_banner_container {
   display:none !important;
}


/* TOP LOGO + andre aviser */
div [data-blockid="10002"],  /* not used anymore ?? */
div [data-blockid="10001"],  /* links to other fyens.dk sites */
div[data-blockid="19002"]  /* huge top logo */
     {display:none !important;}


/* Other things 
------------------
*/
/* I do not like... */
div.likes,
.facebook-big-like {display:none!important}  

/*kæmpe fordele.. nej tak */
div.width-12 >div > div[data-blockid="13819"],
div.width-12 >div > div[data-blockid="17792"],
div.width-12 >div > div[data-blockid="17793"],
div.width-12 >div > div[data-blockid="17827"]
{display:none}
/* =================================================================
   FJERN KÆMPE BLOKKE
===================================================================== */
    .kundeservice,
    .class-userbaseclientloginform,
    .bottom-menu    /* FOOTER MENU */
        {display:none !important;}


/* =================================================================
   TOP MENU
===================================================================== */
.fyn-menu a {
    font-size: 12px !important;
}
.fyn-menu a:hover {
    background:white  !important;
    color:black !important;
}

/* ARTIKELBREDDE ======================= */
.block.width-5 {
   width:95% !important;
}

div.container.width-8 {
  width:auto!important
  }



/* 

    MOSTLY OLD THINGS FROM HERE...
    CLEANUP TO BE DONE!
*/


/* Blok under Articles  */
#layout_under_gallery,
.textads_container {display:none!important;   }

/* Vertical Ads */
.white_table {display:none!important; }
.vejviser_form , .textad_header , .textad_container,
#article_top_right,
#article_text_ad
{display:none !important; }

/* RIGHT SIDE TEXT ADS */
#layout_right > .block_column {
   display:none !important;
}

/* Remove header with other fyens media sites */
#fm_header {
   display:none;
}




/* submenu .. disable if you need it */
#site_top_left_small_navigation {
   display:none !important;
}
#site_top {
  height:45px !important;
}

/* logos.. we know where we are... */
#top_right_pad {
   display:none !important;
}


/* fix text flow in article and comments fix */
.article_column,
#layout_center,
[class^="forum_speech"] {
  max-width: 95% !important;
  width: 95% !important;
}

/* recommend box */
#recommend_box {
  display:none !important;
}

/* related links in article */
.related {
  display:none !important;
}

/* larger image text */
.image_text {
  font-size:150% !important;
}

/* fix image box in article */
.article_image {
   width:100% !important;
}
/* fact box in article */
.fact_text {
  display:none !important;
}

/* comments BG */
[class^="forum_speech"] {
background-image:none !important;}



/* ================= END HERE ================== */
}
