css:
@-moz-document domain("live.bilibili.com") {
.user-level-icon,							/*用户等级*/
.title-label.dp-i-block.p-relative.v-middle,/*活动头衔*/
.chat-item.gift-item,						/*送的礼物*/
.chat-item.system-msg.small-tv,				/*其他直播间赠送小电视的提示信息*/
.chat-item.system-msg.news,					/*系统公告*/
.rank-list-section							/*等级排名、骑士团什么的*/
{
    display: none !important;
}

.chat-history-panel.p-relative 				/*弹幕信息面板*/
{
    height: 92% !important;
    -webkit-transform: translateY(-10px);
    background-color: #f8f8f8;
    z-index: 0 !important;
}
div#chat-control-panel-vm 					/*发送弹幕面板*/
{
    -webkit-transform: translateY(-10px);
}


section#sections-vm							/*视频下面的区域整体下移*/
{
	-webkit-transform: translateY(50px);
}
}

@-moz-document domain("live.bilibili.com") {
/*导航栏*/

.link-navbar[data-v-32a4bc13]{
    height: 37px;
}

.link-navbar-ctnr{
	-webkit-transform: translate(-215px,-13px);
	background-color: rgba(0, 0, 0, 0);
	box-shadow: none;
}
.right-part.h-100.f-right.f-clear{
    -webkit-transform: translate(-50px,10px);
}
.search-bar-ctnr.dp-table.h-100.f-left {
    -webkit-transform: translateX(6px);
}

/*播放器*/
.app-body.p-relative.m-auto 						/*减小视频区域上下左右间隔*/
{
    margin:0 !important;
}
main.app-content.p-relative
{
    padding:20px !important;
}


.bilibili-live-player-video-gift  					/*视频上滚动的礼物信息*/
{
	display:none;
}

/*礼物栏*/

.treasure-box.p-relative  							/*宝箱*/
{
    -webkit-transform: translateY(-20px);
}
}
