css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("myotterspace.com") {
body, .widget ul li a, .f-widget ul li a .site-footer .textwidget {
    color: grey;
   background-color: black;
}
  .header {
    width: 100%;
    position: relative;
    margin: 0;
}
.entry-title a {
    color: grey;
}
  .widget-title, .site-footer-widget .widget-title {
    color: grey;
}
  .main-nav {
    background: #545454;
    position: relative;
}
  .nav-links a, .nav-links .dots, .nav-links span.current {
    background: #5d5d5d;
}
  .page .entry-title, .single-post .entry-title {
    color: #969696;
}
  a {
    color: grey;
}
  .site-footer, .f-right ul.menu li a {
    color: grey;
}
  a {
    background-color: black;
}
  ul.menu li:hover, ul.menu li a:hover, ul.menu li.current-menu-parent > a, ul.menu li.current-menu-ancestor > a, ul.menu li.current_page_ancestor > a, ul.menu li.current-menu-item > a {
    background: grey;
}
  .main-nav ul.menu li a {
    color: #FFF;
}
  input[type="text"], input[type="email"], input[type="url"], input[type="password"], input[type="search"], textarea {
background: grey;
}
  button, input[type="button"], input[type="reset"], input[type="submit"] {
    background: grey;
}
}
