css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("neverfate.ru")
{
    body {
        background-image: none !important;
    }
    
    
    #sizer_v_id {
        height: 4px !important;
        background-color: #300;
    }
    .splitter_v {
        height: 4px !important;
    }
    

    .frame_main {
        overflow:auto;
        background:rgb(236, 216, 174) !important;
    }
    .main_frame_title_line {
        height: 22px !important;
        background: none !important;
    }
    .main_frame_title {
        height: 0px !important;
        background: none !important;
        color: rgb(72,0,0) !important;
        font-size: 14px !important;
    }

    div.top_frame_conner_left, div.top_frame_conner_right {
        display: none;
    }

    /* Слоты свитков */
    /*.it_bg_50_30 {
        background: #ddd !important;
    }*/

    /* тупые таблицы */
    div.tbl_main_frame,
    div.tbl_main_frame_15 {
        border: none;
    }
    div.tbl_main_frame > table > tbody > tr > td {
        padding: 4px;
    }
    div.tbl_top {
        display: none;
    }
    div.tbl_bottom {
        visibility: hidden;
    }

    div.tbl_flag_1,div.tbl_flag_2,div.tbl_flag_3,div.tbl_flag_4,
    div.tbl_shadow
    {
        display: none;
    }
    div.tbl_border,div.tbl_border_2,div.tbl_border_3
    {
        height: 1px !important;
        background: #897856 !important;
    }
    div.tbl_title {
        border-left: 1px solid #897856;
        border-right: 1px solid #897856;
        padding: 2px;
    }

    /* ник чара и пр. */
    div.char_info_box_line {
        height: 60px !important;
    }
    div.char_info_title
    {
    /*    background: url("http://imgs.neverfate.ru/i/char_info_title.png") no-repeat scroll left -23px transparent !important;
    */
        background: none !important;
        height: 28px !important;
    }
    div.char_name_2, div.sec_2 {
        color: #333 !important;
    }
    div.char_info_name {
        top: 5px !important;
    }
    div.char_info_komplekt_line {
        top: 6px !important;
        background: none !important;
    }
    div.char_info_info_hp_line, div.char_info_info_mana_line, div.char_info_info_wear_line,
    div.char_info_bar_prog_hp_line, div.char_info_bar_prog_mana_line, div.char_info_bar_prog_wear_line
    {
        top: 42px !important;
    }

    div.char_info_bar_hp_line, div.char_info_bar_mana_line, div.char_info_bar_wear_line {
        top: 40px !important;
    }

    table.char_info_shm,div.char_info_col_left,div.char_info_col_right {
        top: 80px !important;
    }
    div.char_info_bottom {
        display: none;
    }

    div.char_info_info_hp, div.char_info_bar_prog_hp {
        top: 30px !important;
    }
    div.char_info_bar_hp {
        top: 28px !important;
    }
    div.char_info_info_mana, div.char_info_bar_prog_mana {
        top: 47px !important;
    }
    div.char_info_bar_mana {
        top: 45px !important;
    }
    div.char_info_info_wear, div.char_info_bar_prog_wear {
        top: 64px !important;
    }
    div.char_info_bar_wear {
        top: 62px !important;
    }
    div.char_info_border_1,div.char_info_border_2,div.char_info_border_3 {
        display: none;
    }
    div.char_info_footer {
        top: 352px !important;
    }
    div.char_info_box {
        height: 360px !important;
    }







    /* chat */
    div.chat_frame_1,div.chat_frame_2,div.chat_frame_3,div.chat_frame_4,
    div.chat_frame_5,div.chat_frame_6,div.chat_frame_7,div.chat_frame_8
    {
        display: none;
    }

    div.chat_list {
        padding: 2px 5px 3px 5px !important;
    }
    div.chat_panel_container {
        padding-top: 2px !important;
        padding-bottom: 2px !important;
    }
    td.chat_panel_normal, td.chat_panel_active, td.chat_panele_hidden {
        padding-top: 0px !important;
        padding-bottom: 0px !important;
    }

    div.bot_msg_left {
        background: url(http://imgs.neverfate.ru/i/msg_left.png) no-repeat scroll -13px transparent !important;
        width: 9px !important;
        margin-left: 10px;
    }
    div.bot_msg_right {
        width: 9px !important;
    }

    /*td.BottomBorder, */td.LeftBorder, td.RightBorder {
        display: none;
    }
    div#sizer_v.splitter_v {
    /*    background: #aa7 !important;*/
    }
    /* вертикальный разделитель чат/олнайн */
    div#sizer_h.splitter_h {
        background: #aa7 !important;
    }
    td.Splitter_bg {
        background: none !important;
        width: 0;
    }

    div.dot_1, div.dot_2, div.dot_3, div.dot_4, div.dot_5, div.dot_6 {
        display: none;
    }

    /* Запрещаем выделение текста в некоторых фреймах */
    div.top_frame_bg, div.bottom_frame_bg {
        -moz-user-select:-moz-none;
    }
    input {
        -moz-user-select:text;
    }

    /* убрать деньги в чате */
    body.frame_plain_bg > div[style*="fixed"] {
        display: none;
    }


    #vk_poll {
        display: none;
    }

    /* спрятать подсказки */
    .noticeMain {
        display: none !important;
    }
    
    #sysmsg_main_id {
        display: none !important;
    }
    
    /* Убрать кнопки изгнания */
    .frame_main div.xbg {
        display: none;
    }
    
    
}
