css:
@-moz-document domain("tutanota.de") {
  /* adjust height of left column menu to match mail items */

/*
@import url('https://fonts.googleapis.com/css?family=Lato|Open+Sans|Roboto|Ubuntu');
body {
    font-family: 'Lato';
    font-family: 'Open Sans';
    font-family: 'Ubuntu';
    font-family: 'Roboto';
}
*/
.menuItem {
    height: 48px;
    line-height: 48px;
}
li#menu_mail,
li#menu_settings {
    float: left !important;
}
header#main>div#right-navbar>menu>ul#menu_list {
    float: left !important;
    width: 100% !important;
}
header#main > div#right-navbar {
    margin-left: 0;
}
#logo {
    display: none !important;
}
#newMailAction {
    position: fixed;
    left: 170px;
    top: 6px;
}
#deleteMailAction {
    float: left;
}
button#trashMultipleAction {
    float: left;
}
/* make the image block notification match heigh of email */

.notificationBar {
    height: 49px;
}
div.notificationBar > div.notificationMessage {
    font-size: 12px;
    padding-left: 13px;
}
/*remove border */

body > div#tutanota > div#viewContainer > div.content > div.viewColumn {
    border-right: none;
}
header#main.loggedIn {
    border: none;
    background: #F1F1F1;
}
body div#searchAndMailListColumn>div#searchAndMailList>section.list div.listElement {
    border: none !important;
}
/* hide notification on blocked msgs */

#innerConversationColumn .notificationMessage {
    display: none;
}
/* make from in mail list bold */

.mailInfo {
    font-family: Arial !important;
    font-weight: bold;
    color: #AAA;
    padding: 0 0 0 10px;
    font-size: 12px !important;
}
/* reposition the notification buttons to display or hide imgs */

.notificationBar {
    position: fixed !important;
    top: 54px !important;
    width: 200px !important;
    border: none !important;
    padding: 0;
    margin: 0;
    left: 355px;
    background: transparent !important;
    height: 43px;
}
.notificationButtons {
    display: block !important;
    float: left;
    width: 100% !important;
    background: #F1F1F1;
}
.buttonBar {
    border-bottom: none !important;
}
/* switch position of hide and show img button */

#innerConversationColumn .notificationButtons .cancel {
    float: left;
}
#innerConversationColumn .notificationButtons .loadImages {
    float: right;
}
/* change color of header values */

body section.mail>header div.bubble.displayRecipient {
    /* background: #dde2e9; */
    
    border-left: 4px solid #DDE2E9;
    border-radius: 0;
    background: #F8F8F8;
}
body section.mail>header>div.headerTable>div.headerLine>div.name {
    width: 55px !important;
    text-align: right;
}
/* remove colors from bg */

.menuList {
    background: none !important;
}
.buttonBar {
    background: none !important;
    border: none;
}
/* add color to mail body bg */

.mailBody {
    background: #FAFAFA;
    padding: 25px !important;
}
/* change width of columns */

#mailFolderColumn {
    /*     width: 200px !important; */
}
#conversationColumn {
    width: 60% !important;
}
body div#searchAndMailListColumn>div#searchAndMailList>section.list div.listElement {
    height: 71px;
}
body div#searchAndMailListColumn>div#searchAndMailList section#mailList>div#innerMailList div.mailInList {
    padding: 13px 10px;
}
body div#searchAndMailListColumn>div#searchAndMailList>section.list div.listElement:hover {
    background: #DDE2E9;
}
body section.mail>header>div.mailSubject {
    padding: 10px;
    font-size: 22px;
}
}
