css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("com.nicovideo.jp") {
  /* 文字設定 */
  html,body,div,h1,.h3,h4,h5,h6,p,blockquote,pre,table,th,tr,td,ul,ol,li,dl,dt,dd,form,fieldset,legend,hr,span,a,img,strong,em,
  ins,del,cite,abbr,acronym,sup,sub,dfn,code,var,samp,kbd,b,i{
    font-weight:800;
    font-family:'ヒラギノ角ゴ Pro W3', 'Hiragino Kaku Gothic Pro', メイリオ, Meiryo, 'ＭＳ Ｐゴシック', sans-serif;
    text-decoration: none;
    color: #ccc !important; 
    border: inset 0px #222742 !important;
    text-shadow: 0px !important;
    text-shadow: none !important;
  }

  html, body, .area-pcSiteHeader, .area-communityNavigation{
   background:#111;
   color: #ddd;
  }
  .cp-communityNavigation .item a,  .cp-communityNavigation .on a{
   color: #bbb  !important;
  }
  .cp-communityNavigation .item a:hover,  .cp-communityNavigation .on a:hover{
   color: #fff !important;
  }
  .area-siteFooter, .inHouseAd_wrap, .inHouseAd, .area-pcSiteHeader, .subHeader, .mainHeader, .cp-communityTop .md-sideContentBox .mainHeader, .area-communityFooter{
    display: none;
  }
  .md-mainContentBox communityProfile a, .cp-communityTop .md-sideContentBox .officialNotice_title, .cp-communityTop .md-sideContentBox .liveTitle a{
    color:#D2691E !important;    
  }
  /* ヘッダー */
  #siteHeader, #siteHeaderInner,li.videoPlayback.active a,li.comment a,li.performance a,li.imageQuality a,li.videoPlayback a, .videoSearchOption .tab{
      background:#2f2f2f !important;
      border: inset 0px #222742 !important;
  }

  #siteHeader #siteHeaderInner ul li:hover,li.videoPlayback.active a:hover,li.comment a:hover,li.performance a:hover,li.imageQuality a:hover,li.videoPlayback a:hover {
    background:#D2691E !important;
  }
  #siteHeader #siteHeaderInner ul li a span {
    padding: 0px 6px;
    cursor: pointer;
    color:#fff !important;
    border: inset 0px #222742 !important;
  }
  #siteHeaderRightMenuContainer, #siteHeaderLeftMenuContainer,.nicolib-SuggestSearch-suggestions.suggestBox,.nicolib-SuggestSearch-suggestion-list.suggestBoxInner{
      background:#2f2f2f !important;
  }
  #siteHeaderRightMenuContainer a:hover, #siteHeaderLeftMenuContainer a:hover, .videoSearchOption .active, .videoSearchOption .tab:hover, .category_row li a:hover, ul.child_category_group li.child_category a:hover{
    background:#D2691E !important;
  }
   #siteHeader{
      border-bottom: solid 1px #444 !important;
   }
  
  .noticeList, #profile_text_box, .md-sideContentBox, .cp-communityHeader .communityThumbnail .flag, .cp-communityLive .communityLive_resultList, .last, .next, .cp-communityBbs, #community-body, div.community-bbs{
    background:#222 !important;
    background-color: #222 !important;
  }
  .area-communityHeader,
  .mainContentBox_header,
  .submitButton {
    background-color: #272727 !important;
  }
  .mainHeader {
    border-top-color: #272727 !important;
  }
  
  .md-settingButton, .cp-communityHeader .followButton.following {
    background-color: #434343 !important;
  }

  .area-sideContent a, .area-sideContent a:link,
  .area-mainContent a, .area-mainContent a:link,
  .communityControlList a,
  h3.noticeTitle,
  .md-sideContentBox .text.inactive,
  .now_live_title {
    color: #0033cc !important;
  }
  
  .cp-communityNavigation a {
    color: #272727 !important;
  }
  
  .js-checkBroadcastLimit a.broadcastButton {
    color: #FFF !important;
  }
  
  .blomaga_displayControl.inactive {
    border-color: #0033cc !important;
  }
  
  .cp-communityNavigation .item.active,
  .cp-communityNavigation .item.active:hover{
    border-bottom: 3px solid #272727;
  }
}
