css:
/* i really want this to be global */
#header {
    background: #222
}
div.side {
    padding: 0px 0px 5px 5px;
    background-color: #fff;
    background-image: url("https://s3.amazonaws.com/b.thumbs.redditmedia.com/gRbXhxSi1UZoY_rabQOYfQkR0pGoDfVTx2iwRCVIA4I.png")!important;
    background-position: 0px 0px!important;
    background-repeat: no-repeat!important
}
.side {
    width: 300px;
    margin: 10px 10px 10px auto;
    padding: 250px 0px 10px 0px!important;
    background-color: white;
    opacity: 0.90;
    z-index: 0;
    overflow: hidden
}
#header {
    background: url("https://s3.amazonaws.com/b.thumbs.redditmedia.com/9pKO6awHckkDh5XFKcwK7rzQ5uf3sRiG4fWijvWeGIU.png") no-repeat scroll center top;
    background-size: 100%;
    border: none
}
#header .pagename a {
    position: absolute;
    margin-top: 100px;
    color: silver
}
#header-img {
    margin-top: 71px
}
#header-img {
    background: url("https://s3.amazonaws.com/a.thumbs.redditmedia.com/q6wwbWzGytdjWgZ0.png") no-repeat!important;
    width: 0!important;
    height: 0!important;
    padding: 0 150px 50px 0;
    position: relative
}
#header-img:hover {
    background-position: right top!important
}
.titlebox span.word {
    display: none
}
div.titlebox .subscribers span.number:after {
    content: " Guards hired "
}
.keyHighlight {
    outline: 1px dashed #333!important;
    background-color: #40454A!important
}
div.titlebox .users-online span.number:after {
    content: " On night watch right now"
}
.bottom {
    font-size: 0!important
}
.bottom::before {
    content: "created by Freddy";
    font-size: 10px!important
}
.bottom .age {
    font-size: 10px!important
}
.tabmenu {
    margin-left: 160px
}
.tabmenu li {}
.tabmenu li a {
    background: none;
    border: none!important;
    vertical-align: bottom;
    height: 20px;
    line-height: 20px;
    display: inline-block;
    font-weight: 400;
    letter-spacing: 2px;
    color: #fff
}
.tabmenu li a {
    display: inline-block;
    position: relative;
    padding-bottom: 3px
}
.tabmenu li a:before {
    content: "";
    display: block;
    position: absolute;
    left: 0;
    bottom: 0;
    height: 3px;
    width: 0;
    transition: width 0s ease, background .3s ease
}
.tabmenu li a:after {
    content: "";
    display: block;
    position: absolute;
    right: 0;
    bottom: 0;
    height: 3px;
    width: 0;
    background: #460702;
    transition: width .3s ease
}
.tabmenu li a:hover:before {
    width: 100%;
    background: #8A0707;
    transition: width .3s ease
}
.tabmenu li a:hover:after {
    width: 100%;
    background: transparent;
    transition: all 0s ease
}
.flair {
    border: none!important;
    padding: 0px;
    background: url("https://s3.amazonaws.com/b.thumbs.redditmedia.com/Pap7EJ4kUCG7IrwWATrwocr8e4oewiJ52irOh2sSd7s.png") no-repeat -9999px -9999px;
    display: inline-block
}
.flair-bonnie {
    background-position: 0 0;
    width: 25px;
    height: 25px
}
.flair-chica {
    background-position: 0 -75px;
    width: 25px;
    height: 25px
}
.flair-foxy {
    background-position: 0 -150px;
    width: 25px;
    height: 25px
}
.flair-freddy {
    background-position: 0 -225px;
    width: 25px;
    height: 25px
}
.flair-goldenfreddy {
    background-position: 0 -325px;
    width: 25px;
    height: 25px
}
.flair-noskin {
    background-position: 0 -425px;
    width: 25px;
    height: 25px
}
.flair-newbonnie {
    background-position: 0 -500px;
    width: 25px;
    height: 25px
}
.flair-newchica {
    background-position: 0 -575px;
    width: 25px;
    height: 25px
}
.flair-newfoxy {
    background-position: 0 -650px;
    width: 25px;
    height: 25px
}
.flair-newfreddy {
    background-position: 0 -775;
    width: 25px;
    height: 25px
}
.marknsfw-button a[href="#"],
.unmarknsfw-button a[href="#"] {
    font-size: 0px!important;
    display: inline-block;
    vertical-align: top;
    overflow: hidden;
    letter-spacing: -20px;
    content: ""
}
.unmarknsfw-button a[href="#"]:before,
.marknsfw-button a[href="#"]:before {
    font-size: 10px!important;
    display: block;
    letter-spacing: normal;
    content: "spoiler"
}
.unmarknsfw-button a[href="#"]:before {
    content: "un-spoiler"
}
.nsfw-stamp acronym {
    border: #5F99CF 1px solid!important;
    display: inline-block;
    font-size: 0px!important;
    letter-spacing: 0px;
    overflow: hidden;
    vertical-align: bottom;
    margin-bottom: -1px;
    content: ""
}
.nsfw-stamp acronym:after {
    color: #336699!important;
    content: "SPOILER"!important;
    display: block;
    font-size: x-small!important;
    text-decoration: none;
    margin: -15px 2px 1px
}
noindex:-o-prefocus,
.nsfw-stamp acronym {
    display: none!important
}
div.over18 .entry .buttons .first {
    padding-left: 0px
}
