css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wizchan.org")
{


/*           GRADIENT BACKGROUND STATIC       */
body
{
background: linear-gradient(to bottom, #57A2F5,#B1CAE3 100%);
background-attachment: fixed;
}



a:link:hover, a:visited:hover, a.post_no:hover
{
color: white;
text-decoration: none;
}



a:link, a:visited, a.post_no:visited
{
color: rgba(0, 0, 0, .5);
text-decoration: none;
}



p.intro span.mentioned.unimportant
{
style: italics;
}




div.h2
{
background: rgba(255, 255, 255, 0.24);
color: black;
text-shadow: 1px 1px 2px rgba(0, 0, 0, 0.25);
border-width: 0px;
border-top-width: 0px;
border-bottom-width: 0px;
border-left-width: 0px;
border-right-width: 0px;

border-top-color: rgba(196, 205, 224, 0);
border-bottom-color: rgba(196, 205, 224, 0);
border-left-color: rgba(196, 205, 224, 0);
border-right-color: rgba(196, 205, 224, 0);
}



div.ban
{
background: rgba(255, 255, 255, 0.24);
color: black;
border-width: 0px;
border-top-width: 0px;
border-bottom-width: 0px;
border-left-width: 0px;
border-right-width: 0px;

border-top-color: rgba(196, 205, 224, 0);
border-bottom-color: rgba(196, 205, 224, 0);
border-left-color: rgba(196, 205, 224, 0);
border-right-color: rgba(196, 205, 224, 0);
}



div.box
{
background: rgba(255, 255, 255, 0.24);
color: black;
border-width: 0px;
border-top-width: 0px;
border-bottom-width: 0px;
border-left-width: 0px;
border-right-width: 0px;

border-top-color: rgba(196, 205, 224, 0);
border-bottom-color: rgba(196, 205, 224, 0);
border-left-color: rgba(196, 205, 224, 0);
border-right-color: rgba(196, 205, 224, 0);
}



body div.box-wrap div.box
{
background: rgba(255, 255, 255, 0.24);
color: black;
border-width: 0px;
border-top-width: 0px;
border-bottom-width: 0px;
border-left-width: 0px;
border-right-width: 0px;

border-top-color: rgba(196, 205, 224, 0);
border-bottom-color: rgba(196, 205, 224, 0);
border-left-color: rgba(196, 205, 224, 0);
border-right-color: rgba(196, 205, 224, 0);
}



div.boardlist
{
color: rgba(0, 0, 0, 0.24);
text-align: center;
}


img.board_image
{
border-width: 0px;
}


/*               POST TABLE CRAP       */
form table tr th
{
background: transparent;
color: rgba(0, 0, 0, 0.5);
border: 1px solid transparent;
border-width: 0px;
}


form table tr td
{
color: rgba(0, 0, 0, 0.5);
border-width: 0px;
}


form table tbody tr td textarea#body
{
border-width: 0px;
}


form table tbody tr#upload_embed td input
{
border-width: 0px;
}


form table tbody tr td input
{
border-width: 0px;
}


form table tbody tr td input.password
{
border-width: 0px;
}


form table tbody tr td select#email_selectbox option
{
border-width: 0px;
}


form table tbody tr#upload td input#upload_file
{
border-width: 0px;
}


form table tbody tr th
{
border-width: 0px;
}


form table tbody tr ul
{
display: none;
}



/*       CATALOG GRID SQUARES   */
div.threads ul#Grid li.mix.mix_all div.thread.grid-li.grid-size-small
{
border-width: 0px;
}


div.banner
{
background: rgba(255, 255, 255, 0.24);
color: white;
text-shadow: 1px 1px 2px rgba(0, 0, 0, 0.25);
}




header
{
color: white;
}


h2
{
color: rgba(0, 0, 0, 0.5);
}


/*         USERNAME          */
p.intro label span.name
{
color: rgba(25, 100, 25, 0.5);
}


/*         SAGE/NOKO         */
p.intro label a.email span.name
{
color: rgba(0, 0, 0, 0.5);
}


span.subject
{
}



/*         TIME/DATE/FILENAME/POST NO./ETC     */
p.intro
{
color: rgba(0, 0, 0, 0.5);
}



/*         TIME/DATE/FILENAME/POST NO./ETC     */
p.fileinfo 
{
color: rgba(0, 0, 0, 0.5);
}



/*     "POST TOO LONG..." MESSAGE   */
span.toolong
{
color: rgba(0, 0, 0, 0.5);
}



/*          BIG BOARDNAME AT THE TOP OF EACH PAGE        */
h1
{
color: white;
text-shadow: 1px 1px 2px rgba(0, 0, 0, 0.25)
}


/*       SMALL "BOARDNAME + CATALOG" DISPLAYED UNDERNEATH H1 BOARDNAME    */
header div.subtitle
{
color: white;
text-shadow: 1px 1px 2px rgba(0, 0, 0, 0)
}



h2
{
}



/*               OP            */
div.post.op
{
}


/*            GREENTEXT          */
span.quote
{
color: black;
background: rgba(255, 255, 255, .3);
}



span.mentioned.unimportant
{
/*doesn't work*/
}



/*      HIGHLIGHTED REPLY       */
div.post.reply.highlighted
{
background: rgba(255, 255, 255, 0.36);
}


/*         REPLY       */
div.post.reply
{
background: rgba(255,255,255,0.12);

border-top-width: 0px;
border-bottom: 25px solid transparent;
border-left-width: 0px;
border-right-width: 0px;
}



/*     CREATES THE EXTRA SPACING BETWEEN THREADS      */
hr
{
border-style: solid;
background: none;
visibility: hidden;

border-top-width: 40px;
border-top: thick double #ffffff 0%;
border-bottom-width: 0px;
border-left-width: 0px;
border-right-width: 0px;
}



div.pages
{
background: rgba(0, 0, 0, 0);
border-width: 0px;
}


div.pages.a
{
color: white;
}




/*      COPYRIGHT + DISCLAIMER MESSAGE AT THE BOTTOM (REMOVED)    */
footer
{
display: none;
}






}
