* {
  font-family: 'Roboto', sans-serif;
}

a {
  color: /*[[main_color]]*/;
}

a:hover {
  color: /*[[main_color]]*/;
  text-decoration: none;
}

body {
  background: url(/*[[page_bg]]*/);
  background-size: 100% 100%;
  background-repeat: no-repeat;
  background-attachment: fixed;
}

body>iframe {
  width: 350px;
  top: 204.88px;
  left: 0;
  position: absolute;
}

#headerwrapper {
  background-color: #fff;
  background-image: none;
  box-shadow: 0 8px 17px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19) !important;
  position: fixed;
  width: 350px;
  height: 100%;
  top: 0px;
  z-index: 20100;
}

#headerwrapper #header {
  height: 196.88px; /*350 / 16 * 9*/
  /*
  background-color: #3381f5;
  */
  background: url(/*[[sidebar_bg]]*/);
  background-size: 100% 100%;
  width: 100%;
  left: 0;
  top: 0;
  margin-left: 0;
  margin-right: 0;
  position: absolute;
  padding-top: 0;
  padding-bottom: 8px;
}

#headerwrapper #header h1 {
  padding: 0;
  position: absolute;
  bottom: 0;
  left: 0;
  height: 56px;
  width: 100%;
  font-weight: 500;
  font-size: 14px;
  color: #fff;
}

#headerwrapper #header h1 :first-child a {
  text-transform: capitalize;
  font-weight: 400;
  font-size: 14px;
  color: #fff;
  position: absolute;
  bottom: 0px;
  width: 350px;
  padding-left: 16px;
  line-height: 16px;
}

#headerwrapper #header h1 :nth-child(2) {
  color: white;
  display: block;
  position: absolute;
  font-size: 14px;
  padding-left: 16px;
  line-height: 16px;
  top: 8px;
}

#headerwrapper #header h1>:only-child {
  text-transform: capitalize;
  font-weight: 500;
  font-size: 14px;
  color: #fff;
  position: absolute;
  bottom: 8px;
  line-height: 40px;
  width: 350px;
  padding-left: 16px;
}

#headerwrapper #header .header-links {
  padding: 0;
  float: left;
}

#headerwrapper #header .header-links .section {
  height: 48px;
  position: absolute;
  margin-left: 0;
  width: 100%;
  left: 0;
  top: 0;
  color: white;
}

#headerwrapper #header .header-links .section>.user-avatar {
  border-radius: 0;
  top: 23.44px; /*196.88 - 56 - 100 / 2*/
  left: 16px;
  position: absolute;
}

#headerwrapper #header .header-links .section>.user-avatar img {
  height: 100px;
  max-width: 100px;
  width: 100px;
  border-radius: 50px;
}

#headerwrapper #header .header-links .section>a:first-of-type {
  line-height: 48px;
  float: right;
  margin-right: 50px;
  color: rgba(255, 255, 255, 0.7);
  text-shadow:none;
}

#headerwrapper #header .header-links .section>a:last-of-type {
  margin-top: 16px;
  float: right;
  position: absolute;
  right: 16px;
  text-shadow:none;
}

#headerwrapper #header .header-links .section>a:hover {
  text-decoration: none;
}

#headerwrapper #header .header-links .section #messages-count {
  position: absolute;
  right: 5px;
  top: 14px;
}

#headerwrapper #header .header-links .section #messages-count a {
  border: 0;
  background-image: none;
}

#headerwrapper #menu {
  position: absolute;
  border-bottom: none;
  padding: 0;
  top: 242.88px; /*196.88 + 8 + 8 + 30*/
  width: 100%;
}

#headerwrapper #menu>ul {
  padding: 0;
}

#headerwrapper #menu>ul li {
  box-shadow: none;
  border: none;
  transition: background-color .15s linear;
  background-image: none;
  background-color: #fff;
  height: 48px;
  width: 100%;
}

#headerwrapper #menu>ul li.right {
  float: left;
}

#headerwrapper #menu>ul li.right>a {
  padding: 0 16px;
}

#headerwrapper #menu>ul li:hover {
  background-color: rgba(0, 0, 0, 0.1) !important;
}

#headerwrapper #menu>ul li:active {
  background-color: rgba(0, 0, 0, 0.2) !important;
}

#headerwrapper #menu>ul li>a{
  font-size: 14px;
  line-height: 48px;
  text-align: left;
  padding-left: 16px;
  color: rgba(0, 0, 0, 0.87);
  font-weight: 500;
  text-shadow:none;
}

#headerwrapper #menu>ul li.selected>a {
  color: rgba(0, 0, 0, 0.87);
}

#headerwrapper #menu>ul li>a span.i {
  display: none !important;
}
/*
#headerwrapper #menu>ul li.selected:after {
  content: "";
  width: 3px;
  height: 48px;
  position: absolute;
  margin-left: 0;
  right: 0;
  display: block;
  background-color: white;
  margin-top: -48px;
}
*/

#headerwrapper #menu>ul li.first {
  border-top-left-radius: 0;
  border-bottom-left-radius: 0;
  border: none;
}

#headerwrapper #menu>ul li.last, #headerwrapper #menu>ul li.first, #menu>ul li:last-child {
  border-top-right-radius: 0;
  border-bottom-right-radius: 0;
  border: none;
}

#headerwrapper #menu>ul li .drop_menu {
  left: 350px;
  position: fixed;
}

#headerwrapper #menu>ul li .drop_menu .inner {
  position: relative;
  width: auto;
  margin-top: -47px !important;
  border-radius: 2px;
  box-shadow: none;
  box-shadow: 0 8px 17px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19);
}

#headerwrapper #menu>ul li .drop_menu .inner::before {
  top: 0px;
  display: none;
  left: 0;
  border-right-color: green;
  border-bottom-color: rgba(0,0,0,0);
}

#headerwrapper #menu>ul li p:not(:empty) {
  width: auto;
  line-height: 45px;
  height: 45px;
}

#headerwrapper #menu>ul li p a {
  display: block !important;
  font-weight: 400 !important;
  height: 100%;
  padding: 0;
  border: 0 !important;
  line-height: 45px;
  width: auto;
}

#headerwrapper #menu>ul li p a:hover {
  box-shadow: none;
  border: none !important;
  padding: 0;
  height: 100%;
  background: /*[[main_color]]*/ !important;
}

#headerwrapper #menu>ul li .drop_menu p:first-child, .drop_menu p:first-child a {
  border-top-left-radius: 2px;
  border-top-right-radius: 2px;
}

#headerwrapper #menu>ul li .drop_menu p:last-child, .drop_menu p:last-child a {
  border-bottom-left-radius: 2px;
  border-bottom-right-radius: 2px;
}

#tabs {
  position: relative;
  width: calc(100% - 350px);
  border-radius: 0;
  margin: -30px 0 0 0;
  float: right;
  height: 56px;
  background-color: #fff;
  box-shadow: 0 2px 5px 0 rgba(0, 0, 0, 0.16), 0 2px 10px 0 rgba(0, 0, 0, 0.12);
}

#tabs ul {
  background-color: #fff;
  border-radius: 0;
  height: 100%;
}

#tabs ul li {
  border: 0;
  background-color: #fff;
  background: #fff;
  height: 100%;
  text-align: center;
  position: static;
  float: left;
  margin: 0;
}

#tabs ul li a {
  color: rgba(0, 0, 0, 0.64);
  height: 100%;
  border: 0 !important;
  font-weight: 500;
  width: 100px;
  line-height: 56px;
  border-right-width: 0;
  box-shadow: none;
  text-shadow: none;
  margin: 0;
  transition: background-color .15s linear;
  
}

#tabs ul li a:hover {
  text-decoration: none;
  color: rgba(0, 0, 0, 0.87);
  opacity: 1;
  background-color: rgba(0, 0, 0, 0.1);
}

#tabs ul li a:active {
  color: rgba(0, 0, 0, 0.87);
  opacity: 1;
  background-color: rgba(0, 0, 0, 0.2);
}

#tabs ul li.selected>a {
  background-image: none;
  color: rgba(255, 255, 255, 0.87);
  border: 0 !important;
  box-shadow: none;
  text-shadow: none;
  background-color: /*[[main_color]]*/;
  opacity: 1;
}

#tabs li:first-child a, #tabs li.first a {
  border-top-left-radius: 0;
  border-bottom-left-radius: 0;
}

#tabs li:last-child a {
  border-top-right-radius: 0;
  border-bottom-right-radius: 0;
}

#content {
  margin: 0 0 0 350px;
  position: absolute;
  top: 70px;
  width: calc(100% - 350px);
}

#content #left {
  box-shadow: 0 2px 5px 0 rgba(0, 0, 0, 0.16), 0 2px 10px 0 rgba(0, 0, 0, 0.12);
  border-radius: 2px;
  border: 0;
}

#content #left .header#page-header {
  background-image: none;
  box-shadow: none;
  background-color: white;
  border-top-left-radius: 2px;
  border-top-right-radius: 2px;
}

#content #left a:active {
  background: rgba(0, 0, 0, 0.15);
}

#content #right {
  background: white;
  box-shadow: #0 2px 5px 0 rgba(0, 0, 0, 0.16), 0 2px 10px 0 rgba(0, 0, 0, 0.12);
  padding: 45px 21px 25px 22px;
  font-size: 14px;
  min-height: 500px;
  border-radius: 2px;
}

#content #right {
  color: rgba(0, 0, 0, 0.87);
  text-shadow: none;
}

#content #main #right a {
  color: /*[[main_color]]*/ !important;
}

#content #main #right a:active {
  background: rgba(0, 0, 0, 0.15);
}


#pagewrapper #mailbox-container #mailbox {
  box-shadow: 0 8px 17px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19);
  position: fixed;
  top: 16%;
  border-radius: 2px;
  background-color: #eaf2fe;
  background: #eaf2fe;
  z-index: 20000;
}

#pagewrapper #mailbox-container #mailbox #mailbox-overview #close-mailbox.fr {
  color: rgba(255, 255, 255, 0.7);
  position: absolute;
  top: 10px;
  z-index: 2;
  right: 15px;
  margin-top: 0;
}

#pagewrapper #mailbox-container #mailbox #mailbox-overview #close-mailbox.fr .i {
  display: none !important;
}

#pagewrapper #mailbox-container #mailbox #mailbox-overview h2.title {
  background-image: none;
  background-color: #2867c4;
  color: rgba(255, 255, 255, 0.7);
  font-weight: 400;
  text-shadow: none;
  border-top-left-radius: 2px;
  border-top-right-radius: 2px;
  border-bottom: 0;
  position: absolute;
  top: 0;
  left: 0;
  width: calc(100% - 30px);
  padding: 10px 15px;
  margin: 0;
}

#pagewrapper #mailbox-container #mailbox #mailbox-overview #mailbox-header {
  background: #eaf2fe;
  text-shadow: none;
  border-bottom: 0;
  padding: 6px 14px;
  margin-top: 38px;
}

#pagewrapper #mailbox-container #mailbox #mailbox-overview #bottom-links {
  background-color: #eaf2fe;
}


#footer {
  background: #fff;
  position: fixed;
  bottom: 0;
  left: 0;
  right: calc(100% - 350px);
  padding: 8px 16px;
  z-index: 100101;
  color: rgba(0, 0, 0, 0.87);
  text-shadow: none;
}

#footer a {
  color: /*[[main_color]]*/;
}

#footer a:hover {
  text-decoration: none;
}

#footer div:nth-child(2) {
  width: 100%;
  display: block;
}

#footer div:nth-child(2) a {
  display: none; /*Hide ManageBac homepage link*/
}

#footer .fr {
  display: block;
  width: 100%;
}
}
