css:
@-moz-document domain("watchcartoonsonline.to") {
body {
    background-color: #111;
    color: white;
}
h1, h2, h3, h4 {
    color: white;
}

a {
 color: lightblue;   
}

.post > em  strong > span, .notices {
 color : #d55 !important;   
}

.sidebar .textwidget, .sidebar ul.related-posts-widget, .latestPost  {
    background-color: #404040;
}

.sidebar .widget ul li {
 border-bottom: 1px solid gray;
}

.sidebar ul.related-posts-widget  li * {
    color:white;
}
.sidebar ul.related-posts-widget  li:hover * {
    color:lightblue;
}

.sidebar ul.related-posts-widget  li img {
    color:lightgray;
    font-size: x-small;
}

.sidebar ul.related-posts-widget  li:hover img {
 color:lightblue;
    transition: all 0.50s linear 0s;
}

.sidebar .textwidget p:first-child {
 color : #d55 !important;   
}

.sidebar .textwidget p:nth-child(3) {
    color: lightblue !important;
}

.sidebar #searchform ::-moz-placeholder
{
    background-color: lightgray;
    color : black;
}

/*
  .featured-category-title {
    color : white;
  }*/
body#blog {
    background-image: none;
    background-color: #171717;
}

.single_post {
    background-color: #2f2f2f;
}
.single_post h1 {
    color: white;
}
.main-header {
    background-color: #0A1E32;
}

.breadcrumb > span {
    background-color: gray;
    border-color: black;
    }

.breadcrumb > span:hover {
    background-color: lightgray;
    color: blue;
    }

.breadcrumb > span .arrow:before {
    border-color: rgba(0, 0, 0, 0) rgba(0, 0, 0, 0) rgba(0, 0, 0, 0) gray;	
}
.breadcrumb > span .arrow:after {
    border-color: rgba(0, 0, 0, 0) rgba(0, 0, 0, 0) rgba(0, 0, 0, 0) black;
}
.breadcrumb > span:hover .arrow:before {
    border-color: rgba(0, 0, 0, 0) rgba(0, 0, 0, 0) rgba(0, 0, 0, 0) lightgray;
}



#list-eps {
   background-color: #444; 
}

#list-eps .le-server {
   border-bottom-color : #333; 
}
#list-eps .le-server .les-content .btn-eps {
    color: #ccc;
    background-color: #666;
    border-bottom-color: #555;
}

.pagination a {
    background-color: #444;
}
}

@-moz-document url-prefix("https://www.facebook.com/plugins/feedback.php?href=http%3A%2F%2Fwatchcartoonsonline.to"), regexp("https://www\\.facebook\\.com\\/v\\d\\.\\d\\/plugins\\/like\\.php\\?(.*)") {
.UFIImageBlockContent {
    color: gray;
}

body {
    
    color: white;
}

a {
 color: #55e;   
}
}
