||==================================================================================||
*/@-moz-document domain("www.viki.com") {
*
{
    text-shadow:none!important;
	text-decoration:none !important;
    box-shadow:none!important;
    border-radius:0px!important;
}
@font-face
  {
   font-family: 'Roboto';
   font-style: normal;
   font-weight: 300;
  }
* {font-family: 'Roboto' !important;}
html,.date-day,body
{
	color: #9e9e9e;
}
div[style*="background: rgb(255, 255, 255)"],
div[style*="background:#fff"]
{
background-color: #2f2f2f!important; 
}
/*-header-*/
.navbar-primary,.card,.card-date-container,.page-footer,.menubar
{
    background-color: #161616;
}
/*-search-*/
.icon-search:before
{
	margin-top: 5px;
}
.navbar-search-form
{
    background-color: rgba(0, 0, 0, 0);
}
.navbar-search-form input.navbar-search-box
{
    border-bottom: 0px solid #fff;
    padding-left: 5px;
    border-left: 3px solid #3c9cd7;
}
.navbar-search-form input.navbar-search-box:focus:not([readonly])
{
    border-bottom: 0px solid #fff;
}
/*----navbar secondary------*/
.navbar-secondary, .breadcrumb-header,.breadcrumb-wrapper:before,.breadcrumb-header .breadcrumb-wrapper:before,.dropdown-section
{
    background-color: #242528;
	background-image: none;
	border-bottom: 0px solid #e0e0e0;
}
/*-body-*/
body,.page-container,.section-bg-alt,.modal-content
{
    background: url('/*[[background]]*/') center center  no-repeat fixed!important;
    background: transparent none repeat scroll 0% 0%;
    background-size: cover !important;

    background-color: /*[[changebgcolor]]*/ !important;
}
.thumbnail-img
{
    background-color: rgba(0, 0, 0, 0);
}
/*-borders-*/
.pagination,.menu-dropdown-divider,hr 
{
	border-top: 1px solid #262626;
}
.media-list .media,.separator,.card-date-container,.navbar-expanded .navbar-divider,.collection-item-sm,.page-header-highlight,.schedule-section,.modal-header
{
	border-bottom: 1px solid #262626;	
}
.divider
{
    background-color: #262626;
}
.card-date
{
	border-right: 1px solid #262626;
}
/*--links---*/
.thumb-title,.content-link,.emphasis, .content-heading, .card-heading, .section-heading,h1 a:link,a.tag-contour, .tag-contour,.select2-container .select2-choice
{
    color: #00969e;
}
/*-----visited------*/
h1 a:visited
{
	color: #FADD41;
}
/*------active--------*/
.card-date.active,.card-date.active a
{
    background: #0f405f;
}
/*----tool tip-----*/
.info-tooltip, .hover-tooltip .hover-tooltip-text
{
    background-color: #161616;
    color: #c7c7c7;
    border: 1px solid #262626;
}
.info-tooltip-wrapper.pos-right .tooltip-arrow:before, .info-tooltip-wrapper.pos-left .tooltip-arrow:before, .hover-tooltip:after
{
    color: #262626;
}
/*---card------*/
.card-action-attention
{
    border-top: 1px solid #001f33;
    background-color: #001f33;
}
/*-subnav-*/
.navbar-dropdown-item.hover .navbar-dropdown {
    border-bottom-color: #FADE48;
}
.navbar-dropdown-item .navbar-dropdown-container,a.tag-contour, .tag-contour,.menu-dropdowns
{
    border: 1px solid #363636;
    background-color: #0a0a0a;
}
/*-----link------*/
.navbar-dropdown-item .navbar-dropdown-container a
{
    color: #00a7a9;
}
/*----link hover------*/
.navbar-dropdown-item .navbar-dropdown-container a:hover,.menu-dropdown a:hover,.item-count
{
    color: #FADE48;
    background-color: #2e2e2e;
}
/*------profile-------*/
.page-header-profile,.page-header {
    background: #2B2C30;
}
.profile-header-main {
    background-color: rgba(0, 0, 0, 0);
}
.badges-wrapper {
    background: #242528;
}
.header-tabs {
    background-color: #242528;
    border-top: 1px solid #46484e;
}
.tabs-header .tab a.active {
    color: #D24663;
}
/*------alert-----*/
.alert-info, .alert-success {
    background-color: #0e2533;
    border: 1px solid #0e2533;
}
/*-asd-*/
.text-truncate:after
{
	background-image:none;
}
/*----ads-------*/
div#companion-ad.center-align.mb-small,.mod-advertisement-narrow,.mod-advertisement, .mod-advertisement-no-bg
{
	display:none!important;
}
/*-------hide footer------*/
footer.page-footer,.page-footer,div.page-colophon,.page-colophon
{
	display: /*[[hidefooter]]*/ !important;
}

}
