css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("framabee.org") {
  body {background-color: #222; color: #A6A6A6!important;}
  .dropdown-menu {border-color: #BBB!important; background-color: #222!important;}
  #framanav.navbar {background-color: #222!important; border-bottom: 0px!important;}
  .navbar-header {background-color: #212121!important;}
  .navbar-default {border: none !important;}
  .collapse {background-color: #212121!important;}
  label.checkbox-toggle.btn.btn-default {filter: invert(92%);}
  #framanav .frama {color: #653E9C!important;}
  #q {background-color: #A6A6A6!important;}
  a {color: #148FC0!important;}
  a.btn.btn-success {color: #FFF!important;}
  a.btn-primary {color: #FFF!important;}
  a#cog, a#search_submit, a#search_empty {color: #A6A6A6!important;}
  a.input-group-addon:nth-child(1) {background-color: #222!important;}
  .input-group .form-control:not(:first-child):not(:last-child) {color: #FFF!important;}
  .label {color: #222 !important;}
  #search_submit {background-color: #222!important;}
  #search_empty {background-color: #222!important;}
  p.result-content {color: #A6A6A6!important;}
  div.text-muted {color: #9BAE5F!important;}
  div.panel.panel-default {filter: invert(92%);}
  div.col-md-4 {color : #FFF!important;}
  label.col-md-2 {color : #737373!important;}
  ul.nav.nav-tabs li a {color: #DB460E!important;}
  ul.nav.nav-tabs li {filter: invert(100%)!important;}
  input.btn, div.btn.btn-default {filter: invert(100%)!important;}
  .has-results #menu {margin-top: 1.7em!important;}
  img.img-responsive {filter: invert(100%)!important;}
  .dropdown-menu > li > a:hover, .dropdown-menu > li > a:focus {background-color: transparent!important;}
  h1, h2, h3, h4, h5, h6, .h1, .h2, .h3, .h4, .h5, .h6 {color: #A6A6A6!important;}
  label.btn.btn-sm.btn-default {filter: invert(100%)!important;}
  .table-striped > tbody > tr:nth-of-type(2n+1) {background-color: #222!important;}
  .table-striped > tbody > tr:nth-child(2n+1) > td, .table-striped > tbody > tr:nth-child(2n+1) > th {background-color: #222!important;}
  .table-hover > tbody > tr:hover > td, .table-hover > tbody > tr:hover > th {background-color: #222!important;}
  td.danger {background-color: #222!important;}
  .text-muted, .text-muted a, a.text-muted {color: #A6A6A6!important;}
  #framafooter {background-color : #222!important;}
  #framafooter a {color: #CCC!important;}
  #framafooter h1 {color: #A6A6A6!important;}
}
