css:
@-moz-document domain("vuejs.org") {
body, .content p.tip, #hero, #highlights, #sponsors {
    background:#333;
    color: #567ca2;
}

a, .demo li, #demo li, #logo {
	color: #567ca2;
}

h1, h2, h3, h4, strong, code {
	color: darkorange;
}


code, pre, .highlight  {
	background: #444;
}

.line, .hljs {
	color: #aaa;   
}


#header {
	background: #222;
}


::-webkit-scrollbar {
	background-color: #444;
	border: 2px solid #666;
}

::-webkit-scrollbar-thumb {
	background-color: #222;
	border: 2px solid #555;
}



}
