.forumtxt:after {
    content: '';
    position: absolute;
    border-style: solid;
    border-width: 0 6px 7px;
    border-color: #FFFFFF transparent;
    display: block;
    width: 0;
    z-index: 1;
    top: -7px;
    left: 12px;
}
