css:
@-moz-document url-prefix("https://trello.com") {
body {
background: hsla(216,13%,13%,1) !important;
}


body, button, html, input, select, textarea {
font-weight: 300;
}

body,
body *,
button,
html,
input,
select,
textarea {
    font-size: 12px;
    letter-spacing: 1.3px;
}

body .icon-lg,
body .icon-sm {
    font-family: trellicons;
}

body div#board {
    display: flex;
    -webkit-box-pack: justify;
    -ms-flex-pack: justify;
    justify-content: space-between;
    padding: 12px;
    margin: 0 1px;
}

body a:hover,
body .dark-hover:hover .icon-sm {
	color: #558efc;
}

.icon-lg.light, .icon-lg.light-hover:hover,
.icon-sm.light, .icon-sm.light-hover:hover,
.light-hover:hover .icon-lg, .light-hover:hover .icon-sm {
	color: #3a3f4b;
}

.header-btn {
	color: #6c727e;
}

.board-header-btn {
	height: 17px;
}

div#board .js-list.list-wrapper {	
	-ms-flex-preferred-size: 280px;
	flex-basis: 300px;
	-ms-flex-negative: 0;
	flex-shrink: 0;
	margin: 0 16px 0 0;
	width: 300px;
	padding: 0px;
	border: 2px solid #000000;
	border-left-width: 2px;
	border-right-width: 2px;
	/* box-shadow: 0 -1px 0 0px hsla(219,13%,17%,1), inset 0 1px 0 0px hsla(219,13%,12%,1); */
	border-radius: 9px;
	background: hsla(219,13%,12%,1);
}

div#board .js-list.list-wrapper .list {
    border-radius: 7px ;
    padding: 0;
    box-shadow: 0 0 0 1px #000;
    width: auto ;
    margin: 0 ;
    background: transparent;
}

div#board .js-list.list-wrapper .list .list-cards {
    margin: 0;
    overflow-y: hidden;
    padding: 0;
}

div#board .js-list.list-wrapper .list .list-cards:hover {
    overflow-y: auto;
}

.list-card {
    border-radius: 0;
    background: hsla(222,13%,19%,1) ;
    border-bottom: .5px solid #2c303a;
    border-top: .5px solid #3e4551;
    -webkit-transition-duration: .25s;
    transition-duration: .25s;
    -webkit-transition-property: background, -webkit-box-shadow;
}

body .list-card:hover {
    border-bottom: .5px solid #2c303a;
    color: #fff;
}

.list-card.active-card {
	background: hsla(220,50%,29%,1);
	box-shadow: 0 0 0 1px #558efc,inset 0 0 0 4px hsla(0,0%,0%,0) !important;
	color: #fff;
}

.list-card.active-card a.list-card-title.js-card-name {
	background: hsla(220,50%,29%,1);
}

.list-card.active-card a.list-card-title.js-card-name {
/*	background: hsla(220,50%,29%,1); */
}

.list-card.placeholder {
	background:hsla(220,50%,29%,1)!important;
	border:none;
	box-shadow:inset 0 0px 40px rgba(0,0,0,
.4);
}

.list-card-operation,
body .list-card-operation:hover {
	background-color: transparent;
}

body .list-card-details {
	padding: 0;
	box-shadow: inset 3px 0 0 0px hsla(155,100%,23%,0);
}

body .list-card:hover .list-card-details {
	box-shadow: inset 3px 0 0 0px hsla(155,100%,23%,1);
}

body .list-card.active-card .list-card-details {
	/* box-shadow: inset 3px 0 0 0px red;*/
}

body a.list-card-title {
	background: #31363f;
    color: #9da5b4;
    font-weight: 300;
    border-bottom: 0 solid #000;
    padding: 5px 9px 6px;
}

.list-title .icon-menu {
    right: 0;
    top: 8px;
}

textarea.list-header-name.mod-list-name.js-list-name-input {
    color: #3a3f4b;
    font-weight: 400;
    text-shadow: 1px 1px 0 #000;
}

.list-gradient-bottom,
.list-gradient-top {
    display: none;
}

.list-area-wrapper {
    padding-left: 10px;
}

.list-area {
    width: 3500px;
}

body .board-wrapper .list-header {
    z-index: 10;
    margin: 0px 0px 2px ;
    background: hsla(220, 16%, 12%, 1);
    padding: 5px;
    border-radius: 7px 7px 0 0;
    box-shadow: 0 1px 0px 1px #000000;
}

body textarea.mod-list-name {
    padding: 5px 5px 0;
    height: 24px;
    min-height: 25px;
    margin: 0;
}

body .list-header-extras {
    top: 0;
    right: 0;
}

.list-header-extras-menu:hover {
	background: transparent;
}

.list-header h2 {
    font-size: 15px;
    font-weight: 400;
    text-transform: uppercase;
}

.list-header-menu-icon {
    top: 5px;
    right: 0;
}

.list.add-list .placeholder {
    display: none;
    padding: 7px;
    color: #e84c3d;
}

.board-header-btn {
    color: #fff;
    font-weight: 400;
}

.board-canvas {
    background: #353f4b;
    background-size: cover;
}

.list-card.is-covered .list-card-cover {
	mix-blend-mode: multiply;
}

#board {
    padding: 8px 0 0;
}

body #header {
    background: #5f697c;
    padding: 1px;
}

.body-board-view #header,body .board-canvas {
    background: 0 0;
}

div#header>a,div#header>div {
    top: 0;
}

body input[type=text].header-search-input,div#header>div a.header-btn {
    background-color: transparent;
    background: 0 0;
}

div#header>div a.header-btn {
    font-weight: 400;
    margin: 1px 1px 1px 0;
    min-width: 25px;
    line-height: 24px;
}

body input[type=text].header-search-input {
    padding: 2px 6px;
}

body .icon-lg {
    line-height: 24px;
}

body #header,body .header-btn-icon,div#header>div a.header-btn,input.header-search-input.js-search-input.js-disable-on-dialog {
    height: 24px;
    min-height: 24px;
}

body .header-btn.header-member .member {
    border-radius: 0;
    height: 24px;
	padding:0;
}

body .header-btn.header-member .member-avatar, body .icon-lg {
    height: 24px;
}

body .board-header {
    background: #282c34;
}

body .board-header-btn {
    padding: 0 9px 0 8px;
}

body .board-header-btn-name {
    line-height: 14px;
}

h2 {
    font-size: 18px;
    line-height: 1.2em;
    margin: 0 0 9px;
    font-weight: 400;
}

.member,
.member img {
    border-radius: 20px;
    border: none;
}

.member img {
    border-radius: 0;
	height: 100%;
}



.fancy-scrollbar::-webkit-scrollbar-track-piece {
    background: 0 0;
}

.fancy-scrollbar::-webkit-scrollbar-thumb:horizontal,
.fancy-scrollbar::-webkit-scrollbar-thumb:vertical {
    background: #555;
}

.open-card-composer:hover {
    background: #ecf0f3;
    color: #666;
    text-decoration: none;
    margin: 0;
}

.board-widgets {
    background: rgba(255, 255, 255, .98);
}

.sidebar-hide-btn {
    top: 5px;
}

.atMention,code {
    font-size: 12px;
}

.atMention {
    font-weight: 100;
    color: #9da5b4;
}

code {
    font-family: Menlo, Monaco, "Courier New", monospace;
    background-color: #f7f7f9;
    border: 1px solid #e1e1e8;
    border-radius: 3px;
    padding: 3px 4px;
    color: #d14;
}

body {
    background-color: #21252b;
}

.member.js-member-on-card-menu {
    height: 20px;
    width: 20px;
    margin: 0 0 0 1px;
    border-radius: 0;
}

span.member-initials {
    width: 20px;
    height: 20px;
}

.list-card-members.js-list-card-members {
}

.icon-lg,
.icon-sm,span.badge-text {
    color: #9da5b4;
}

.list-card-details .badges {
	padding-left: 6px;
}

.badge span.badge-text {
    font-size: 10px;
    color: #9da5b4;
    padding: 0;
}

body .badge {
    margin: 0;
    padding: 4px 5px 0 0;
}

span.badge-icon {
    font-size: 9px;
}

.window,
.window .comment-frame {
    background-color: #21252b;
}

.window .checklist-new-item-text,
.window .checklist-new-item-text:hover,
.window .checklist-progress-percentage,
.window .icon-lg,
.window .icon-sm,
.window .quiet,
.window .quiet a,
.window a,
.window button,
.window input,
.window input[type=submit]:disabled,
.window select,
.window textarea,body .window,html .window {
    color: rgba(157, 165, 180, .6);
}

.window a.button-link,
.window input.js-add-comment,
.button.primary,
button.primary,
input[type=button].primary,
input[type=submit].primary {
    background: linear-gradient(#3a3f4b, #353b45);
    border: 1px solid #000;
    border-bottom-color: #000;
    -webkit-transition-duration: .25s;
    transition-duration: .25s;
    -webkit-transition-property: background-color, -webkit-box-shadow;
}

.window a.button-link:hover,
.window input.js-add-comment:hover,
.button.primary:hover,
button.primary:hover,
input[type=button].primary:hover,
input[type=submit].primary:hover {
    background: #558efc;
}

.window .card-detail-data.u-gutter {
    margin: 0 -9px 13px;
    padding: 8px 10px 2px;
}

.window textarea.comment-box-input.js-new-comment-input {
    background: rgba(0, 0, 0, .5);
}

.card-detail-data.u-gutter {
    margin: 0 0 26px;
}

.window-title {
    -webkit-box-flex: 1;
    -ms-flex-positive: 1;
    flex-grow: 1;
    padding-top: 2px;
}

.window-title,body textarea.mod-card-back-title.js-card-detail-title-input {
    display: block;
    height: 16px;
}

textarea.mod-card-back-title.is-editing,
textarea.mod-card-back-title:focus {
	background:hsla(216,13%,12%,1);
}

.window .window-header {
    margin: 0 3px;
    padding: 2px 10px;
}

span.window-header-icon.icon-lg.icon-card {
    padding: 0 8px;
    height: 16px;
}

.window-header-inline-content.quiet.js-current-list {
    -ms-flex-preferred-size: 100%;
    flex-basis: 100%;
    margin: 0 0 0 29px;
}

.card-detail-item.card-detail-item-block.u-clearfix.editable {
    padding: 0 6px;
    margin-bottom: 0;
}

.window-module-title.window-module-title-no-divider {
    padding: 0 0 0 38px;
    display: flex;
    -webkit-box-orient: vertical;
    -webkit-box-direction: normal;
    -ms-flex-direction: column;
    flex-direction: column;
    -webkit-box-align: start;
    -ms-flex-align: start;
    align-items: flex-start;
    margin: 0;
}

span.window-module-title-icon.icon-lg.icon-comment {
    position: static;
    padding: 0;
}

.attachment-thumbnail:hover .attachment-thumbnail-details,
.quiet-button:hover {
	background-color: hsla(222,13%,15%,1);
}

.member.member-no-menu,
.new-comment.js-new-comment {
    margin: 0;
}

body a.button-link {
    font-weight: 300;
    color: #9da5b4;
    vertical-align: baseline;
}

span.icon-sm.icon-member {
    padding: 0;
}

.button-link {
    font-weight: 300;
    color: #d7dae0;
}

.icon-card:before,
.icon-sm {
    font-size: 10px;
}

body textarea.mod-card-back-title {
    font-weight: 300;
    font-size: 12px;
}

body .header-btn.header-member .member,
body .icon-lg {
    width: auto;
}

body .icon-lg {
    padding: 0 8px 0 0;
}

.window .window-header-icon {
    position: static;
    line-height: 10px;
}

.card-detail-window,
.window .window-title {
    padding: 0;
}

.window .window-main-col {
    border: 1px solid #000;
    margin-top: -1px;
    padding: 0 10px;
    background: #282c34;
}

body .card-detail-item.card-detail-item-block.u-clearfix.editable {
    padding: 5px 0;
    margin: 11px 9px;
    background: #21252b;
    border: 1px solid #000;
    border-radius: 6px;
}

.checklist-item-checkbox.enabled.js-toggle-checklist-item {
    border-width: 0;
    box-shadow: 0 0 0 .6px #000;
    background: 0 0;
}

.checklist-item p.checklist-item-details-text {
    padding: 6px 9px 2px;
}

.checklist-item:hover p.checklist-item-details-text,
.checklist-item:hover span.icon-sm.icon-check.checklist-item-checkbox-check {
    color: #fff;
}

.checklist-item:hover .checklist-item-checkbox.enabled.js-toggle-checklist-item {
    box-shadow: 0 0 0 .6px #fff;
}

a.open-card-composer.js-open-card-composer {
    font-size: 0;
    background: #282c34;
	background: hsla(220,13%,15%,1);
    border: 0 ;
    border-radius: 0 0 7px 7px ;
    z-index: 9 ;
    box-shadow: inset 0 1px 0 0 #000, 0 -10px 20px 7px rgba(0,0,0,0.4) ;
    padding: 8px 7px 0px ;
    margin: 0 ;
    text-align: center;
    color: transparent;
}

a.open-card-composer.js-open-card-composer:before {
    content: "+";
    color: #6c727e;
    font-size: 1.4rem ;
    line-height: 1rem;
}

a.open-card-composer.js-open-card-composer:hover {
	background: hsla(220,35%,16%,1);
	background: hsla(220,50%,29%,1);
	box-shadow: 0 0 0 1px #558efc,inset 0 0 0 4px hsla(0,0%,0%,0);
	color: #fff;
}

a.open-card-composer.js-open-card-composer:hover:before {
	color: #fff;
}

.badge.is-complete .badge-text,
body .list-card:hover .badge {
    color: #fff;
}

a.list-card-title.js-card-name {
    border-color: #ffffff;
	box-shadow: inset 3px 0 0 0px hsl(219, 13%, 22%);
}

.list-card:hover a.list-card-title.js-card-name {
    color: #9da5b4;
	box-shadow: inset 3px 0 0 0px hsl(155, 100%, 50%);
}

body .board-header * {
    line-height: 13px;
    font-size: 11px;
    color: #9da5b4;
}

.board-header.u-clearfix.js-board-header,
.board-header-btn-icon,
span.board-header-btn-text,
a.board-header-btn.board-header-btn-name.js-rename-board {
    padding: 0;
}

.board-header-btn-icon {
    top: 3px;
    left: 1px;
    position: static;
}

body .board-header.u-clearfix.js-board-header {
	padding: 4px 5px 0px;
}

.action-comment {
    background-color: #21252b;
}

.phenom.mod-comment-type {
    border-bottom: 1px solid #000;
}

.new-comment .member {
    position: absolute;
    top: -41px;
    left: 0px;
}

span.icon-sm.icon-edit.list-card-operation.dark-hover.js-open-quick-card-editor.js-card-menu {
    background-color: none;
}
}
