css:
@-moz-document domain("en.virtualpopstar.com") {
.achievement-container.leftmenu {
    margin-left: 18px !important;
    width: 175px !important;
}

.block_content_bottom {
    height: 11px;
    background-image: none;
    background-color: /*[[COLOR_DEF]]*/;
    margin-bottom: 4px;
}

.block_content_title {
    line-height: 28px;
}

.block_content_top {
    height: 28px;
    background-image: none;
    background-color: /*[[COLOR_DEF]]*/;
    margin-top: 3px;
}

.block_menu_multi {
    background-color: /*[[COLOR_DEF]]*/;
    background-image: url(http://i.imgur.com/0ZGQBbS.png);
    background-repeat: no-repeat;
    background-position: top right;
    border: none;
    color: #FFF;
    height: 28px;
    line-height: 29px;
    margin-bottom: 6px;
    margin-left: 0;
    padding-left: 22px;
    width: 183px;
}

.block_menu_multi:hover {
    background-color: /*[[COLOR_HOV]]*/;
}

.block_menu_multi_active {
    background-color: /*[[COLOR_ACT]]*/;
    background-image: url(http://i.imgur.com/y81EWTD.png);
}

.block_menu_single {
    background-color: /*[[COLOR_DEF]]*/;
    background-image: none;
    background-repeat: no-repeat;
    background-position: top right;
    border: none;
    color: #FFF;
    height: 28px;
    line-height: 29px;
    margin-bottom: 6px;
    margin-left: 0;
    padding-left: 22px;
    width: 183px;
}

.block_menu_single.active {
    background-color: /*[[COLOR_ACT]]*/;
    background-image: none;
}

.block_menu_single:hover {
    background-color: /*[[COLOR_HOV]]*/;
    background-image: none;
}

.block_mid.search {
    text-align: center;
    background: #fff;
    margin-bottom: 21px;
    margin-left: 0;
    width: 205px;
    border-bottom: solid 11px /*[[COLOR_DEF]]*/;
    border-top: solid 29px /*[[COLOR_DEF]]*/;
    padding-bottom: 5px;
}

.block_submenu {
    margin-left: 10px;
    padding-left: 0;
}

.content_container {
    margin-top: 11px;
}

.content_left_area {
    width: 205px;
    margin-right: 6px;
}

input[type="checkbox"] {
    vertical-align: sub;
}

input[type="submit"].dark {
    background-color: /*[[COLOR_DEF]]*/;
    background-image: none;
    height: 20px;
    line-height: 15px;
    margin-top: 12px;
    width: 95px;
}

.block_content_top_left, .block_content_top_center_small, .block_content_top_right, .block_content_top_center_med, .block_top.avatar, .block_content_top_center_mini {
    background-image: none;
    height: 28px;
}

.block_content_bottom_med, .block_content_bottom_small, .block_bottom.avatar, .block_content_bottom_mini {
    background-image: none;
    height: 12px;
    background: /*[[COLOR_DEF]]*/;
}

.block_content_top_container, .block_top.avatar {
    height: 28px;
    background: /*[[COLOR_DEF]]*/;
	    margin-top: 3px;
}

.block_content_title_mini, .block_content_title_small, .block_title {
    line-height: 25px;
}

.header_bg {
    height: 132px;
    border-bottom: 12px solid /*[[COLOR_DEF]]*/;
}

.okButton, .button_ok, input[type="submit"], .okButton.long.dark {
    margin: 4px 0px 0px 2px;
    height: 24px;
    width: 139px;
    line-height: 24px;
    background-image: none;
    background-color: /*[[COLOR_DEF]]*/;
}

.okButton.active {
    margin: 4px 0px 0px 2px;
    height: 24px;
    width: 139px;
    line-height: 24px;
    background-image: none;
    background-color: /*[[COLOR_ACT]]*/;
}

.okButton:hover, input[type="submit"]:hover {
    background-color: /*[[COLOR_HOV]]*/;
}

.okButton:hover, .button_ok:hover, input[type="submit"]:hover, .okButton.long.dark:hover, .okButton.active:hover {
    background-color: /*[[COLOR_HOV]]*/;
}

ul.darkbox_menu li {
    height: 21px;
    line-height: 21px;
    background-image: none;
    background-color: #808080;
}

.darkbox_button_active {
    background-image: none !important;
}
}
