css:
@-moz-document domain("myoniyonitranslations.com") {
body.custom-background {
    background-color: #090f0f;
}

.site-header-main {
    -webkit-align-items: center;
    -ms-flex-align: center;
    align-items: center;
    display: -webkit-flex;
    display: -ms-flexbox;
    display: flex;
    -webkit-flex-wrap: wrap;
    -ms-flex-wrap: wrap;
    flex-wrap: wrap;
    background-color: #090f0f;
}

.entry-content,
.entry-summary {
    background-color: #090f0f;
    color: #008B8B;
}

.entry-title {
    font-family: Montserrat, "Helvetica Neue", sans-serif;
    font-size: 28px;
    font-size: 1.75rem;
    font-weight: 700;
    line-height: 1.25;
    margin-bottom: 1em;
    background-color: #090f0f;
    color: #008B8B;
}

.site-header {
    padding: 2.625em 7.6923%;
    background-color: #090f0f;
}

.site-content {
    word-wrap: break-word;
    background-color: #090f0f;
}

.site {
    background: url("http://7-themes.com/data_images/out/72/7017895-dark-souls-wallpaper-hd.jpg") fixed no-repeat center
}

p {
    margin: 0 0 1.75em;
    color: #05838b;
}

h1,
h2,
h3,
h4,
h5,
h6 {
    clear: both;
    font-weight: 700;
    margin: 0;
    text-rendering: optimizeLegibility;
    color: #085b77;
}

a {
    color: #085b77 !important;
}

@media screen and (min-width: 44.375em) {
	body:not(.custom-background-image):before,
	body:not(.custom-background-image):after {
		/* background: inherit; */
		/* content: ""; */
		/* display: block; */
		/* height: 21px; */
		/* left: 0; */
		/* position: fixed; */
		/* width: 100%; */
		/* z-index: 99; */
        display:none;
	}
}
}
