@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("deviantart.com") {

div.gm-controls span.gmb-edit a {
background-position:0 0!important;
background:url(data:image/gif;base64,) no-repeat!important;
opacity:.08!important;
}

div.gm-controls span.gmb-submit a {
background:url(data:image/gif;base64,)no-repeat!important;
opacity:.08!important;
}

div.gm-controls span.gmb-edit a:hover {
background:url(data:image/gif;base64,)!important;
opacity:1!important;
}

div.gm-controls span.gmb-submit a:hover {
background:url(data:image/gif;base64,)!important;
opacity:1!important;
}

div.gm-controls span.gmb-edit a:active {
background:url(data:image/gif;base64,)!important;
opacity:1!important;
}

div.gm-controls span.gmb-submit a:active {
background:url(data:image/gif;base64,)!important;
opacity:1!important;
}

div.gm-controls-config span a.current {
left:0px!important;
opacity:.5!important;
}

/* group edit buttons */ 

[class="gm-chaos gm-chaos-edit gmbuttons"], [class="gm-chaos gm-chaos-new gmbuttons"] {
opacity:.08!important;
}

[class="gm-chaos gm-chaos-edit gmbuttons"]:hover, [class="gm-chaos gm-chaos-new gmbuttons"]:hover {
opacity:1!important;
}

#gmi-GroupEdit_AppMenuItem {
opacity:.08!important;
}

#gmi-GroupEdit_AppMenuItem:hover {
opacity:1!important;
}
}
