css:
@-moz-document domain(digitalgamingleague.co.za) {
.match-summary {
    border: 0px solid #1f1d3c;
}
.match-summary a {
    color: #73a9c6;
}
.match-summary .text-container>span {
    background: #85C3E2;
    color: #1F1D3C;
    font-weight: bold;
    padding: 0 3px 3px;
    margin-top: 0;
    border: none;
    border-left: none;
    margin-left: 0;
    /* color: #FFF;*/
    
    white-space: nowrap;
    text-overflow: ellipsis;
}
.content-wrapper {
    background: #2C393F;
}
.content-wrapper .box,
.content-wrapper .widget-wrapper {
    background-color: #2C393F;
    border: 0px solid #c8c8c8;
    box-shadow: 0 0px 0px 0 #c8c8c8;
    color: #FFFFFF
}
input,
input.form-control {
    background-color: #BBB;
    color: #FFF;
}
form-control {
    border-radius: 0px;
}
.versus-banner .competitor-details .competitors-text {
    font-family: "telkom_123light", Arial, Sans-Serif;
    /*Impact, Charcoal, sans-serif;*/
    
    color: #D48D06;
    font-size: 32px;
    font-weight: 700;
    text-shadow: 2px 2px #4E646F;
    text-align: center;
    flex-grow: 1;
}
.jquery-comments .textarea-wrapper .textarea {
    margin: 0;
    outline: 0;
    overflow-y: auto;
    cursor: text;
    border: 1px solid #CCC;
    background: #2C393F;
    color: #eee;
    font-size: 1em;
    line-height: 1.45em;
    padding: .25em .8em;
    padding-right: 2em;
}
.jquery-comments .textarea-wrapper:after {
    border: 7px solid #2C393F;
}
body {
    color: #a7c4ef;
}
a {
    color: #73a9c6;
}
a.visited {
    color: #73a9c6;
}
.versus-banner .versus-logo {
    right: 10px;
    border-style: solid;
    border-width: 1px;
    border-color: #DDD;
    box-shadow: 2px 2px #4E646F;
}
.table-striped>tbody>tr:nth-of-type(odd) {
    background-color: #434b4f;
}
.table>tbody>tr>td,
.table>tbody>tr>th,
.table>tfoot>tr>td,
.table>tfoot>tr>th,
.table>thead>tr>td,
.table>thead>tr>th {
    border-top: 0px solid #ddd;
}
}
