css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mamba.ru") {
  /*шапка*/
  .ui-menu-cont {
    background: linear-gradient(to top, #d16408, #f28d38) !important;
  }
  .ui-menu li {
    background: linear-gradient(to top, #d16408, #f28d38) !important;
    border-left: 1px solid #DD7E39;
  }
  .ui-menu li a:hover {
    background: linear-gradient(to top, #db7723, #f49b50) !important;
    border-left: 1px solid #DD7E39 !important;
  }
  .ui-menu li.selected a {
    background: linear-gradient(to top, #ba5908, #e17e2a) !important;
    border-left: 1px solid #DD7E39;
  }
  .ui-menu li a {
    border: none;
  }
  #logo a {
    display: none !important;
  }
  
  /*Фотолинейка сверху*/
  .b-photoline {
    display: none !important;
  }
  
  /*Левый блок (лидер, реклама...)*/
  .MainBlockLeft {
    display: none !important;
  }
  
  /*Панель возле своего фото профиля*/
  .add-bonus  {
    display: none !important;
  }
  .btn-group {
    padding: 1px !important;
  }
  .btn-group-item {
    border-right: none !important;
  }
  .btn-group-item .inset {
    border: none !important;
  }
  .vip_icon {
    display: none !important;
  }
  .btn-group-item .ui-user-block .ui-user-icon {
    margin: 2px 0px 0 0 !important;
  }
  
  /*Снежок*/
  .web-promo-new_year-snowballs {
    display: none !important;
  }
  /*Подарить ёлочку*/
  .web-promo-new_year-anketa {
    display: none !important;
  }
  
  
  /*Блок с фотографиями найденных анкет*/
  .MainBlockRight {
    width: 100% !important;
  }
  .tiles-list-wrapper {
    min-width: 100% !important;
  }
  
  
  
  
   .web-photorating-voting, .web-question-question_main, .b-question_main_carousel, .question_main__block, .b-question_main {
    width: 100% !important;
    max-width: 100% !important;
  }
  
  .question_module__item {
    width: 32% !important;
  }
}  
  
  
  
  
@-moz-document url-prefix("https://www.mamba.ru/search.phtml"), url-prefix("https://www.mamba.ru/"), url-prefix("http://www.mamba.ru/search.phtml"), url-prefix("http://www.mamba.ru/") {  
  
  .web-new_faces, .web-photorating-main_top {
    display: none !important;
  }
  
  /*Размер фотографии*/
  .web-search-search_results > .layout:not(.loading) > .tiles-list-wrapper > .tiles-list > .tiles-item {
    max-width: 187px !important;
  }
  
  .search-banner-wrapper {
    display: none !important;
  }
  .search-form-wrapper_dark-bg {
    background: #fff !important;
  }
  
  .search-form-wrapper {
    min-height: 100px !important;
    margin-bottom: 0px !important;
  }
  .search-filter {
    margin-top: 65px !important;
  }
  .search-form {
    max-width: 100% !important;
  }
  ._reset.web-search-search_form .search-form-params .search-form-params-content {
    left: 3% !important;
  }
 
  ._reset.web-search-search_form .search-form-params .search-form-params-content .search-form-param .button-content {
    padding-left: 25% !important;
  }
  
  .web-hitlist-hits_widget, .web-messenger-contacts_widget {
    max-width: 49% !important;
  }
  #Paginator {
    width: 100% !important;
  }
  .horizontal-row
  {
    display: none !important;
  }
  .photolayer .new-faces-wrapper .actions-wrapper .actions-block {
    margin-top: 50% !important;
  }
    
 
}

@-moz-document url-prefix("https://www.mamba.ru/my/messages.phtml"), url-prefix("http://www.mamba.ru/my/messages.phtml") {
  .ContactBanner, .c-photo-selection {
    display: none !important;
  }
  #ContactsCont li {
        float: left !important;
        margin-right: 1% !important;
        width: 49%;
    }
  .delete-support {
    background: #ffd7b1 !important;
  }
  .ActionsBaloon {
    left: 200px !important;
  }
}

@-moz-document url-prefix("https://www.mamba.ru/my/message.phtml"), url-prefix("http://www.mamba.ru/my/message.phtml") {
    /*Сообщения*/
  .messenger-window .my-msg {
    background-color: #f0ffdc !important;
    }
  .messenger-window .u-msg {
    background-color: #fff0ec  !important;
  }
  .messenger-window .mw-messages li.my-msg {
    width: 62% !important;
    border-radius: 10px !important;
    margin-bottom: 5px !important;
    margin-left: 30% !important;
  }
  .messenger-window .mw-messages li.u-msg {
    width: 62% !important;
    border-radius: 10px !important;
    margin-bottom: 5px !important;
    margin-left: 3% !important;
    
  }
  .messenger-window .mb-msg .del, .messenger-window .mb-msg del {
    margin-top: 8px;
    width: 100%;
    height: 8px;
    border-top: 1px solid #fff;
    
  }
  
}












@-moz-document url-prefix("https://www.mamba.ru/diary"), url-prefix("http://www.mamba.ru/diary") {

.contentList .block {
  position: static;
  padding: 15px 15px 15px 15px;
  border: 1px #e3dede solid;
  border-radius: 5px;
  background: #f8f8f1;
  margin-bottom: 10px;
}

.contentList .block .autor .author__photo, .contentList .block .autor img {
  position: inherit;
  top: 15px;
  left: 7px;
  width: 62px;
  height: 62px;
  border-radius: 5px;
}
}
