css:
@-moz-document domain("diepresse.com") {

.abohalf span, .articleauthor, .articlepublisher, .autor, .ed-name, .ed-services-features li, .stand, small, time {
    color: #aaaaaa !important;
}

#nav li a, #subnav li a, .content, .content p a, .message p, .mostcommented, .mr, .sendernamen, .submit b, dt a {
    color: #ffffff !important;
}

.progtag_aktiv {
    background-color: #85c9fa !important;
    border-radius: 5px !important;
    color: #ffffff !important;
}

.scorer {
    background-color: #222222 !important;
    border: solid 1px #aaaaaa !important;
}

.corr {
    background-color: #85c9fa !important;
}

.ed-main div table, .ed-zitat-text, .fullScore, a, b, dt, h1, h2, h3, h4, h5 {
    color: #85c9fa !important;
}

#network {
    background-color: #222222 !important;
    left: 50% !important;
    margin-left: -475px !important;
    width: 930px !important;
}

#fullsizeOverlay, .hoverbox, .incorr, .num, td, tr {
    background-color: #222222 !important;
}

#nav, .activechannel ul, .channel ul {
    border-color: #85c9fa !important;
}

#frame {
    border-color: #222222 !important;
}

.dia_nav_l a, .dia_nav_r a {
    background: #85c9fa !important;
    color: #ffffff  !important;
}

#headerbar, .btn, .commentSubmit, .gallery_ear, .logo h1, .submit {
    background-color: #85c9fa !important;
}

.blog-header h1 a {
    background: #333333 !important;
    border: solid 1px #aaaaaa !important;
    text-shadow: none !important;
}

#abend, #abo1, #abo2, #abo3, #nachmittag, #nacht, #tv1, #tv2, #tv3, #tv4, #tv5, #tv6, #tv7, #tv8, #vormittag, .s, fieldset {
    background: #333333 !important;
    border: solid 1px #aaaaaa !important;
}

#abo1 + div a, #abo2 + div a, #abo3 + div a, #tv1 + div a, #tv2 + div a, #tv3 + div a, #tv4 + div a, #tv5 + div a, #tv6 + div a, #tv7 + div a, #tv8 + div a, #TabsNavigation a {
    background: #444444 !important;
    border: none !important;
    margin: 1px !important;
}

.articlefeat h2 {
    background-color: transparent !important;
    border: none !important;
    color: #85c9fa !important;
}

.logo {
    background-color: transparent !important;
}

.headright {
    background-color: transparent !important;
}

body, li {
    background-color: #222222 !important;
    border-color: #222222 !important;
}

#nav a:hover {
    background-color: #333333 !important;
}

.channel h2, .ressortliste h2 {
    background-color: #333333 !important;
    border-color: transparent !important;
}

.channel h2 a, .ressortliste h2 a {
    background-color: transparent !important;
}

.articleBottomAd, .articleicons, .articleicons_v, .socialIcons, .socialicons_v, .tnemesitrevda, .unit-rating {
    display: none !important;
}

legend {
    background-color: transparent !important;
    color: #85c9fa  !important;
}

div {
    background-color: transparent !important;
    border-color: #333333 !important;
    color: #ffffff !important;
}

.articlefeat ul li {
    background-color: transparent !important;
    border: none !important;
}

.articlefeat {
    border: solid 1px #aaaaaa !important;
    padding: 10px !important;
}

#fullsizeContent, #wrapper {
    float: none !important;
    margin: 0px auto !important;
}

.sticky {
    background: #222222 !important;
    left: 50% !important;
    margin-left: -475px !important;
}

#lightbox {
    background-color: #333333 !important;
}

#subnav {
    border: none !important;
}

.comment, .commentanswer {
    background-color: #333333 !important;
    background-image: none !important;
    border: solid 1px #aaaaaa !important;
}

.weiter, .zurueck {
    background-color: #333333 !important;
    color: #85c9fa !important;
}

#closeButton {
    background: url(https://i.imgur.com/WxcSNXa.png) no-repeat !important;
    height: 0px !important;
    padding-left: 15px !important;
    padding-top: 15px !important;
    width: 0px !important;
}

.commentfrom strong img[alt=Antworten] {
    background: url(https://i.imgur.com/WbE5FnI.png) no-repeat !important;
    height: 0px !important;
    padding-left: 4px !important;
    padding-top: 7px !important;
    width: 0px !important;
}

.progtag_aktiv, .wetterProgtext {
    background-image: none !important;
}

#nav .activechannel > a, #subnav .active a, .tabs .active, .wetterTabs .active {
    background-color: #85c9fa !important;
    color: #333333 !important;
}

.vis .countBox {
color: #333333 !important;
}

.tabs a, .wetterTabs a {
    background-color: #333333 !important;
    color: #85c9fa !important;
}

input[type=password], input[type=text], textarea {
    background: #333333 !important;
    border-color: #aaaaaa !important;
    color: #ffffff !important;
}

}
