css:
@-moz-document domain(www.ogre3d.org) {
/* ----------------------------------------------------------- */




/* Want the name and avatar on the left? Remove this line

 .postprofile { float: left !important;}
 .postbody { float: right !important; }

    AND REMOVE THIS LINE TOO! */



/* ----------------------------------------------------------- */


#page-body {
 width: 75% !important;
 margin: 0 auto 0 auto !important;
}
div.online {
 background-image: none !important;
}
div.post {
 border: 0 !important;
 border-bottom: 2px dotted #ccc !important;
 padding: 0 !important;
 margin: 0 !important;
}
div.post div.inner {
 border: 0 !important;
}
.postbody {
 margin: 0 !important;
 padding: 1em 0 1em 0 !important;
 width: 90% !important;
 border: 0 !important;
}
.postbody .content {
 padding: 1em 0 1em 0 !important;
}
.postbody .signature {
 opacity: .25 !important;
}
.postprofile {
 left: 0 !important;
 width: 10% !important;
 border: 0 !important;
 text-align: center !important;
 overflow: hidden !important;
 font-size: smaller !important;
 padding: 0 !important;
 margin: 0 !important;
}
.postbody > h3 {
 font-size: 1em !important;
 display: inline !important;
}
.postbody > h3 a {
 font-size: 100% !important;
 color: #333 !important;
 letter-spacing: inherit !important;
}
.postbody > p.author {
 display: inline !important;
}
.postbody > p.author a img {
 display: none !important;
}
.postprofile dt {
 margin: 0 !important;
 padding: 0 !important;
}
.postprofile dd {
 display: none !important;
 font-size: smaller !important;
 text-align: left !important;
}
.postprofile:hover dd {
 display: block !important;
}
.postprofile dd img {
 display: none !important;
}
.postprofile dt a img {
 width: 48px !important;
 height: 48px !important;
 padding: 2px !important;
 border: 1px solid transparent !important;
}
.postprofile dd ul.profile-icons {
 display: none !important;
}
div.online div.inner dl.postprofile dt a img {
 border-color: #aaaaFF !important;
}

/* ----------------------------------------------------------- */
}
