css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(brchan.org) {
body {
    background: #EEEEEE;
    color: #333333;
    font-family: Verdana,sans-serif;
    font-size: 0.9em;
}
div.post.reply {
    background: #dddddd;
    border: solid 1px #cccccc
}
div.post.reply div.body a {
    color: #FF6600
}
div.post.reply div.body a:hover {
    color: #FF6600
}
.intro span.name {
    color: #333333;
    font-weight: 400;
}
div.post p {
    display: block;
    margin: 0;
    margin-top: 10px;
    line-height: 1.0em;
    font-size: 11pt;
    min-height: 1.16em;
}
a:hover, .intro a.post_no {
    color: #789922;
}
a:hover, .intro a.post_no:hover {
    color: #0066FF;
}
.unimportant * {
    font-size: 11px;
}
a, a:visited {
    text-decoration: underline;
    color: #FF6600;
}
div.post p.fileinfo {
    padding-left: 5px;
    font-size: 11px;
}
#thread-quick-reply {
    display: none;
    float: right;
    margin-left: 10px;
    font-size: 1.5em;
}
div.post.reply.highlighted {
    background: #ffd6b8;
    border-left: 1px solid #cccccc;
    border-right: 1px solid #cccccc;
    border-top: 1px solid #cccccc;
    border-bottom: 1px solid #cccccc;
    -moz-border-radius: 5px;
    border-radius: 5px;
}
.own_post {
    font-style: italic;
    font-weight: normal;
    color: white;
    opacity: .666;
    background: #fb2b49;
    padding-top: 3px;
    padding-bottom: 4px;
    padding-left: 20px;
    padding-right: 20px;
}
.desktop-style div.boardlist:not(.bottom) {
    background-color: #ffffff;
}
form table tr th {
    background: #eeeeee;
    border: 0px solid #054500;
    font-weight: 200;
}
div.banner {
    background-color: #eeeeee;
    font-size: 14pt;
    font-weight: normal;
    color: #FF6600;
    text-align: center;
    margin: 0.5em 0;
}
.post-table th, .post-table-options th {
    width: 120px;
}
div.banner, div.banner a {
    color: #FF6600;
}
#thread-links>a {
    padding-right: 5px;
}
#options_tablist {
    background: #ADADAD;
    border-right: 1px dotted #033;
}
.options_tab h2 {
    color: #000;
    background: #ADADAD;
}
.options_tab {
    background-color: #DDDDDD;
}
#options_div {
    border: 1px solid #CCCCCC;
}
.options_tab_icon.active {
    color: #757575;
}
#options_div, #alert_div {
    background-color: #DDDDDD;
}
header div.subtitle, h1 {
    color: #FF7000;
}
div.boardlist {
    color: #DDDDDD;
}
div.boardlist a {
    color: #FF7000;
}
div.pages {
    color: #FF7000;
    background-color: #EEEEEE;
    border-right: 1px solid rgba(255, 255, 255, 0);
    border-bottom: 1px solid rgb(238, 238, 238);
}
div.pages a, a:visited {
    color: #333333;
}
}
