@-moz-document url-prefix("http://forumwar.herokuapp.com/room/") {
body {
    background-color:#545454;
    font: 15px Arial LT std;
}
::selection {
    background: #FFFFFF; /* Safari */
    color: #000000
}
::-moz-selection {
 background: #FFFFFF; /* Firefox */
 color: #000000;
}
p {
    color:#000000;
}
p:hover {
    color:#000000;
}

a#connection {
    color:#FFFFFF
}

a { /*ici*/
    color:#ffffff;
}

#chat {
    background-color:#383838
}

#msg_list>li.m10 { /* Normal*/
    color:#B8B8B8;
    font-family: 'Arial';
    font-size: 15px;
}

#msg_list>li.mi { /* Jaune*/
    color: #ffff7c;
    font-family: "Arial";
    font-size: 15px;
    font-style: italic;
}

#msg_list>li.ma { /* Bleu*/
    color: #519cff;
    font-family: "Arial";
    font-size: 15px;
}

#msg_list>li.mg { /*Vert*/
    color: #30ff98;
    font-family: "Arial";
    font-size: 15px;
}


#msg_list>li.m13 { /*Spectateur*/
    color: #c68765;
    font-family: "Arial";
    font-size: 15px;
}

#msg_list>li.m12{ /* Mort*/
	color: #000000;

}

#right {
    background-color: #454545;
}

#player_list {
    background-color:transparent;
    width:210px;
}

li {
    color:#C4C4C4;
    font-family: "Arial";
    font-size: 15px;
}

#dead_players {
    text-decoration: line-through;
    color:#ffff00;
    font-family: "Arial";
    font-size: 15px;
    font-style: italic;

}

h3 {
    color:#FFFFFF;
    font-family: "Arial";
    font-size: 15px;
    text-decoration: underline; 
}

#left {
    background-color: #454545;
}

#compo {
    background-color: #454545;
}

#chat_text_input {
    background-color:#383838;
    color:#B8B8B8;
    border-color:#000000;
    outline:none;
    width:418.35px;
}

#chat_input {
    background-color:#454545;

}

#content {
    background-color:#454545;
}

#chat_text {
    border-color:#000000;
    height:95.3%;
}

button#chat_button {
    background-color:#454545;
    color:#ffffff;
    border-color:#000000;
}
button#quit {
    background-color:#454545;
    color:#ffffff;
    border-color:#000000;
}
button#start_button {
    background-color:#454545;
    color:#ffffff;
    border-color:#000000;
    margin-left:5px;
}
button#join_button {
    background-color:#454545;
    color:#ffffff;
    border-color:#000000;
}
#msg_list>li.m12 { /* mort */ margin-left: 5px !important; } #msg_list>li.m12::before { /* mort */ content: "| "; }
#msg_list>li.m13 { /* spectateur */ margin-left: 5px !important; } #msg_list>li.m13::before { /* spectateur */ content: "| "; }
#time>h3 { text-decoration: none; }
#edit_compo {
    background-color:#383838;
    width: 473px;
    position: absolute;
    border-style: solid;
    border-color: black;
    border-width: 2px;
}
.simple_block:hover {
    border-style: solid;
    border-color: black;
    border-width: 2px;
}
.simple_block_selected {
    border-style: dotted;
    border-width: 1px;
    border-color: #ffffff;
}
button#close {
    float: right;
}
button#confirmation_compo {
    float: right;
}
