css:
@-moz-document domain("stackoverflow.com") {
/* Header */
#announcement-banner,
[role="menubar"] #search,
[role="menubar"] ol,
/* Left Sidebar */
#left-sidebar,
/* Question Header */
#question-header a[href="/questions/ask"],
/* Right Sidebar */
#sidebar .s-sidebarwidget,
#sidebar #hot-network-questions,
#sidebar #feed-link,
#sidebar #hireme,
/* Main */
#mainbar .post-menu,
#mainbar .js-add-link,
#mainbar .question-status,
#mainbar [title="Timeline"],
#mainbar #post-form,
#mainbar .bottom-notice,
/* Footer */
#footer,
.js-dismissable-hero
{ display: none !important; }

/* Layout */
#mainbar {
  float: none;
  width: auto;
}
#sidebar {
  float: none;
  width: auto;
}

/* Styling */
body {
  padding-top: 0;
}
.top-bar._fixed {
  position: static;
}
#content {
  border: 0;
}
#question-header {
  margin: 1em 0;
}
#answers-header h2 {
  color: var(--black-350);
}
#mainbar .js-voting-container [class*="vote"] svg path {
  fill: var(--black-075);
}
#sidebar {
  margin: 5em 0;
}
body>.container {
  max-width: 1000px;
}
pre, code {
  background-color: #f3f4f4;
}
}
