css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://baidu.com"), url-prefix("http://www.baidu.com"), url-prefix("https://www.baidu.com"), url-prefix("https://baidu.com"){

  ::-moz-selection {
  background:rgba(255,255,255,0.2);
  color:white;
  }
  /*fxxk AD*/
  html body.has-background.s-manhattan-index div#wrapper.wrapper_s div#wrapper_wrapper div#container.container_s div#content_left div#1.result.c-container{
    display:  none !important;
    visibility: hidden !important;
  }
  html body.has-background.s-manhattan-index div#wrapper div.s-skin-container.s-isindex-wrap{
    /*background-image: url(https://unsplash.it/1920/1080/?random)!important;*/
  }


/*https://ss0.bdstatic.com/5aV1bjqh_Q23odCf/static/superman/img/logo/logo_white.png*/
/*270*129*/
  /*logo样式*/
  #lg{
    opacity:0.5 !important;
  }

  #result_logo img{
    display:none !important;
  }#result_logo{
    background: none;
    background:url("https://ss0.bdstatic.com/5aV1bjqh_Q23odCf/static/superman/img/logo/logo_white.png") left no-repeat !important;
    width:117px;
    height:38px;
    background-size:117px 54px !important;
    opacity:0.5 !important;
  }

     /*搜索框样式*/
  #kw,#soutu-url-kw
  {
   background: none !important; 
   background-color: rgba(255,255,255,0.05) !important;
    color: #ffc70e !important;
    transition: background-color 0.25s !important;
    box-shadow: 0px 0px 2px rgba(255,255,255,0.4) !important;
  }
  #kw:hover,#soutu-url-kw:hover{
    background-color: rgba(255,255,255,0.0) !important;
  }
  .soutu-btn{
    opacity:0.3 !important;
  }
  #su,#s_btn_wr{
    background: none ;
    box-shadow: 0px 0px 2px rgba(255,255,255,0.2);
    color : rgba(255,255,255,0.4) !important;
    border-bottom-width :0px !important;
  }

  #su{
    transition: background-color 0.5s ;

  }
  #su:hover{
    background-color: rgba(255,255,255,0.1) ;
  }
  #form.fm span.bg.s_ipt_wr.quickdelete-wrap{
    border-right-width: 1px;
    border-right-color: white;
  }

  
    /*搜索下拉栏样式*/
  html body.has-background.s-manhattan-index div#wrapper div#head.s-skin-hasbg.white-logo.s-opacity-100 div#head_wrapper.s-isindex-wrap.head_wrapper.s-title-img div#s_fm.s_form div#s_form_wrapper.s_form_wrapper.soutu-env-nomac.soutu-env-newindex form#form.fm div.bdsug.bdsugbg,.bdsug{
    background: none !important;
    background-color: rgba(0,0,0,0.7) !important;
  }
  html body.has-background.s-manhattan-index div#wrapper div#head.s-skin-hasbg.white-logo.s-opacity-100 div#head_wrapper.s-isindex-wrap.head_wrapper.s-title-img div#s_fm.s_form div#s_form_wrapper.s_form_wrapper.soutu-env-nomac.soutu-env-newindex form#form.fm div.bdsug.bdsugbg ul li ,.bdsug ul li{
    color:#ffc70e !important;
  }
  html body.has-background.s-manhattan-index div#wrapper div#head.s-skin-hasbg.white-logo.s-opacity-100 div#head_wrapper.s-isindex-wrap.head_wrapper.s-title-img div#s_fm.s_form div#s_form_wrapper.s_form_wrapper.soutu-env-nomac.soutu-env-newindex form#form.fm div.bdsug.bdsugbg ul li:hover,.bdsug ul li:hover{
    background-color: rgba(255,255,255,0.4);
  }
  
  
  /*导航区背景*/
  #s_menu_mine,
  .s-bg-space.s-opacity-white-background,
  #s_menu_set{
    background: none !important;
    background-color: rgba(255,255,255,0.1) !important;
  }
  
  #s_ctner_contents{
    background: none !important;
    background-color: rgba(0,0,0,0.3) !important;
  }
  #s_xmancard_mine.s-xmancard-mine.s-xmancard div.s-mine-wrapper.clearfix div.s-code-blocks.s-block-nav div.s-block-container div.s-mblock-content.s-opacity-blank3.block-classed div.dir-item.s-opacity-blank3 div.dir-content.s-opacity-blank3 div.d-nav-item.s-opacity-blank3 a{
    transition: background-color 0.5s !important;
  }
  #s_xmancard_mine.s-xmancard-mine.s-xmancard div.s-mine-wrapper.clearfix div.s-code-blocks.s-block-nav div.s-block-container div.s-mblock-content.s-opacity-blank3.block-classed div.dir-item.s-opacity-blank3 div.dir-content.s-opacity-blank3 div.d-nav-item.s-opacity-blank3 a:hover{
    background: none !important;
    background-color: rgba(255,255,255,0.1) !important;
    box-shadow: 0px 0px 2px #888888;
  }
  
  
  /*导航区字体颜色*/
  #s_xmancard_mine.s-xmancard-mine.s-xmancard div.s-mine-wrapper.clearfix div.s-code-blocks.s-block-nav div.s-block-container div.s-mblock-content.s-opacity-blank3.block-classed div.dir-item.s-opacity-blank3 div.dir-content.s-opacity-blank3 div.d-nav-item.s-opacity-blank3 a > div >span{
    transition: color 0.5s !important;
  }
  #s_xmancard_mine.s-xmancard-mine.s-xmancard div.s-mine-wrapper.clearfix div.s-code-blocks.s-block-nav div.s-block-container div.s-mblock-content.s-opacity-blank3.block-classed div.dir-item.s-opacity-blank3 div.dir-content.s-opacity-blank3 div.d-nav-item.s-opacity-blank3 a:hover > div >span{
    color : white !important;
  }
  
  .mine-text,.set-menu div,
  .mine-title-text{
    color:#00b4ff!important;
  }
  .name-text{
    color: white!important;
    transition: text-shadow 0.5s !important;
  }
  .name-text:hover{
    text-shadow:1px 1px 1px #c6c6c6 !important;
  }
  .nav-text-content,
  .manager-btn-text{
    color: #c3c3c3 !important;
  }
  
  /*导航栏小div样式*/
  
  .sui-dialog-body,
  .sui-prevent-wheel.s-isindex-wrap.sui-dialog.sui-dialog-minesetting,
  .s-mod-title,
  .sui-prevent-wheel.s-isindex-wrap.sui-dialog.sui-dialog-cardsetting{
    background: none !important; 
  }
  .sui-prevent-wheel.s-isindex-wrap.sui-dialog.sui-dialog-minesetting,
  .sui-prevent-wheel.s-isindex-wrap.sui-dialog.sui-dialog-cardsetting{
    background-color: rgba(0,0,0,0.8)!important;
  }
  
  html body.has-background.s-manhattan-index div#wrapper div#head.s-skin-hasbg.white-logo.s-opacity-100.s_down div#s_wrap.s-isindex-wrap.s-down div#s_main.main.clearfix div#s_mancard_main.s-mancacrd-main div div div div div.sui-dialog-body div div div h3{
    color:white !important;
  }
  .s-mod-title,
  .s-cardsetting-left div,
  .s-cardsetting-wrap.clearfix div h3{
    color:white !important;
  }
  .s-mod-title:hover{
     text-shadow:1px 1px 1px #c6c6c6 !important;
  }
  div.sui-dialog-body div.pannel-content div.recom-content div.one-class div.class-recoms div.recom-item span.recom-text,
  .dir-item{
    color:black !important;
  }
  
  
  
  /*顶部div样式*/
  #s_user_name_menu.s-isindex-wrap.s-user-set-menu.menu-top div a,
  .bdpfmenu a,
  .bdpfmenu,
  #s_msg_content,
  .weather-setting-content{
    background: none !important;
    color:gray !important;
  }
  #s_msg_content
  {
    background-color: rgba(0,0,0,0.7) !important;
  }
  .weather-setting-content{
    background-color: rgba(255,255,255,0.8) !important;
  }
  .bdpfmenu a{
    border-top-width:0px !important;
    border-right-width:0px !important;
    border-bottom-width:0px !important;
    border-left-width:0px!important;
  }
  #s_user_name_menu.s-isindex-wrap.s-user-set-menu.menu-top div a:hover,
  .bdpfmenu a:hover{
    color:white !important;
  }
  .s-treasure{
    display: none !important;
  }
  #s_user_name_menu.s-isindex-wrap.s-user-set-menu.menu-top div{
    border-width: 0px;
  }
    
  /*搜图下拉栏*/
  
  .soutu-layer{
    background: none !important;
    background-color: rgba(0,0,0,0.7) !important;
    box-shadow: rgb(209,209,209) 2px 2px 5px 0px !important;
  }
  .soutu-drop{
    background: none !important;
    background-color: rgba(255,255,255,0.1) !important;
  }
  .upload-text{
    color:#00b4ff !important;
  }
  .upload-text span:hover{
    text-shadow:1px 1px 1px #ffffff !important;
  }
  
  /*搜索结果页样式*/
  a{
    color:rgb(0,180,255);
  }
  div,p{
    color: rgba(255,255,255,0.7);
  }
  html body{
    background: none;
    background-image: url(http://www.mscststs.com/background-overwatch-repeat.jpg)!important;
  }
  #s_tab,#head,
  html body div#wrapper.wrapper_s div div div div form#form.fm span input#kw.s_ipt,
  #form.fm div.bdsug.bdsugbg,
  #rs,#foot,#help
  {
    background: none !important;
  }
  
  #u div.usermenu a{
    background: none !important;
    color:rgba(255,255,255,0.5) !important;
  }
  #u div.usermenu a:hover{
    color:white !important;
  }
  #page strong,#page a,#page a span{
    background: none !important;
    color: rgb(248,158,53);
    transition: background-color 0.5s;
  }
  #page a span:hover,#page a.n:hover{
    background-color: rgba(255,255,255,0.2) !important;
  }
  div.bdsug.bdsugbg a,#form.fm div.bdsug.bdsugbg ul li{
    background: none!important;
    background-color: rgba(0,0,0,0.7) !important;
  }
  div.bdsug.bdsugbg ul li b,div.bdsug.bdsugbg ul li,#u a{
    color:	rgb(255,199,14) !important;
  }
  div.bdsug.bdsugbg ul li:hover{
    background-color: rgba(255,255,255,0.2) !important;
  }

  #con-ar div div.cr-content div div div div.opr-recommends-merge-d img,
  html body.has-background.s-manhattan-index div.result-op.xpath-log div.rrecom-container.rrecom_content_s,
  html body.has-background.s-manhattan-index div.result-op.xpath-log,#content_right,.result-op.xpath-log,
  span.rrecom-btn-parent.rrecom-btn-s.rrecom-btn-moving.rrecom-btn-click,.no-qrcode-layer
  {
    display:none !important;
  }
  #con-ar div.result-op.xpath-log div.cr-content div.opr-recommends-merge-content div div a{
    color : rgba(255,255,255,0.6) !important;
  }
  #s_tab a{
    color : #ffffff !important;
  }
  #s_tab b{
    color:rgb(0,180,255) !important;
  }
  #form.fm div.bdsug.bdsugbg ul li.bdsug-overflow:hover{
    background-color: rgba(255,255,255,0.2) !important;
  }
  #con-ar div div.cr-content div table thead tr th{
    background: none !important;
    background-color: rgba(255,255,255,0.2) !important;
    color : #ffffff;
  }
  
  
}
