@namespace url(http://www.w3.org/1999/xhtml);

/* I really want this to be global! */ 
body, 
body div,
h1, h2, h3, h4, h5, h6, 
a { font-family: "Comic Sans MS" !important; }

body { background-color:hotpink !important; 
       background-image:none !important; }
body div { background-color:hotpink !important; }

/* Unicorn cursor */
body { cursor: url('data:image/x-icon;base64,'), auto; }


/***
Glowlinks by user152378
http://userstyles.org/users/152378
https://userstyles.org/styles/89724/glowlinks
***/
@keyframes glow {  
   50% {  box-shadow: 0 0 0 4px RED, 0 0 0 8px ORANGE, 0 0 0 12px YELLOW, 0 0 0 16px GREEN, 0 0 0 20px BLUE, 0 0 0 24px INDIGO, 0 0 0 28px VIOLET;  }
 }

@-webkit-keyframes glow {
   50% {  box-shadow: 0 0 0 4px RED, 0 0 0 8px ORANGE, 0 0 0 12px YELLOW, 0 0 0 16px GREEN, 0 0 0 20px BLUE, 0 0 0 24px INDIGO, 0 0 0 28px VIOLET;  }
 }

 a:hover, a:focus {
   animation-name: glow;
   animation-duration: 2s;
   animation-iteration-count: infinite;
   -webkit-animation-name: glow;
   -webkit-animation-duration: 2s;
   -webkit-animation-iteration-count: infinite;
   border-radius: 2px !important;
   text-decoration: none !important;
   outline: none !important;
 }
/*** Glowlinks END ***/
