css:
@-moz-document domain("totalminerforums.net") {
#main_content_section > div.cat_bar, #main_content_section > div.cat_bar > h3 {
    background: url(http://i.imgur.com/EZCJvAf.png) repeat 0 -160px !important;
}
#header {
    background: url(http://i.imgur.com/EZCJvAf.png) no-repeat 0 -240px !important; 
    padding-left: 20px;
}
#header > div {
    background: url(http://i.imgur.com/EZCJvAf.png) no-repeat 100% -240px !important;
    display: block !important;
    padding: 5px 20px 0 0px !important;
}
#content_section > div {
 background: url(http://i.imgur.com/motCYdc.png) repeat-y right !important;
    display: block;
    padding: 0px 20px 0px 0;
}
#content_section {
 background: url(http://i.imgur.com/motCYdc.png) repeat-y top left !important;   
}
#category_2 > tr > td > div {
   background: url(http://i.imgur.com/EZCJvAf.png) no-repeat 0 -160px !important; 
}
#category_2 > tr > td > div > h3 {
background: url(http://i.imgur.com/EZCJvAf.png) no-repeat 100% -160px !important;
}
#category_3 > tr > td > div > h3 {    
    background: url(http://i.imgur.com/EZCJvAf.png) no-repeat 0 -160px !important;
}
#category_3 > tr > td > div {
    background: url(http://i.imgur.com/EZCJvAf.png) no-repeat 100% -160px !important;
}
#category_1 > tr > td > div {
    background: url(http://i.imgur.com/EZCJvAf.png) no-repeat 0 -160px !important;
}
#category_1 > tr > td > div > h3 {
    background: url(http://i.imgur.com/EZCJvAf.png) no-repeat 100% -160px !important;
}
#category_4 > tr > td > div {
        background: url(http://i.imgur.com/EZCJvAf.png) no-repeat 0 -160px !important;
}
#category_4 > tr > td > div > h3 {
       background: url(http://i.imgur.com/EZCJvAf.png) no-repeat 100% -160px !important; 
}
body {
    background: black;
}
input {
 background: black !important;
}
#messageindex > table > thead > tr > th.first_th {
 background: url(http://i.imgur.com/EZCJvAf.png) no-repeat 0 -160px !important;  
}
#messageindex > table > thead > tr > th:nth-child(2) {
    background: url(http://i.imgur.com/EZCJvAf.png) no-repeat -10px -160px !important;
}
.table_grid tr.catbg th, .table_grid tr.titlebg th {
    line-height: 15px !important;
}
#messageindex > table > thead > tr > th:nth-child(3) {
  background: url(http://i.imgur.com/EZCJvAf.png) no-repeat -10px -160px !important;  
}
#messageindex > table > thead > tr > th.lefttext.last_th {
    background: url(http://i.imgur.com/EZCJvAf.png) no-repeat 100% -160px !important;
}
a {
    text-shadow: 1px 0 0 #000, 0 -1px 0 #000, 0 1px 0 #000, -1px 0 0 #000;
}
#footer_section {
      background: url(http://i.imgur.com/EZCJvAf.png) no-repeat 0 -820px !important;  
}
#footer_section > div {
   background: url(http://i.imgur.com/EZCJvAf.png) no-repeat 0 -820px !important;  
}
#main_content_section > div.roundframe > div > div.cat_bar {
    background: url(http://i.imgur.com/EZCJvAf.png) no-repeat 0 -160px !important;
}
#main_content_section > div.roundframe > div > div.cat_bar > h3 {
    background: url(http://i.imgur.com/EZCJvAf.png) no-repeat 100% -160px !important;
}
[title="New Posts"] {
    display: inline;
  -moz-box-sizing: border-box;
  box-sizing: border-box;
  background: url(http://i.imgur.com/x6kJhTu.png) no-repeat;
  width: 16px; /* Width of new image */
  height: 16px; /* Height of new image */
  padding-left: 16px; /* Equal to width of new image */
  margin: -1px -1px;
}
[title="No New Posts"] {
    display: inline;
  -moz-box-sizing: border-box;
  box-sizing: border-box;
  background: url(http://i.imgur.com/oP9KFy3.png) no-repeat;
  width: 16px; /* Width of new image */
  height: 16px; /* Height of new image */
  padding-left: 16px; /* Equal to width of new image */
  margin: -1px -1px;
}
#posting_icons {
    display: none;
}
#main_content_section > div.mark_read > div > ul > li > a {
    background: url(http://i.imgur.com/mkdscR4.png) no-repeat 0 -60px !important;
}
#main_content_section > div.mark_read > div > ul > li > a > span {
    background: url(http://i.imgur.com/mkdscR4.png) no-repeat 100% -60px !important;
}
#board_3_childboards > div.cat_bar {
 background: url(http://i.imgur.com/EZCJvAf.png) no-repeat 0 -160px !important;   
}
#board_3_childboards > div.cat_bar > h3 {
    background: url(http://i.imgur.com/EZCJvAf.png) no-repeat 100% -160px !important;
}
}
