@-moz-document domain("eksisozluk.com") {
  
  body {
    background-color: #FFF;
    font-family: 'Ubuntu', sans-serif;
  }
  
  body > header {
    min-width: 300px;
    border-top: 2px solid #009BFB;
    background-color: #FBFBFB;
    border-bottom: 1px solid #CCC;
  }
  
  a {
    color: #0970e3;
  }
  
  #top-bar .eksico {
    fill: #D1D1D1;
  }
  
  .tabs {
    border-bottom: 1px solid #EEE;
    margin-bottom: 4px;
  }
  
  .tabs > li.active {
    border: 1px solid #EEE;
    border-bottom-color: #eeeeee;
  }
  
  .sub-title-menu > a, .sub-title-menu > div > a, .sub-title-menu > div > .dropdown > a {
    color: #999;
  }
  
  #title span {
    font-weight: 300;
  }
  
  .topic-list.partial > li > a {
    padding: 5px 30px 5px 0;
    color: #777;
    border-bottom: 1px solid #DDD;
  }
  
  .topic-list.partial > li > a > small {
    font-size: 10px;
    color: #888;
    top: calc(50% - 8px);
  }
  
  html.no-touch .topic-list li > a:hover {
    background-color: inherit;
    color: #0970e3;
  }

  #entry-list .content {
    margin-bottom: 5px;
    overflow: hidden;
    max-height: 100%;
    position: relative;
    font-size: 15px;
    line-height: 25px;
    color: #777;
  }
  
  #entry-list a, #draft-content a {
    color: #FB625C;
  }
  
  #content-body h1 {
    color: #0970e3;
    font-weight: 300;
  }
  
  #message-thread .incoming {
    background: #F2F2F2;
  }
  
  #message-thread .outgoing {
    background: #FFF;
  }
    
  #sub-navigation > ul > li > a {
    color: #AAA;
    font-weight: 400;
  }
  
  .popped-player h1 a {
    font-weight: 300;
    color: #0970e3;
  }
  
  html.no-touch #sub-navigation > ul > li:hover > a, html.no-touch #sub-navigation > ul > li.active > a {
    color: #0970e3;
    border-color: #0970e3;
    text-decoration: none;
  }
  
  h1#user-profile-title a {
    font-weight: 300;
  }
  
  #profile-cards li h3 a {
    color: #39A0FB;
    font-weight: 300;
  }
  
  #profile-cards li h3 {
    color: #39A0FB;
    font-weight: 300;
  }
  
  #profile-cards li h3 small {
    color: #777;
    font-weight: 300;
  }
  
  #profile-cards li ul li a {
    color: #999;
  }
  
  #search-form button[type="submit"] {
    height: 29px;
    line-height: 30px;
    background-color: #DDD;
  }
  
  #search-form #search-textbox {
    border: 1px solid #EEE;
  }
  
  html.no-touch a.more-data:hover {
    background-color: #EEE;
    color: #0076FF;
  }
  
  html.no-touch a:hover {
    text-decoration: inherit;
  }
  
  #top-navigation > ul > li > a {
        color: #CCC;
  }
  
  #top-navigation > ul > li > a:hover {
        color: #009BFB;
  }
  
  button.primary, input.primary[type="submit"], input.primary[type="button"] {
    border: 1px solid #666;
    background-color: #666;
    color: white;
  }
  
  button.primary:hover, input.primary[type="submit"]:hover, input.primary[type="button"]:hover, button.primary:focus, input.primary[type="submit"]:focus, input.primary[type="button"]:focus {
    background-color: #0076FF;
    border: 1px solid #0076FF;
}
}
