/* i really want this to be global */
@-moz-document domain("recolor.me") {
.button.button_red {
	background: -webkit-gradient linear, left top, left bottom, color-stop0.05, blue, color-stop1, blue;
	background: -moz-linear-gradient center top, blue 5%, blue 100%;
	background: linear-gradient(to bottom, blue 0%, blue 100%) repeat scroll 0 0 rgba(0, 0, 0, 0);
	filter: startColorstr'blue', endColorstr'blue';
	background-color: blue;
	border: 2px solid blue;
}
a:link,
a:visited,
a:active {
	text-decoration: none;
	color: blue;
}
.button.button_green,
.smallbutton.button_blue {
	background: -webkit-gradient linear, left top, left bottom, color-stop 0.05, lightblue, color-stop lightblue;
	background: -moz-linear-gradient center top, lightblue 5%, lightblue 100%;
	background: linear-gradient(to bottom, lightblue 0%, lightblue 100%) repeat scroll 0 0 rgba(0, 0, 0, 0);
	filter:  startColorstr'lightblue', endColorstr'lightblue';
	background-color: lightblue;
	border: 1px solid lightblue;
	text-shadow: 0 0 2px rgba(0, 0, 0, 0.9);
}
#faction_room_list a span {
	display: inline-block;
	padding: 15px;
	word-wrap: break-word;
	-webkit-hyphens: auto;
	-moz-hyphens: auto;
	hyphens: auto;
}
.text_big {
	color: lightblue;
	font-size: 20px;
	text-shadow: 0 2px 2px #000;
}
#bank_value {
	font-size: 25px;
	color: blue;
	text-shadow: 0 2px 2px #000;
	vertical-align: middle;
	display: inline-block;
	margin: 0 5px;
}
#bank_value {
	font-size: 25px;
	color: rgba(18, 0, 255, 0.88);
	text-shadow: 0 2px 2px #000;
	vertical-align: middle;
	display: inline-block;
	margin: 0 5px;
}
.smallredbutton {
	background: -webkit-gradient(linear, left top, left bottom, color-stop(0.05, lightblue), color-stop(1, lightblue));
	background: -moz-linear-gradient(center top, lightblue 5%, lightblue 100%);
	background: linear-gradient(to bottom, lightblue 0%, lightblue 100%) repeat scroll 0 0 rgba(0, 0, 0, 0);
	filter: startColorstr'lightblue', endColorstr'lightblue';
	background-color: lightblue;
	box-shadow: 0 2px 2px rgba(0, 0, 0, 0.6);
	border-radius: 4px;
	text-indent: 0;
	border: 1px solid lightblue;
	display: inline-block;
	color: lightblue !important;
	font-weight: bold;
	font-style: normal;
	white-space: nowrap;
	padding: 5px 10px;
	text-decoration: none;
	text-align: center;
	text-shadow: 1px 1px 1px lightblue;
}
.subscribe_option.active {
	background: lightblue;
	color: green;
}
.orb {
	display: inline-block;
	width: 30px;
	height: 30px;
	line-height: 30px;
	text-align: center;
	color: blue;
	text-shadow: 0 0 1px #000;
}
.map_container .map img.map_bg {
	display: block;
	position: absolute;
	z-index: 1;
	top: 0;
	left: 0;
	width: 100%;
}
.map_container .map a {
	display: block;
	position: relative;
	z-index: 3;
	padding: 0px 0px 0px;
	width: 142px;
	font-size: 16px;
	letter-spacing: 0.6px;
	background: rgba(0, 0, 0, 1);
	border: 1px solid rgba(100, 100, 100, 1);
	border-radius: 0px;
	box-shadow: 0 1px 1px 1px rgba(0, 0, 0, 0.66) inset;
	opacity: 1;
	color: #fff7cf;
	font-weight: bold;
	line-height: 20px;
	text-align: center;
	text-shadow: 0 1px 1px #000;
	margin-top: 85px;
}
