css:
@-moz-document domain("facebook.com") {
._5p3y[dir="ltr"],
._5p3y[dir="ltr"] button,
._5p3y[dir="ltr"] input,
._5p3y[dir="ltr"] label,
._5p3y[dir="ltr"] select,
._5p3y[dir="ltr"] td,
._5p3y[dir="ltr"] textarea,
._5p3y[dir="ltr"] .uiMentionsInput .highlighter,
._5p3y[dir="ltr"] .uiButtonText,
._5p3y[dir="ltr"] .uiButton input,
._5p3y[dir="ltr"] .uiLinkButton input,
._5p3y[dir="ltr"] button.as_link,
._5p3y[dir="ltr"] .fbChatSidebar,
._5p3y[dir="ltr"] .fbDock,
._5p3y[dir="ltr"] .fbFeedTicker .fbFeedTickerStory .tickerFeedMessage,
._5p3y[dir="ltr"] .fbFeedTicker .fbFeedTickerStory div.tickerFeedMessage h5,
._5p3y[dir="ltr"] .fbFeedTicker .fbFeedTickerStory div.tickerFeedMessage h6,
._5p3y[dir="ltr"] .uiTooltipX .tooltipContent,
._5p3y[dir="ltr"] a[role="button"],
._5p3y[dir="rtl"] *[dir="ltr"] {
	font-family: times roman numeral;
}
element.style {
	padding: 0px 0 0px;
	font-size: 20px;
	line-height: 30px;
}
#facebook ._-kb h1,
#facebook ._-kb h2,
#facebook ._-kb h3,
#facebook ._-kb h4,
#facebook ._-kb h5,
#facebook ._-kb h6,
#facebook ._-kb p {
	font-family: times roman numeral;
}
._3ma {
	font-family: "times roman numeral", "!important;"
	"font-weight:bold! important;"
	"letter-spacing: normal;"
	"text-rendering: optimizelegibility;"
}
}
