css:
@-moz-document url-prefix("http://docs.sencha.com/") {
body {
	    color: #d4d0d0;
}
.x-container,
.x-panel-body {
    background-color: #384244  !important;
    color: #fefefe !important;
}
.inline-example {
    background-color: #425b62 !important
}
.pln {
    color: #cfcfcf;
}
#center-container h1 .class-source-link {
    color: #a7cc7d;
}
.typ {
    color: #feb880;
}
.cfg-contentEl {
    color: #fff !important;
}
a:link {
	color: #8ed0d9;
}
a:link:visited {
    color: #b28ffc !important;
}
.docClass {
    color: #f37878;
}
.members {
    color: #fff;
}
#treecontainer a {
    color: #fff;
}
.members .member a.defined-in {
    color: #F69C55;
}
.class-overview .x-panel-body pre.prettyprint {
    background-color: #313b40;
}
.str {
    color: #d2f698;
}
.kwd {
    color: #3abac4;
}
pre.prettyprint {
    background-color: #353d3d;
	border:none;
}
.com {
    color: #c29e90;
}
.pun {
    color: #fff;
}
#center-container .docs-contents h1 {
    color: #51dbda !important;
}
#center-container .doc-contents h1,
#center-container .doc-contents h2,
#center-container .doc-contents h3 {
    color: #4fb3ff !important;
}
.x-grid-row .x-grid-tree-node-leaf .x-grid-row-selected .x-grid-row-focused {
    background-color: #262626;
}
.class-overview .hierarchy {
    background-color: #45484a;
    border: 1px solid #3b4048;
    border-bottom: 1px solid #3b4048;
}
.cls-grouping button, .cls-private-cb {
	color: #161616 !important;
}
.x-btn-default-toolbar-small .x-btn-inner {
	color:#f5f5f5;
}
.hover-menu {
    border:none;
    background: #4c595c;
}
.x-toolbar .x-toolbar-text {
	color:#fefefe;
}
.x-toolbar-default { 
	background-image:none !important;
	border:none;
	
	background-color: #2b3335 !important;
}
#treecontainer .x-grid-cell-inner {
    -webkit-transition: background-color none;
}
body{
	    background-color: #353d3d;
}
.hover-menu a {
    color: #ffffff;
}
.cm-s-default span.cm-keyword {
    color: #d86fe7;
}
.cm-s-default span.cm-variable {
    color: #fd9f75;
}
.cm-s-default span.cm-property {
    color: #7fc2c8;
}
.cm-s-default span.cm-string {
    color: #cce598;
}
.cm-s-default span.cm-atom {
    color: #d86fe7;
}
.members .member .name {
    color: #e7a3ff;
}
.highlight {
    background-color: #289383;
}
#treecontainer .x-grid-cell-inner {
    -webkit-transition: none;
}
.x-grid-row-selected .x-grid-cell, .x-grid-row-selected .x-grid-rowwrap-div {
	background-color: #2b3335 !important;
}
.x-menu-item-text {
    font-size: 11px;
    color: #fff;
}
}
